INSERT INTO `entity_data` (`id`, `entity`, `name`, `caption`, `type`, `symptoms`, `diagnosis`, `causes`, `parse_data`, `prevention`, `complications`, `medication`, `images`) VALUES
(1, 'whooping_cough', 'Pertussis (Whooping Cough)', 'A%20young%20boy%20coughing%20due%20to%20pertussis.', 'Infectious%20disease', '["Runny nose", "fever", "cough"]', '{"desc": "Nasopharyngeal swab", "list": ["Nasopharyngeal swab"]}', '{"desc": "Bordetella pertussis (spread through the air)", "list": ["Bordetella pertussis\'\'", "spread through the air"]}', '[{"list": ["bacterial disease"], "text": "Pertussis (also known as whooping cough or 100-day cough) is a highly contagious bacterial disease."}, {"list": ["common cold", "runny nose", "fever", "cough"], "text": "Initially, symptoms are usually similar to those of the common cold with a runny nose, fever, and mild cough."}, {"text": "This is then followed by weeks of severe coughing fits."}, {"text": "Following a fit of coughing, a high-pitched whoop sound or gasp may occur as the person breathes in. The coughing may last for 10 or more weeks, hence the phrase \\"100-day cough\\"."}, {"list": ["break ribs", "very tired"], "text": "A person may cough so hard that they vomit, break ribs, or become very tired from the effort."}, {"list": ["periods where they do not breathe"], "text": "Children less than one year old may have little or no cough and instead have periods where they do not breathe."}, {"text": "The time between infection and the onset of symptoms is usually seven to ten days."}, {"text": "Disease may occur in those who have been vaccinated, but symptoms are typically milder."}, {"list": ["Bordetella pertussis\'\'"], "text": "Pertussis is caused by the bacterium Bordetella pertussis."}, {"list": ["airborne disease"], "text": "It is an airborne disease which spreads easily through the coughs and sneezes of an infected person."}, {"text": "People are infectious to others from the start of symptoms until about three weeks into the coughing fits."}, {"list": ["antibiotics"], "text": "Those treated with antibiotics are no longer infectious after five days."}, {"list": ["sample from the back of the nose and throat"], "text": "Diagnosis is by collecting a sample from the back of the nose and throat."}, {"list": ["culture", "polymerase chain reaction"], "text": "This sample can then be tested by either culture or by polymerase chain reaction."}, {"list": ["vaccination", "pertussis vaccine"], "text": "Prevention is mainly by vaccination with the pertussis vaccine."}, {"text": "Initial immunization is recommended between six and eight weeks of age, with four doses to be given in the first two years of life."}, {"text": "The vaccine becomes less effective over time, with additional doses often recommended for older children and adults."}, {"text": "Antibiotics may be used to prevent the disease in those who have been exposed and are at risk of severe disease."}, {"text": "In those with the disease, antibiotics are useful if started within three weeks of the initial symptoms, but otherwise have little effect in most people."}, {"text": "In children less than one year old and among those who are pregnant, they are recommended within six weeks of symptom onset."}, {"list": ["erythromycin", "azithromycin", "clarithromycin", "trimethoprim/sulfamethoxazole"], "text": "Antibiotics used include erythromycin, azithromycin, clarithromycin, or trimethoprim/sulfamethoxazole."}, {"text": "Evidence to support interventions, other than antibiotics, for the cough is poor."}, {"text": "Many children less than a year of age require hospitalization."}, {"text": "An estimated 16.3 million people worldwide were infected in 2015."}, {"list": ["developing world"], "text": "Most cases occur in the developing world, and people of all ages may be affected."}, {"text": "In 2015, it resulted in 58,700 deaths – down from 138,000 deaths in 1990."}, {"text": "Nearly 0.5% of infected children less than one year of age die."}, {"text": "Outbreaks of the disease were first described in the 16th century."}, {"text": "The bacterium that causes the infection was discovered in 1906."}, {"text": "The pertussis vaccine became available in the 1940s."}, {"list": ["paroxysmal", "fainting", "vomiting"], "text": "The classic symptoms of pertussis are a paroxysmal cough, inspiratory whoop, and fainting, or vomiting after coughing."}, {"list": ["subconjunctival hemorrhages", "rib fractures", "urinary incontinence", "hernias", "vertebral artery dissection"], "text": "The cough from pertussis has been documented to cause subconjunctival hemorrhages, rib fractures, urinary incontinence, hernias, and vertebral artery dissection."}, {"list": ["pleura", "pneumothorax"], "text": "Violent coughing can cause the pleura to rupture, leading to a pneumothorax."}, {"text": "Vomiting after a coughing spell or an inspiratory whooping sound on coughing, almost doubles the likelihood that the illness is pertussis."}, {"list": ["emesis"], "text": "The absence of a paroxysmal cough or posttussive emesis, though, makes it almost half as likely."}, {"list": ["runny nose"], "text": "The illness usually starts with mild respiratory symptoms include mild coughing, sneezing, or a runny nose."}, {"list": ["catarrhal"], "text": "This is known as the catarrhal stage."}, {"list": ["paroxysmal"], "text": "After one to two weeks, the coughing classically develops into uncontrollable fits, each with five to ten forceful coughs, followed by a high-pitched \\"whoop\\" sound in younger children, or a gasping sound in older children, as the person tries to inhale (paroxysmal stage)."}, {"text": "Coughing fits can occur on their own or can be triggered by yawning, stretching, laughing, eating, or yelling; they usually occur in groups, with multiple episodes on an hourly basis throughout the day."}, {"text": "This stage usually lasts two to eight weeks, or sometimes longer."}, {"text": "A gradual transition then occurs to the convalescent stage, which usually lasts one to four weeks."}, {"text": "This stage is marked by a decrease in paroxysms of coughing, both in frequency and severity, and a cessation of vomiting."}, {"text": "A tendency to produce the \\"whooping\\" sound after coughing may remain for a considerable period after the disease itself has cleared up."}, {"list": ["time between exposure and the development of symptoms"], "text": "The time between exposure and the development of symptoms is on average 7–14 days (range 6–20 days), rarely as long as 42 days."}, {"list": ["Bordetella pertussis\'\'"], "text": "Pertussis is caused by the bacterium Bordetella pertussis.<!"}, {"list": ["airborne disease"], "text": "--> It is an airborne disease that spreads easily through the coughs and sneezes of an infected person."}, {"text": "Uncertainties have existed of \'\'B."}, {"list": ["zoonotic disease", "virulent", "agar"], "text": "pertussis\'\' and whooping cough as a zoonotic disease since around 1910 but in the 1930s, knowledge was gained that the bacteria lost their virulent power when repeatedly spread on agar media."}, {"text": "This explained the difficulties to reproduce results from different studies as the pre-inoculating handlings of the bacteria were not standardized among scientists."}, {"list": ["primate"], "text": "Today it is established that at least some primate species are highly susceptible to \'\'B."}, {"list": ["incidence"], "text": "pertussis\'\' and develop clinical whooping cough in high incidence when exposed to low inoculation doses."}, {"list": ["gorillas"], "text": "The bacteria may be present in wild animal populations, but this is not confirmed by laboratory diagnosis, although whooping cough is known among wild gorillas."}, {"list": ["zoos"], "text": "Several zoos also have a long-standing custom of vaccinating their primates against whooping cough."}, {"text": "It acts primarily via its pertussis toxin but also via invasion of tissues and alveolar macrophages."}, {"text": "B. pertussis attaches to the cilia of respiratory epithelial cells, where it produces cilia-paralyzing toxins, and causes inflammation of the respiratory tract, thereby interfering with the \\"mucociliary escalator\\" by which pulmonary secretions (i.e., mucus) are cleared."}, {"text": "A physician\'s overall impression is most effective in initially making the diagnosis."}, {"text": "Single factors are much less useful."}, {"list": ["culturing", "Bordet-Gengou medium", "polymerase chain reaction", "direct fluorescent antibody", "serological", "complement fixation test"], "text": "Methods used in laboratory diagnosis include culturing of nasopharyngeal swabs on a nutrient medium (Bordet-Gengou medium), polymerase chain reaction (PCR), direct fluorescent antibody (DFA), and serological methods (e.g. complement fixation test)."}, {"text": "The bacteria can be recovered from the person only during the first three weeks of illness, rendering culturing and DFA useless after this period, although PCR may have some limited usefulness for an additional three weeks."}, {"list": ["pertussis toxin"], "text": "Serology may be used for adults and adolescents who have already been infected for several weeks to determine whether antibody against pertussis toxin or another virulence factor of \'\'B."}, {"text": "pertussis\'\' is present at high levels in the blood of the person."}, {"text": "By this stage, they have been contagious for some weeks and may have spread the infection to many people."}, {"text": "Because of this, adults, who are not in great danger from pertussis, are increasingly being encouraged to be vaccinated."}, {"list": ["B. parapertussis\'\'"], "text": "A similar, milder disease is caused by B. parapertussis."}, {"list": ["vaccination"], "text": "The primary method of prevention for pertussis is vaccination."}, {"text": "Evidence is insufficient to determine the effectiveness of antibiotics in those who have been exposed, but are without symptoms."}, {"text": "Preventive antibiotics, however, are still frequently used in those who have been exposed and are at high risk of severe disease (such as infants)."}, {"list": ["Pertussis vaccines", "World Health Organization", "Centers for Disease Control and Prevention"], "text": "Pertussis vaccines are effective at preventing illness and are recommended for routine use by the World Health Organization and the Centers for Disease Control and Prevention."}, {"text": "The vaccine saved an estimated half a million lives in 2002."}, {"list": ["acellular"], "text": "The multicomponent acellular pertussis vaccine is 71–85% effective, with greater effectiveness for more severe strains."}, {"text": "Despite widespread vaccination, however, pertussis has persisted in vaccinated populations and is today \\"one of the most common vaccine-preventable diseases in Western countries\\"."}, {"text": "The 21st-century resurgences in pertussis infections are attributed to a combination of waning immunity and bacterial mutations that elude vaccines."}, {"text": "Immunization does not confer lifelong immunity; a 2011 CDC study indicated that protection may only last three to six years."}, {"text": "This covers childhood, which is the time of greatest exposure and greatest risk of death from pertussis."}, {"text": "An effect of widespread immunization on society has been the shift of reported infections from children aged 1–9 years to infants, adolescents, and adults, with adolescents and adults acting as reservoirs for \'\'B."}, {"text": "pertussis\'\' and infecting infants with fewer than three doses of vaccine."}, {"list": ["natural immunity"], "text": "Infection induces incomplete natural immunity that wanes over time."}, {"text": "A 2005 study said estimates of the duration of infection-acquired immunity range from 7 to 20 years and the different results could be the result of differences in levels of circulating \'\'B."}, {"text": "pertussis\'\', surveillance systems, and case definitions used."}, {"text": "The study said protective immunity after vaccination wanes after 4–12 years."}, {"text": "Vaccination exemption laws appear to increase cases."}, {"text": "Both WHO and the CDC found that the acellular pertussis vaccines were effective at prevention of the disease, but had a limited impact on infection and transmission, meaning that vaccinated people could act as asymptomatic reservoirs of infection."}, {"list": ["erythromycin", "clarithromycin", "azithromycin"], "text": "The antibiotics erythromycin, clarithromycin, or azithromycin are typically the recommended treatment."}, {"list": ["macrolides"], "text": "Newer macrolides are frequently recommended due to lower rates of side effects."}, {"list": ["Trimethoprim-sulfamethoxazole", "pyloric stenosis"], "text": "Trimethoprim-sulfamethoxazole (TMP/SMX) may be used in those with allergies to first-line agents or in infants who have a risk of pyloric stenosis from macrolides."}, {"text": "A reasonable guideline is to treat people age >1 year within 3 weeks of cough onset and infants age <1 year and pregnant women within 6 weeks of cough onset."}, {"text": "If the person is diagnosed late, antibiotics will not alter the course of the illness, and even without antibiotics, they should no longer be spreading pertussis."}, {"text": "Antibiotics when used early decrease the duration of infectiousness, and thus prevent spread."}, {"text": "Short-term antibiotics (azithromycin for 3–5 days) are as effective as long-term treatment (erythromycin 10–14 days) in eliminating \'\'B."}, {"text": "pertussis\'\' with fewer and less severe side effects."}, {"text": "People with pertussis are infectious from the beginning of the catarrhal stage (a runny nose, sneezing, low-grade fever, symptoms of the common cold) through the third week after the onset of paroxysms (multiple, rapid coughs) or until 5 days after the start of effective antimicrobial treatment."}, {"list": ["cough"], "text": "Effective treatments of the cough associated with this condition have not been developed."}, {"list": ["Disability-adjusted life year", "pneumonia", "bronchitis", "encephalopathy", "earache", "seizures", "immune system", "apneas"], "text": "[[File:Pertussis world map - DALY - WHO2004.svg|thumb|upright=1.3|Disability-adjusted life year for pertussis per 100,000 inhabitants as of 2004.Common complications include pneumonia, bronchitis, encephalopathy, earache, and seizures. Most healthy older children and adults fully recover, but those with comorbid conditions have a higher risk of morbidity and mortality.Infection in newborns is particularly severe. Pertussis is fatal in an estimated 1.6% of hospitalized US infants under one year of age. First-year infants are also more likely to develop complications, such as: pneumonia (20%), encephalopathy (0.3%), seizures (1%), failure to thrive, and death (1%) —perhaps due to the ability of the bacterium to suppress the immune system. Pertussis can cause severe paroxysm-induced cerebral hypoxia, and 50% of infants admitted to hospital suffer apneas. Reported fatalities from pertussis in infants increased substantially from 1990 to 2010."}, {"list": ["DTaP", "incidence", "Canada", "Toronto", "Australia"], "text": "Whooping cough world map-Deaths per million persons-WHO2012.svg was likely much higher. After vaccinations were introduced in the 1940s, pertussis incidence fell dramatically to approximately 1,000 by 1976. Incidence rates have increased since 1980. In 2015, rates in the United States were 20,762 people. Pertussis is the only vaccine-preventable disease that is associated with increasing deaths in the U.S. The number of deaths increased from four in 1996 to 17 in 2001, almost all of which were infants under one year. In Canada, the number of pertussis infections has varied between 2,000 and 10,000 reported cases each year over the last ten years, and it is the most common vaccine-preventable illness in Toronto. In 2009 Australia reported an average of 10,000 cases a year, and the number of cases had increased. In the U.S. pertussis in adults has increased significantly since about 2004."}, {"text": "In 2010 ten infants in California died, and health authorities declared an epidemic encompassing 9,120 cases."}, {"text": "They found that doctors had failed to correctly diagnose the infants\' condition during several visits."}, {"text": "Statistical analysis identified significant overlap in communities with a cluster of nonmedical child exemptions and cases."}, {"text": "The number of exemptions varied widely among communities, but tended to be highly clustered."}, {"text": "In some schools, more than three-fourths of parents filed for vaccination exemptions."}, {"text": "The data suggest vaccine refusal based on nonmedical reasons and personal belief exacerbated the outbreak."}, {"text": "Other factors included reduced duration of the current vaccine and that most vaccinated adults and older children had not received a booster shot."}, {"text": "In April and May 2012 pertussis was declared to be at epidemic levels in Washington, with 3,308 cases."}, {"text": "In December 2012 Vermont declared an epidemic of 522 cases."}, {"text": "Wisconsin had the highest incidence rate, with 3,877 cases, although it did not make an official epidemic declaration."}, {"list": ["Jules Bordet", "Octave Gengou"], "text": "B. pertussis was discovered in 1906 by Jules Bordet and Octave Gengou, who also developed the first serology and vaccine."}, {"text": "Efforts to develop an inactivated whole-cell vaccine began soon after \'\'B."}, {"text": "pertussis\'\' was cultured that year."}, {"list": ["Louis W. Sauer", "Evanston Hospital"], "text": "In the 1920s, Louis W. Sauer developed a weak vaccine for whooping cough at Evanston Hospital (Evanston, IL)."}, {"text": "In 1925 Danish physician Thorvald Madsen was the first to test a whole-cell vaccine on a wide scale."}, {"list": ["Faroe Islands"], "text": "Madsen used the vaccine to control outbreaks in the Faroe Islands in the North Sea."}, {"list": ["Atlanta, Georgia,", "Leila Denmark"], "text": "In 1932 an outbreak of whooping cough hit Atlanta, Georgia, prompting pediatrician Leila Denmark to begin her study of the disease."}, {"list": ["Journal of the American Medical Association", "Emory University", "Eli Lilly & Company"], "text": "Over the next six years her work was published in the Journal of the American Medical Association, and in partnership with Emory University and Eli Lilly & Company, she developed the first pertussis vaccine."}, {"list": ["Grace Eldering", "Loney Gordon", "Pearl Kendrick", "diphtheria", "tetanus"], "text": "In 1942 American scientists Grace Eldering, Loney Gordon, and Pearl Kendrick combined the whole-cell pertussis vaccine with diphtheria and tetanus toxoids to generate the first DTP combination vaccine."}, {"list": ["strep throat", "leukocytosis"], "text": "To minimize the frequent side effects caused by the pertussis component, Japanese scientist Yuji Sato developed an acellular vaccine consisting of purified haemagglutinins (HAs: filamentous strep throat and leukocytosis-promoting-factor HA), which are secreted by \'\'B."}, {"text": "pertussis\'\'."}, {"text": "Sato\'s acellular pertussis vaccine was used in Japan starting in 1981."}, {"text": "Later versions of the acellular vaccine in other countries consisted of additional defined components of \'\'B."}, {"list": ["DTaP"], "text": "pertussis\'\' and were often part of the DTaP combination vaccine."}, {"list": ["encephalopathy"], "text": "During the 1970s and 1980s, a controversy erupted related to the question of whether the whole-cell pertussis component caused permanent brain injury in rare cases, called pertussis vaccine encephalopathy."}, {"text": "Despite this allegation, doctors recommended the vaccine due to the overwhelming public health benefit, because the claimed rate was very low (one case per 310,000 immunizations, or about 50 cases out of the 15 million immunizations each year in the United States), and the risk of death from the disease was high (pertussis killed thousands of Americans each year before the vaccine was introduced)."}, {"text": "No studies showed a causal connection, and later studies showed no connection of any type between the DPT vaccine and permanent brain injury."}, {"list": ["epilepsy"], "text": "The alleged vaccine-induced brain damage proved to be an unrelated condition, infantile epilepsy."}, {"list": ["Journal of the American Medical Association\'\'"], "text": "In 1990, the Journal of the American Medical Association called the connection a \\"myth\\" and \\"nonsense\\"."}, {"list": ["anecdotal reports"], "text": "However, before that point, criticism of the studies showing no connection and a few well-publicized anecdotal reports of permanent disability that were blamed on the DPT vaccine gave rise to 1970s anti-DPT movements."}, {"text": "Negative publicity and fear-mongering caused the immunization rate to fall in several countries, including the UK, Sweden, and Japan."}, {"text": "A dramatic increase in the incidence of pertussis followed."}, {"text": "In the United States, low profit margins and an increase in vaccine-related lawsuits led many manufacturers to stop producing the DPT vaccine by the early 1980s."}, {"list": ["Lea Thompson"], "text": "In 1982, the television documentary DPT: Vaccine Roulette depicted the lives of children whose severe disabilities were incorrectly blamed on the DPT vaccine by reporter Lea Thompson."}, {"list": ["lawsuits"], "text": "The ensuing negative publicity led to many lawsuits against vaccine manufacturers."}, {"list": ["liability insurance"], "text": "By 1985, vaccine manufacturers had difficulty obtaining liability insurance."}, {"text": "The price of DPT vaccine skyrocketed, leading providers to curtail purchases, limiting availability."}, {"text": "Only one manufacturer remained in the US by the end of 1985."}, {"list": ["National Childhood Vaccine Injury Act", "no-fault"], "text": "To correct the situation, Congress in 1986 passed the National Childhood Vaccine Injury Act (NCVIA), which established a federal no-fault system to compensate victims of injury caused by mandated vaccines."}, {"text": "The majority of claims that have been filed through the NCVIA have been related to injuries allegedly caused by the whole-cell DPT vaccine."}, {"text": "The concerns about side effects led Sato to introduce an even safer acellular vaccine for Japan in 1981 that was approved in the US in 1992 for use in the combination DTaP vaccine."}, {"list": ["tetanus", "diphtheria"], "text": "The acellular vaccine has a rate of adverse events similar to that of a Td vaccine (a tetanus-diphtheria vaccine containing no pertussis vaccine)."}]', '{"list": ["Pertussis vaccine"], "conditions": ["Pertussis vaccine"]}', '{"list": ["Vomiting", " broken ribs", " very tired"], "conditions": ["broken ribs", "very tired"]}', '{}', '[{"url": "https://upload.wikimedia.org/wikipedia/commons/2/2a/Pertussis.jpg", "file": "File:Pertussis.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/2/2a/Pertussis.jpg/300px-Pertussis.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/8/81/Pertussis.webm", "file": "File:Pertussis.webm", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/8/81/Pertussis.webm/300px-Pertussis.webm"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/a/a7/Whooping_cough_boy.ogv", "file": "File:Whooping cough boy.ogv", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/a/a7/Whooping_cough_boy.ogv/300px-Whooping_cough_boy.ogv"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/8/82/Bordetella_pertussis.jpg", "file": "File:Bordetella pertussis.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/8/82/Bordetella_pertussis.jpg/300px-Bordetella_pertussis.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/a/a9/Whooping_Cough_(8744519854).jpg", "file": "File:Whooping Cough (8744519854).jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/a/a9/Whooping_Cough_(8744519854).jpg/300px-Whooping_Cough_(8744519854).jpg"}]');
INSERT INTO `entity_data` (`id`, `entity`, `name`, `caption`, `type`, `symptoms`, `diagnosis`, `causes`, `parse_data`, `prevention`, `complications`, `medication`, `images`) VALUES
(2, 'malaria', 'Malaria', 'A%20Plasmodium%20from%20the%20saliva%20of%20a%20female%20mosquito%20moving%20across%20a%20mosquito%20cell', 'Infectious%20disease', '["Fever", "vomiting", "headache"]', '{"desc": "Examination of the blood, antigen detection tests", "list": ["antigen detection tests"]}', '{"desc": "Plasmodium spread by mosquitos", "list": ["mosquitos"]}', '[{"list": ["mosquito-borne infectious disease", "parasitic", "protozoans", "microorganisms", "Plasmodium\'\'", "type"], "text": "Malaria is a mosquito-borne infectious disease affecting humans and other animals caused by parasitic protozoans (a group of single-celled microorganisms) belonging to the Plasmodium type."}, {"list": ["symptoms", "fever", "tiredness", "vomiting", "headaches"], "text": "Malaria causes symptoms that typically include fever, tiredness, vomiting, and headaches."}, {"list": ["yellow skin", "seizures", "coma", "death"], "text": "In severe cases it can cause yellow skin, seizures, coma, or death."}, {"text": "Symptoms usually begin ten to fifteen days after being bitten."}, {"text": "If not properly treated, people may have recurrences of the disease months later."}, {"list": ["infection"], "text": "In those who have recently survived an infection, reinfection usually causes milder symptoms."}, {"list": ["resistance"], "text": "This partial resistance disappears over months to years if the person has no continuing exposure to malaria."}, {"list": ["Anopheles\'\'"], "text": "The disease is most commonly transmitted by an infected female Anopheles mosquito."}, {"list": ["parasites", "saliva", "blood"], "text": "The mosquito bite introduces the parasites from the mosquito\'s saliva into a person\'s blood."}, {"list": ["liver", "reproduce"], "text": "The parasites travel to the liver where they mature and reproduce."}, {"text": "Five species of Plasmodium can infect and be spread by humans."}, {"list": ["P. falciparum\'\'", "P. vivax\'\'", "P. ovale\'\'", "P. malariae\'\'"], "text": "Most deaths are caused by P. falciparum because P. vivax, P. ovale, and P. malariae generally cause a milder form of malaria."}, {"list": ["P. knowlesi\'\'"], "text": "The species P. knowlesi rarely causes disease in humans."}, {"list": ["microscopic examination", "blood films", "antigen-based", "rapid diagnostic tests"], "text": "Malaria is typically diagnosed by the microscopic examination of blood using blood films, or with antigen-based rapid diagnostic tests."}, {"list": ["polymerase chain reaction", "DNA", "common"], "text": "Methods that use the polymerase chain reaction to detect the parasite\'s DNA have been developed, but are not widely used in areas where malaria is common due to their cost and complexity."}, {"list": ["mosquito nets", "insect repellents", "mosquito control", "insecticides", "standing water"], "text": "The risk of disease can be reduced by preventing mosquito bites through the use of mosquito nets and insect repellents, or with mosquito control measures such as spraying insecticides and draining standing water."}, {"list": ["medications", "prevent malaria"], "text": "Several medications are available to prevent malaria in travellers to areas where the disease is common."}, {"list": ["sulfadoxine/pyrimethamine", "infants", "first trimester", "pregnancy"], "text": "Occasional doses of the combination medication sulfadoxine/pyrimethamine are recommended in infants and after the first trimester of pregnancy in areas with high rates of malaria."}, {"list": ["vaccine"], "text": "Despite a need, no effective vaccine exists, although efforts to develop one are ongoing."}, {"list": ["antimalarial medications", "artemisinin"], "text": "The recommended treatment for malaria is a combination of antimalarial medications that includes an artemisinin."}, {"list": ["mefloquine", "lumefantrine"], "text": "The second medication may be either mefloquine, lumefantrine, or sulfadoxine/pyrimethamine."}, {"list": ["Quinine", "doxycycline"], "text": "Quinine along with doxycycline may be used if an artemisinin is not available."}, {"list": ["drug resistance"], "text": "It is recommended that in areas where the disease is common, malaria is confirmed if possible before treatment is started due to concerns of increasing drug resistance."}, {"list": ["chloroquine"], "text": "Resistance among the parasites has developed to several antimalarial medications; for example, chloroquine-resistant \'\'P."}, {"text": "falciparum\'\' has spread to most malarial areas, and resistance to artemisinin has become a problem in some parts of Southeast Asia."}, {"list": ["tropical", "subtropical", "equator"], "text": "The disease is widespread in the tropical and subtropical regions that exist in a broad band around the equator."}, {"list": ["Sub-Saharan Africa", "Asia", "Latin America"], "text": "This includes much of Sub-Saharan Africa, Asia, and Latin America."}, {"text": "In 2016, there were 216 million cases of malaria worldwide resulting in an estimated 731,000 deaths."}, {"text": "Approximately 90% of both cases and deaths occurred in Africa."}, {"text": "Rates of disease have decreased from 2000 to 2015 by 37%, but increased from 2014 during which there were 198 million cases."}, {"list": ["economic development"], "text": "Malaria is commonly associated with poverty and has a major negative effect on economic development."}, {"text": "In Africa, it is estimated to result in losses of US$12 billion a year due to increased healthcare costs, lost ability to work, and negative effects on tourism."}, {"text": "The signs and symptoms of malaria typically begin 8–25 days following infection; however, symptoms may occur later in those who have taken antimalarial medications as prevention."}, {"list": ["flu", "sepsis", "gastroenteritis", "viral diseases"], "text": "Initial manifestations of the disease—common to all malaria species—are similar to flu-like symptoms, and can resemble other conditions such as sepsis, gastroenteritis, and viral diseases."}, {"list": ["headache", "fever", "shivering", "joint pain", "vomiting", "hemolytic anemia", "jaundice", "hemoglobin in the urine", "retinal damage", "convulsions"], "text": "The presentation may include headache, fever, shivering, joint pain, vomiting, hemolytic anemia, jaundice, hemoglobin in the urine, retinal damage, and convulsions."}, {"list": ["paroxysm", "tertian fever"], "text": "The classic symptom of malaria is paroxysm—a cyclical occurrence of sudden coldness followed by shivering and then fever and sweating, occurring every two days (tertian fever) in \'\'P."}, {"text": "vivax and P."}, {"list": ["quartan fever"], "text": "ovale infections, and every three days (quartan fever) for P."}, {"text": "malariae\'\'."}, {"text": "P. falciparum infection can cause recurrent fever every 36–48 hours, or a less pronounced and almost continuous fever."}, {"text": "Severe malaria is usually caused by \'\'P."}, {"text": "falciparum\'\' (often referred to as falciparum malaria)."}, {"text": "Symptoms of falciparum malaria arise 9–30 days after infection."}, {"list": ["neurological", "abnormal posturing", "nystagmus", "conjugate gaze palsy", "opisthotonus", "seizures", "coma"], "text": "Individuals with cerebral malaria frequently exhibit neurological symptoms, including abnormal posturing, nystagmus, conjugate gaze palsy (failure of the eyes to turn together in the same direction), opisthotonus, seizures, or coma."}, {"list": ["complications"], "text": "Malaria has several serious complications."}, {"list": ["respiratory distress"], "text": "Among these is the development of respiratory distress, which occurs in up to 25% of adults and 40% of children with severe \'\'P."}, {"text": "falciparum\'\' malaria."}, {"list": ["metabolic acidosis", "pulmonary oedema", "pneumonia", "anaemia"], "text": "Possible causes include respiratory compensation of metabolic acidosis, noncardiogenic pulmonary oedema, concomitant pneumonia, and severe anaemia."}, {"list": ["acute respiratory distress syndrome"], "text": "Although rare in young children with severe malaria, acute respiratory distress syndrome occurs in 5–25% of adults and up to 29% of pregnant women."}, {"list": ["Coinfection", "HIV"], "text": "Coinfection of HIV with malaria increases mortality."}, {"list": ["blackwater fever", "lysed"], "text": "Renal failure is a feature of blackwater fever, where hemoglobin from lysed red blood cells leaks into the urine."}, {"text": "Infection with \'\'P."}, {"list": ["encephalopathy"], "text": "falciparum\'\' may result in cerebral malaria, a form of severe malaria that involves encephalopathy."}, {"text": "It is associated with retinal whitening, which may be a useful clinical sign in distinguishing malaria from other causes of fever."}, {"list": ["Enlarged spleen", "enlarged liver", "both of these", "low blood sugar", "hemoglobin in the urine", "renal failure"], "text": "Enlarged spleen, enlarged liver or both of these, severe headache, low blood sugar, and hemoglobin in the urine with renal failure may occur."}, {"list": ["coagulopathy", "shock"], "text": "Complications may include spontaneous bleeding, coagulopathy, and shock."}, {"list": ["Malaria in pregnant women", "stillbirths", "infant mortality", "abortion", "low birth weight"], "text": "Malaria in pregnant women is an important cause of stillbirths, infant mortality, abortion and low birth weight, particularly in \'\'P."}, {"text": "falciparum infection, but also with P."}, {"text": "vivax\'\'."}, {"list": ["parasites", "Plasmodium\'\'", "Apicomplexa"], "text": "Malaria parasites belong to the genus Plasmodium (phylum Apicomplexa)."}, {"list": ["P. falciparum\'\'", "P. malariae\'\'", "P. ovale\'\'", "P. vivax\'\'", "P. knowlesi\'\'"], "text": "In humans, malaria is caused by P. falciparum, P. malariae, P. ovale, P. vivax and P. knowlesi."}, {"text": "Among those infected, \'\'P."}, {"text": "falciparum is the most common species identified (~75%) followed by P."}, {"text": "vivax\'\' (~20%)."}, {"text": "Although \'\'P."}, {"text": "falciparum traditionally accounts for the majority of deaths, recent evidence suggests that P."}, {"text": "vivax malaria is associated with potentially life-threatening conditions about as often as with a diagnosis of P."}, {"text": "falciparum\'\' infection."}, {"text": "P. vivax  proportionally is more common outside Africa."}, {"list": ["higher apes"], "text": "There have been documented human infections with several species of Plasmodium from higher apes; however, except for \'\'P."}, {"list": ["zoonotic", "macaques"], "text": "knowlesi\'\'—a zoonotic species that causes malaria in macaques —these are mostly of limited public health importance."}, {"list": ["Global warming"], "text": "Global warming is likely to affect malaria transmission, but the severity and geographic distribution of such effects is uncertain."}, {"list": ["life cycle", "Anopheles\'\'", "definitive host", "sporozoite", "vector"], "text": "In the life cycle of Plasmodium, a female Anopheles mosquito (the definitive host) transmits a motile infective form (called the sporozoite) to a vertebrate host such as a human (the secondary host), thus acting as a transmission vector."}, {"list": ["hepatocytes", "asexually", "schizogony", "merozoites"], "text": "A sporozoite travels through the blood vessels to liver cells (hepatocytes), where it reproduces asexually (tissue schizogony), producing thousands of merozoites."}, {"text": "These infect new red blood cells and initiate a series of asexual multiplication cycles (blood schizogony) that produce 8 to 24 new infective merozoites, at which point the cells burst and the infective cycle begins anew."}, {"list": ["gametocytes", "gametes"], "text": "Other merozoites develop into immature gametocytes, which are the precursors of male and female gametes."}, {"text": "When a fertilized mosquito bites an infected person, gametocytes are taken up with the blood and mature in the mosquito gut."}, {"list": ["ookinete", "zygote"], "text": "The male and female gametocytes fuse and form an ookinete—a fertilized, motile zygote."}, {"list": ["salivary glands"], "text": "Ookinetes develop into new sporozoites that migrate to the insect\'s salivary glands, ready to infect a new vertebrate host."}, {"text": "The sporozoites are injected into the skin, in the saliva, when the mosquito takes a subsequent blood meal."}, {"text": "Only female mosquitoes feed on blood; male mosquitoes feed on plant nectar and do not transmit the disease."}, {"text": "The females of the Anopheles genus of mosquito prefer to feed at night."}, {"text": "They usually start searching for a meal at dusk and will continue throughout the night until taking a meal."}, {"list": ["blood transfusions"], "text": "Malaria parasites can also be transmitted by blood transfusions, although this is rare."}, {"text": "Symptoms of malaria can recur after varying symptom-free periods."}, {"list": ["recrudescence", "relapse"], "text": "Depending upon the cause, recurrence can be classified as either recrudescence, relapse, or reinfection."}, {"text": "Recrudescence is when symptoms return after a symptom-free period."}, {"text": "It is caused by parasites surviving in the blood as a result of inadequate or ineffective treatment."}, {"list": ["hypnozoites"], "text": "Relapse is when symptoms reappear after the parasites have been eliminated from blood but persist as dormant hypnozoites in liver cells."}, {"text": "Relapse commonly occurs between 8–24 weeks and is commonly seen with \'\'P."}, {"text": "vivax and P."}, {"text": "ovale\'\' infections."}, {"list": ["temperate", "overwintering"], "text": "P. vivax malaria cases in temperate areas often involve overwintering by hypnozoites, with relapses beginning the year after the mosquito bite."}, {"text": "Reinfection means the parasite that caused the past infection was eliminated from the body but a new parasite was introduced."}, {"text": "Reinfection cannot readily be distinguished from recrudescence, although recurrence of infection within two weeks of treatment for the initial infection is typically attributed to treatment failure."}, {"list": ["immunity"], "text": "People may develop some immunity when exposed to frequent infections."}, {"list": ["liver", "erythrocytes"], "text": "Malaria infection develops via two phases: one that involves the liver (exoerythrocytic phase), and one that involves red blood cells, or erythrocytes (erythrocytic phase)."}, {"text": "When an infected mosquito pierces a person\'s skin to take a blood meal, sporozoites in the mosquito\'s saliva enter the bloodstream and migrate to the liver where they infect hepatocytes, multiplying asexually and asymptomatically for a period of 8–30 days."}, {"list": ["differentiate"], "text": "After a potential dormant period in the liver, these organisms differentiate to yield thousands of merozoites, which, following rupture of their host cells, escape into the blood and infect red blood cells to begin the erythrocytic stage of the life cycle."}, {"list": ["cell membrane"], "text": "The parasite escapes from the liver undetected by wrapping itself in the cell membrane of the infected host liver cell."}, {"text": "Within the red blood cells, the parasites multiply further, again asexually, periodically breaking out of their host cells to invade fresh red blood cells."}, {"text": "Several such amplification cycles occur."}, {"text": "Thus, classical descriptions of waves of fever arise from simultaneous waves of merozoites escaping and infecting red blood cells."}, {"text": "Some \'\'P."}, {"text": "vivax\'\' sporozoites do not immediately develop into exoerythrocytic-phase merozoites, but instead, produce hypnozoites that remain dormant for periods ranging from several months (7–10 months is typical) to several years."}, {"text": "After a period of dormancy, they reactivate and produce merozoites."}, {"text": "Hypnozoites are responsible for long incubation and late relapses in \'\'P."}, {"text": "vivax infections, although their existence in P."}, {"text": "ovale\'\' is uncertain."}, {"list": ["immune system"], "text": "The parasite is relatively protected from attack by the body\'s immune system because for most of its human life cycle it resides within the liver and blood cells and is relatively invisible to immune surveillance."}, {"list": ["spleen"], "text": "However, circulating infected blood cells are destroyed in the spleen."}, {"text": "To avoid this fate, the \'\'P."}, {"list": ["proteins"], "text": "falciparum\'\' parasite displays adhesive proteins on the surface of the infected blood cells, causing the blood cells to stick to the walls of small blood vessels, thereby sequestering the parasite from passage through the general circulation and the spleen."}, {"text": "The blockage of the microvasculature causes symptoms such as in placental malaria."}, {"list": ["blood–brain barrier"], "text": "Sequestered red blood cells can breach the blood–brain barrier and cause cerebral malaria."}, {"list": ["mortality", "morbidity"], "text": "According to a 2005 review, due to the high levels of mortality and morbidity caused by malaria—especially the \'\'P."}, {"list": ["selective pressure", "human genome"], "text": "falciparum\'\' species—it has placed the greatest selective pressure on the human genome in recent history."}, {"list": ["sickle cell trait", "thalassaemia", "glucose-6-phosphate dehydrogenase deficiency", "Duffy antigens"], "text": "Several genetic factors provide some resistance to it including sickle cell trait, thalassaemia traits, glucose-6-phosphate dehydrogenase deficiency, and the absence of Duffy antigens on red blood cells."}, {"text": "The impact of sickle cell trait on malaria immunity illustrates some evolutionary trade-offs that have occurred because of endemic malaria."}, {"text": "Sickle cell trait causes a change in the hemoglobin molecule in the blood."}, {"list": ["capillaries", "hemoglobin S"], "text": "Normally, red blood cells have a very flexible, biconcave shape that allows them to move through narrow capillaries; however, when the modified hemoglobin S molecules are exposed to low amounts of oxygen, or crowd together due to dehydration, they can stick together forming strands that cause the cell to sickle or distort into a curved shape."}, {"text": "In these strands the molecule is not as effective in taking or releasing oxygen, and the cell is not flexible enough to circulate freely."}, {"text": "In the early stages of malaria, the parasite can cause infected red cells to sickle, and so they are removed from circulation sooner."}, {"text": "This reduces the frequency with which malaria parasites complete their life cycle in the cell."}, {"list": ["homozygous", "allele", "sickle-cell anaemia"], "text": "Individuals who are homozygous (with two copies of the abnormal hemoglobin beta allele) have sickle-cell anaemia, while those who are heterozygous (with one abnormal allele and one normal allele) experience resistance to malaria without severe anemia."}, {"list": ["benefits"], "text": "Although the shorter life expectancy for those with the homozygous condition would tend to disfavor the trait\'s survival, the trait is preserved in malaria-prone regions because of the benefits provided by the heterozygous form."}, {"list": ["viral hepatitis", "chronic liver disease"], "text": "Liver dysfunction as a result of malaria is uncommon and usually only occurs in those with another liver condition such as viral hepatitis or chronic liver disease."}, {"text": "The syndrome is sometimes called malarial hepatitis."}, {"text": "While it has been considered a rare occurrence, malarial hepatopathy has seen an increase, particularly in Southeast Asia and India."}, {"text": "Liver compromise in people with malaria correlates with a greater likelihood of complications and death."}, {"list": ["enlarged spleen", "low number of platelets", "higher-than-normal levels of bilirubin", "white blood cells"], "text": "Owing to the non-specific nature of the presentation of symptoms, diagnosis of malaria in non-endemic areas requires a high degree of suspicion, which might be elicited by any of the following: recent travel history, enlarged spleen, fever, low number of platelets in the blood, and higher-than-normal levels of bilirubin in the blood combined with a normal level of white blood cells."}, {"text": "Reports in 2016 and 2017 from countries were malaria is common suggest high levels of over diagnosis due to insufficient or inaccurate laboratory testing."}, {"list": ["blood films", "antigen", "rapid diagnostic tests"], "text": "Malaria is usually confirmed by the microscopic examination of blood films or by antigen-based rapid diagnostic tests (RDT)."}, {"text": "In some areas, RDTs need to be able to distinguish whether the malaria symptoms are caused by Plasmodium falciparum or by other species of parasites since treatment strategies could differ for non-\'\'P."}, {"text": "falciparum\'\' infections."}, {"text": "Microscopy is the most commonly used method to detect the malarial parasite—about 165 million blood films were examined for malaria in 2010."}, {"text": "Despite its widespread usage, diagnosis by microscopy suffers from two main drawbacks: many settings (especially rural) are not equipped to perform the test, and the accuracy of the results depends on both the skill of the person examining the blood film and the levels of the parasite in the blood."}, {"list": ["sensitivity"], "text": "The sensitivity of blood films ranges from 75–90% in optimum conditions, to as low as 50%."}, {"text": "Commercially available RDTs are often more accurate than blood films at predicting the presence of malaria parasites, but they are widely variable in diagnostic sensitivity and specificity depending on manufacturer, and are unable to tell how many parasites are present."}, {"text": "In regions where laboratory tests are readily available, malaria should be suspected, and tested for, in any unwell person who has been in an area where malaria is endemic."}, {"text": "In areas that cannot afford laboratory diagnostic tests, it has become common to use only a history of fever as the indication to treat for malaria—thus the common teaching \\"fever equals malaria unless proven otherwise\\"."}, {"list": ["overdiagnosis"], "text": "A drawback of this practice is overdiagnosis of malaria and mismanagement of non-malarial fever, which wastes limited resources, erodes confidence in the health care system, and contributes to drug resistance."}, {"list": ["polymerase chain reaction"], "text": "Although polymerase chain reaction-based tests have been developed, they are not widely used in areas where malaria is common as of 2012, due to their complexity."}, {"list": ["World Health Organization"], "text": "Malaria is classified into either \\"severe\\" or \\"uncomplicated\\" by the World Health Organization (WHO)."}, {"text": "It is deemed severe when any of the following criteria are present, otherwise it is considered uncomplicated."}, {"text": "Various types of malaria have been called by the names below:"}, {"text": "Methods used to prevent malaria include medications, mosquito elimination and the prevention of bites."}, {"list": ["vaccine for malaria"], "text": "There is no vaccine for malaria."}, {"text": "The presence of malaria in an area requires a combination of high human population density, high anopheles mosquito population density and high rates of transmission from humans to mosquitoes and from mosquitoes to humans."}, {"text": "If any of these is lowered sufficiently, the parasite will eventually disappear from that area, as happened in North America, Europe and parts of the Middle East."}, {"text": "However, unless the parasite is eliminated from the whole world, it could become re-established if conditions revert to a combination that favors the parasite\'s reproduction."}, {"text": "Furthermore, the cost per person of eliminating anopheles mosquitoes rises with decreasing population density, making it economically unfeasible in some areas."}, {"list": ["initial costs"], "text": "Prevention of malaria may be more cost-effective than treatment of the disease in the long run, but the initial costs required are out of reach of many of the world\'s poorest people."}, {"text": "There is a wide difference in the costs of control (i.e. maintenance of low endemicity) and elimination programs between countries."}, {"list": ["Chinese provinces"], "text": "For example, in China—whose government in 2010 announced a strategy to pursue malaria elimination in the Chinese provinces—the required investment is a small proportion of public expenditure on health."}, {"text": "In contrast, a similar program in Tanzania would cost an estimated one-fifth of the public health budget."}, {"list": ["anemia"], "text": "In areas where malaria is common, children under five years old often have anemia which is sometimes due to malaria."}, {"text": "Giving children with anemia in these areas preventive antimalarial medication improves red blood cell levels slightly but did not affect the risk of death or need for hospitalization."}, {"list": ["Vector control"], "text": "Vector control refers to methods used to decrease malaria by reducing the levels of transmission by mosquitoes."}, {"list": ["insect repellents", "DEET", "picaridin"], "text": "For individual protection, the most effective insect repellents are based on DEET or picaridin."}, {"list": ["mosquito nets", "indoor residual spraying"], "text": "Insecticide-treated mosquito nets (ITNs) and indoor residual spraying (IRS) have been shown to be highly effective in preventing malaria among children in areas where malaria is common."}, {"text": "Prompt treatment of confirmed cases with artemisinin-based combination therapies (ACTs) may also reduce transmission."}, {"text": "Mosquito nets help keep mosquitoes away from people and reduce infection rates and transmission of malaria."}, {"text": "Nets are not a perfect barrier and are often treated with an insecticide designed to kill the mosquito before it has time to find a way past the net."}, {"text": "Insecticide-treated nets are estimated to be twice as effective as untreated nets and offer greater than 70% protection compared with no net."}, {"text": "Between 2000 and 2008, the use of ITNs saved the lives of an estimated 250,000 infants in Sub-Saharan Africa."}, {"text": "About 13% of households in Sub-Saharan countries owned ITNs in 2007 and 31% of African households were estimated to own at least one ITN in 2008."}, {"text": "In 2000, 1.7 million (1.8%) African children living in areas of the world where malaria is common were protected by an ITN."}, {"text": "That number increased to 20.3 million (18.5%) African children using ITNs in 2007, leaving 89.6 million children unprotected and to 68% African children using mosquito nets in 2015."}, {"list": ["pyrethroids", "toxicity"], "text": "Most nets are impregnated with pyrethroids, a class of insecticides with low toxicity."}, {"text": "They are most effective when used from dusk to dawn."}, {"text": "It is recommended to hang a large \\"bed net\\" above the center of a bed and either tuck the edges under the mattress or make sure it is large enough such that it touches the ground."}, {"text": "Indoor residual spraying is the spraying of insecticides on the walls inside a home."}, {"text": "After feeding, many mosquitoes rest on a nearby surface while digesting the bloodmeal, so if the walls of houses have been coated with insecticides, the resting mosquitoes can be killed before they can bite another person and transfer the malaria parasite."}, {"list": ["World Health Organization", "DDT", "cyfluthrin", "deltamethrin"], "text": "As of 2006, the World Health Organization recommends 12 insecticides in IRS operations, including DDT and the pyrethroids cyfluthrin and deltamethrin."}, {"list": ["Stockholm Convention"], "text": "This public health use of small amounts of DDT is permitted under the Stockholm Convention, which prohibits its agricultural use."}, {"list": ["insecticide resistance"], "text": "One problem with all forms of IRS is insecticide resistance."}, {"text": "Mosquitoes affected by IRS tend to rest and live indoors, and due to the irritation caused by spraying, their descendants tend to rest and live outdoors, meaning that they are less affected by the IRS."}, {"text": "There are a number of other methods to reduce mosquito bites and slow the spread of malaria."}, {"text": "Efforts to decrease mosquito larva by decreasing the availability of open water in which they develop or by adding substances to decrease their development is effective in some locations."}, {"text": "Electronic mosquito repellent devices which make very high-frequency sounds that are supposed to keep female mosquitoes away, do not have supporting evidence."}, {"list": ["health education"], "text": "Community participation and health education strategies promoting awareness of malaria and the importance of control measures have been successfully used to reduce the incidence of malaria in some areas of the developing world."}, {"text": "Recognizing the disease in the early stages can prevent the disease from becoming fatal."}, {"text": "Education can also inform people to cover over areas of stagnant, still water, such as water tanks that are ideal breeding grounds for the parasite and mosquito, thus cutting down the risk of the transmission between people."}, {"text": "This is generally used in urban areas where there are large centers of population in a confined space and transmission would be most likely in these areas."}, {"list": ["Intermittent preventive therapy"], "text": "Intermittent preventive therapy is another intervention that has been used successfully to control malaria in pregnant women and infants, and in preschool children where transmission is seasonal."}, {"text": "There are a number of medications that can help prevent or interrupt malaria in travelers to places where infection is common."}, {"text": "Many of these medications are also used in treatment."}, {"list": ["mefloquine", "doxycycline", "atovaquone/proguanil"], "text": "In places where Plasmodium is resistant to one or more medications, three medications—mefloquine, doxycycline, or the combination of atovaquone/proguanil (Malarone)—are frequently used for prevention."}, {"text": "Doxycycline and the atovaquone/proguanil are better tolerated while mefloquine is taken once a week."}, {"list": ["chloroquine"], "text": "Areas of the world with chloroquine sensitive malaria are uncommon."}, {"text": "The protective effect does not begin immediately, and people visiting areas where malaria exists usually start taking the drugs one to two weeks before arriving and continue taking them for four weeks after leaving (except for atovaquone/proguanil, which only needs to be started two days before and continued for seven days afterward)."}, {"text": "The use of preventative drugs is often not practical for those who live in areas where malaria exists, and their use is usually only in pregnant women and short-term visitors."}, {"list": ["side effects"], "text": "This is due to the cost of the drugs, side effects from long-term use, and the difficulty in obtaining anti-malarial drugs outside of wealthy nations."}, {"list": ["anemia"], "text": "During pregnancy, medication to prevent malaria has been found to improve the weight of the baby at birth and decrease the risk of anemia in the mother."}, {"text": "The use of preventative drugs where malaria-bearing mosquitoes are present may encourage the development of partial resistance."}, {"list": ["antimalarial medications"], "text": "Malaria is treated with antimalarial medications; the ones used depends on the type and severity of the disease."}, {"list": ["medications against fever"], "text": "While medications against fever are commonly used, their effects on outcomes are not clear."}, {"text": "Simple or uncomplicated malaria may be treated with oral medications."}, {"text": "The most effective treatment for \'\'P."}, {"list": ["artemisinins", "artemisinin-combination therapy"], "text": "falciparum\'\' infection is the use of artemisinins in combination with other antimalarials (known as artemisinin-combination therapy, or ACT), which decreases resistance to any single drug component."}, {"list": ["amodiaquine", "lumefantrine", "sulfadoxine/pyrimethamine"], "text": "These additional antimalarials include: amodiaquine, lumefantrine, mefloquine or sulfadoxine/pyrimethamine."}, {"list": ["dihydroartemisinin", "piperaquine"], "text": "Another recommended combination is dihydroartemisinin and piperaquine."}, {"text": "ACT is about 90% effective when used to treat uncomplicated malaria."}, {"list": ["clindamycin"], "text": "To treat malaria during pregnancy, the WHO recommends the use of quinine plus clindamycin early in the pregnancy (1st trimester), and ACT in later stages (2nd and 3rd trimesters)."}, {"text": "In the 2000s (decade), malaria with partial resistance to artemisins emerged in Southeast Asia."}, {"text": "Infection with \'\'P."}, {"text": "vivax, P."}, {"text": "ovale or P."}, {"text": "malariae\'\' usually do not require hospitalization."}, {"text": "Treatment of \'\'P."}, {"list": ["primaquine"], "text": "vivax\'\' requires both treatment of blood stages (with chloroquine or ACT) and clearance of liver forms with primaquine."}, {"list": ["tafenoquine"], "text": "Treatment with tafenoquine prevents relapses after confirmed \'\'P."}, {"text": "vivax\'\' malaria."}, {"text": "Severe and complicated malaria are almost always caused by infection with \'\'P."}, {"text": "falciparum\'\'."}, {"text": "The other species usually cause only febrile disease."}, {"text": "Severe and complicated malaria are medical emergencies since mortality rates are high (10% to 50%)."}, {"text": "Cerebral malaria is the form of severe and complicated malaria with the worst neurological symptoms."}, {"list": ["intravenous"], "text": "Recommended treatment for severe malaria is the intravenous use of antimalarial drugs."}, {"list": ["parenteral"], "text": "For severe malaria, parenteral artesunate was superior to quinine in both children and adults."}, {"text": "In another systematic review, artemisinin derivatives (artemether and arteether) were as efficacious as quinine in the treatment of cerebral malaria in children."}, {"list": ["critical care unit"], "text": "Treatment of severe malaria involves supportive measures that are best done in a critical care unit."}, {"list": ["high fevers", "poor breathing effort", "low blood potassium"], "text": "This includes the management of high fevers and the seizures that may result from it. It also includes monitoring for poor breathing effort, low blood sugar, and low blood potassium."}, {"list": ["Drug resistance"], "text": "Drug resistance poses a growing problem in 21st-century malaria treatment."}, {"list": ["artemisinins"], "text": "Resistance is now common against all classes of antimalarial drugs apart from artemisinins."}, {"text": "Treatment of resistant strains became increasingly dependent on this class of drugs."}, {"text": "The cost of artemisinins limits their use in the developing world."}, {"text": "Malaria strains found on the Cambodia–Thailand border are resistant to combination therapies that include artemisinins, and may, therefore, be untreatable."}, {"text": "Exposure of the parasite population to artemisinin monotherapies in subtherapeutic doses for over 30 years and the availability of substandard artemisinins likely drove the selection of the resistant phenotype."}, {"text": "Resistance to artemisinin has been detected in Cambodia, Myanmar, Thailand, and Vietnam, and there has been emerging resistance in Laos."}, {"list": ["Disability-adjusted life year", "fatality rates", "Chronic", "Salmonella\'\'", "Epstein–Barr virus", "behavioural disorders", "epilepsy", "clinical trials", "placebo"], "text": "Malaria world map - DALY - WHO2004.svg infection without severe disease can occur in an immune-deficiency syndrome associated with a decreased responsiveness to Salmonella bacteria and the Epstein–Barr virus. During childhood, malaria causes anemia during a period of rapid brain development, and also direct brain damage resulting from cerebral malaria. Some survivors of cerebral malaria have an increased risk of neurological and cognitive deficits, behavioural disorders, and epilepsy. Malaria prophylaxis was shown to improve cognitive function and school performance in clinical trials when compared to placebo groups."}, {"list": ["Sub-Saharan Africa", "Ivory Coast", "Angola", "Burkina Faso", "Mozambique", "Mali", "Malaria Atlas Project", "endemic", "disease burden", "Greater Mekong Subregion"], "text": "Paludisme.png WHO estimates that in 2015 there were 214 million new cases of malaria resulting in 438,000 deaths. Others have estimated the number of cases at between 350 and 550 million for falciparum malaria The majority of cases (65%) occur in children under 15 years old. About 125 million pregnant women are at risk of infection each year; in Sub-Saharan Africa, maternal malaria is associated with up to 200,000 estimated infant deaths yearly. There are about 10,000 malaria cases per year in Western Europe, and 1300–1500 in the United States. About 900 people died from the disease in Europe between 1993 and 2003.  Both the global incidence of disease and resulting mortality have declined in recent years. According to the WHO and UNICEF, deaths attributable to malaria in 2015 were reduced by 60% from a 2000 estimate of 985,000, largely due to the widespread use of insecticide-treated nets and artemisinin-based combination therapies. In 2012, there were 207 million cases of malaria.<! --> That year, the disease is estimated to have killed between 473,000 and 789,000 people, many of whom were children in Africa. Efforts at decreasing the disease in Africa since the turn of millennium have been partially effective, with rates of the disease dropping by an estimated forty percent on the continent.  Malaria is presently endemic in a broad band around the equator, in areas of the Americas, many parts of Asia, and much of Africa; in Sub-Saharan Africa, 85–90% of malaria fatalities occur. An estimate for 2009 reported that countries with the highest death rate per 100,000 of population were Ivory Coast (86.15), Angola (56.93) and Burkina Faso (50.66). A 2010 estimate indicated the deadliest countries per population were Burkina Faso, Mozambique and Mali. The Malaria Atlas Project aims to map global endemic levels of malaria, providing a means with which to determine the global spatial limits of the disease and to assess disease burden.  This effort led to the publication of a map of P. falciparum endemicity in 2010. As of 2010, about 100 countries have endemic malaria.  Every year, 125 million international travellers visit these countries, and more than 30,000 contract the disease. The geographic distribution of malaria within large regions is complex, and malaria-afflicted and malaria-free areas are often found close to each other. Malaria is prevalent in tropical and subtropical regions because of rainfall, consistent high temperatures and high humidity, along with stagnant waters in which mosquito larvae readily mature, providing them with the environment they need for continuous breeding. In drier areas, outbreaks of malaria have been predicted with reasonable accuracy by mapping rainfall. Malaria is more common in rural areas than in cities. For example, several cities in the Greater Mekong Subregion of Southeast Asia are essentially malaria-free, but the disease is prevalent in many rural regions, including along international borders and forest fringes. In contrast, malaria in Africa is present in both rural and urban areas, though the risk is lower in the larger cities."}, {"text": "Although the parasite responsible for \'\'P."}, {"text": "falciparum\'\' malaria has been in existence for 50,000–100,000 years, the population size of the parasite did not increase until about 10,000 years ago, concurrently with advances in agriculture and the development of human settlements."}, {"text": "Close relatives of the human malaria parasites remain common in chimpanzees."}, {"text": "Some evidence suggests that the \'\'P."}, {"text": "falciparum\'\' malaria may have originated in gorillas."}, {"text": "References to the unique periodic fevers of malaria are found throughout recorded history."}, {"text": "Hippocrates described periodic fevers, labelling them tertian, quartan, subtertian and quotidian."}, {"list": ["Columella"], "text": "The Roman Columella associated the disease with insects from swamps."}, {"list": ["Roman Empire", "Roman fever"], "text": "Malaria may have contributed to the decline of the Roman Empire, and was so pervasive in Rome that it was known as the \\"Roman fever\\"."}, {"text": "Several regions in ancient Rome were considered at-risk for the disease because of the favourable conditions present for malaria vectors."}, {"list": ["Sardinia", "Pontine Marshes", "Etruria", "Rome", "Tiber River"], "text": "This included areas such as southern Italy, the island of Sardinia, the Pontine Marshes, the lower regions of coastal Etruria and the city of Rome along the Tiber River."}, {"text": "The presence of stagnant water in these places was preferred by mosquitoes for breeding grounds."}, {"text": "Irrigated gardens, swamp-like grounds, runoff from agriculture, and drainage problems from road construction led to the increase of standing water."}, {"list": ["Medieval", "bad air"], "text": "The term malaria originates from Medieval —\\"bad air\\"; the disease was formerly called ague or marsh fever due to its association with swamps and marshland."}, {"text": "The term first appeared in the English literature about 1829."}, {"text": "Malaria was once common in most of Europe and North America, where it is no longer endemic, though imported cases do occur."}, {"list": ["Charles Louis Alphonse Laveran", "Constantine", "Algeria"], "text": "Scientific studies on malaria made their first significant advance in 1880, when Charles Louis Alphonse Laveran—a French army doctor working in the military hospital of Constantine in Algeria—observed parasites inside the red blood cells of infected people for the first time."}, {"list": ["protist"], "text": "He, therefore, proposed that malaria is caused by this organism, the first time a protist was identified as causing disease."}, {"list": ["Nobel Prize for Physiology or Medicine"], "text": "For this and later discoveries, he was awarded the 1907 Nobel Prize for Physiology or Medicine."}, {"list": ["Carlos Finlay", "yellow fever", "Havana"], "text": "A year later, Carlos Finlay, a Cuban doctor treating people with yellow fever in Havana, provided strong evidence that mosquitoes were transmitting disease to and from humans."}, {"list": ["Josiah C. Nott", "Sir Patrick Manson", "filariasis"], "text": "This work followed earlier suggestions by Josiah C. Nott, and work by Sir Patrick Manson, the \\"father of tropical medicine\\", on the transmission of filariasis."}, {"list": ["Sir Ronald Ross"], "text": "In April 1894, a Scottish physician Sir Ronald Ross visited Sir Patrick Manson at his house on Queen Anne Street, London."}, {"list": ["Presidency General Hospital", "Calcutta"], "text": "This visit was the start of four years of collaboration and fervent research that culminated in 1897 when Ross, who was working in the Presidency General Hospital in Calcutta, proved the complete life-cycle of the malaria parasite in mosquitoes."}, {"text": "He thus proved that the mosquito was the vector for malaria in humans by showing that certain mosquito species transmit malaria to birds."}, {"text": "He isolated malaria parasites from the salivary glands of mosquitoes that had fed on infected birds."}, {"text": "For this work, Ross received the 1902 Nobel Prize in Medicine."}, {"list": ["Liverpool School of Tropical Medicine", "Egypt", "Panama", "Greece", "Mauritius"], "text": "After resigning from the Indian Medical Service, Ross worked at the newly established Liverpool School of Tropical Medicine and directed malaria-control efforts in Egypt, Panama, Greece and Mauritius."}, {"list": ["Walter Reed"], "text": "The findings of Finlay and Ross were later confirmed by a medical board headed by Walter Reed in 1900."}, {"list": ["William C. Gorgas", "Panama Canal"], "text": "Its recommendations were implemented by William C. Gorgas in the health measures undertaken during construction of the Panama Canal."}, {"text": "This public-health work saved the lives of thousands of workers and helped develop the methods used in future public-health campaigns against the disease."}, {"list": ["cinchona tree", "quinine"], "text": "The first effective treatment for malaria came from the bark of cinchona tree, which contains quinine."}, {"list": ["Andes", "Peru"], "text": "This tree grows on the slopes of the Andes, mainly in Peru."}, {"list": ["indigenous peoples of Peru", "tincture"], "text": "The indigenous peoples of Peru made a tincture of cinchona to control fever."}, {"list": ["Jesuits", "London Pharmacopoeia"], "text": "Its effectiveness against malaria was found and the Jesuits introduced the treatment to Europe around 1640; by 1677, it was included in the London Pharmacopoeia as an antimalarial treatment."}, {"list": ["Pierre Joseph Pelletier", "Joseph Bienaimé Caventou"], "text": "It was not until 1820 that the active ingredient, quinine, was extracted from the bark, isolated and named by the French chemists Pierre Joseph Pelletier and Joseph Bienaimé Caventou."}, {"text": "Quinine became the predominant malarial medication until the 1920s when other medications began to be developed."}, {"text": "In the 1940s, chloroquine replaced quinine as the treatment of both uncomplicated and severe malaria until resistance supervened, first in Southeast Asia and South America in the 1950s and then globally in the 1980s."}, {"list": ["Artemisia annua\'\'", "traditional Chinese medicines"], "text": "The medicinal value of Artemisia annua has been used by Chinese herbalists in traditional Chinese medicines for 2,000 years."}, {"list": ["Compendium of Materia Medica"], "text": "In 1596, Li Shizhen recommended tea made from qinghao specifically to treat malaria symptoms in his \\"Compendium of Materia Medica\\"."}, {"list": ["Tu Youyou"], "text": "Artemisinins, discovered by Chinese scientist Tu Youyou and colleagues in the 1970s from the plant Artemisia annua, became the recommended treatment for \'\'P."}, {"text": "falciparum\'\' malaria, administered in severe cases in combination with other antimalarials."}, {"list": ["traditional Chinese herbal medicine", "Ge Hong"], "text": "Tu says she was influenced by a traditional Chinese herbal medicine source, The Handbook of Prescriptions for Emergency Treatments, written in 340 by Ge Hong."}, {"list": ["Tu Youyou", "Nobel Prize in Physiology or Medicine"], "text": "For her work on malaria, Tu Youyou received the 2015 Nobel Prize in Physiology or Medicine."}, {"list": ["malariotherapy", "syphilis"], "text": "Plasmodium vivax was used between 1917 and the 1940s for malariotherapy—deliberate injection of malaria parasites to induce a fever to combat certain diseases such as tertiary syphilis."}, {"list": ["Julius Wagner-Jauregg"], "text": "In 1927, the inventor of this technique, Julius Wagner-Jauregg, received the Nobel Prize in Physiology or Medicine for his discoveries."}, {"text": "The technique was dangerous, killing about 15% of patients, so it is no longer in use."}, {"list": ["DDT"], "text": "The first pesticide used for indoor residual spraying was DDT."}, {"list": ["agriculture"], "text": "Although it was initially used exclusively to combat malaria, its use quickly spread to agriculture."}, {"list": ["resistant"], "text": "In time, pest control, rather than disease control, came to dominate DDT use, and this large-scale agricultural use led to the evolution of resistant mosquitoes in many regions."}, {"list": ["antibiotic resistance"], "text": "The DDT resistance shown by Anopheles mosquitoes can be compared to antibiotic resistance shown by bacteria."}, {"text": "During the 1960s, awareness of the negative consequences of its indiscriminate use increased, ultimately leading to bans on agricultural applications of DDT in many countries in the 1970s."}, {"list": ["Paris Green"], "text": "Before DDT, malaria was successfully eliminated or controlled in tropical areas like Brazil and Egypt by removing or poisoning the breeding grounds of the mosquitoes or the aquatic habitats of the larva stages, for example by applying the highly toxic arsenic compound Paris Green to places with standing water."}, {"list": ["Malaria vaccines"], "text": "Malaria vaccines have been an elusive goal of research."}, {"list": ["attenuated"], "text": "The first promising studies demonstrating the potential for a malaria vaccine were performed in 1967 by immunizing mice with live, radiation-attenuated sporozoites, which provided significant protection to the mice upon subsequent injection with normal, viable sporozoites."}, {"text": "Since the 1970s, there has been a considerable effort to develop similar vaccination strategies for humans."}, {"list": ["RTS,S"], "text": "The first vaccine, called RTS,S, was approved by European regulators in 2015."}, {"list": ["economic development"], "text": "Malaria is not just a disease commonly associated with poverty: some evidence suggests that it is also a cause of poverty and a major hindrance to economic development."}, {"text": "Although tropical regions are most affected, malaria\'s furthest influence reaches into some temperate zones that have extreme seasonal changes."}, {"text": "The disease has been associated with major negative economic effects on regions where it is widespread."}, {"text": "During the late 19th and early 20th centuries, it was a major factor in the slow economic development of the American southern states."}, {"list": ["GDP", "parity of purchasing power"], "text": "A comparison of average per capita GDP in 1995, adjusted for parity of purchasing power, between countries with malaria and countries without malaria gives a fivefold difference ($1,526 USD versus $8,268 USD)."}, {"text": "In the period 1965 to 1990, countries where malaria was common had an average per capita GDP that increased only 0.4% per year, compared to 2.4% per year in other countries."}, {"text": "Poverty can increase the risk of malaria since those in poverty do not have the financial capacities to prevent or treat the disease."}, {"text": "In its entirety, the economic impact of malaria has been estimated to cost Africa US$12 billion every year."}, {"text": "The economic impact includes costs of health care, working days lost due to sickness, days lost in education, decreased productivity due to brain damage from cerebral malaria, and loss of investment and tourism."}, {"list": ["outpatient"], "text": "The disease has a heavy burden in some countries, where it may be responsible for 30–50% of hospital admissions, up to 50% of outpatient visits, and up to 40% of public health spending."}, {"text": "Cerebral malaria is one of the leading causes of neurological disabilities in African children."}, {"text": "Studies comparing cognitive functions before and after treatment for severe malarial illness continued to show significantly impaired school performance and cognitive abilities even after recovery."}, {"list": ["socioeconomic"], "text": "Consequently, severe and cerebral malaria have far-reaching socioeconomic consequences that extend beyond the immediate effects of the disease."}, {"list": ["counterfeits", "Cambodia", "China", "Indonesia", "Laos", "Thailand", "Vietnam"], "text": "Sophisticated counterfeits have been found in several Asian countries such as Cambodia, China, Indonesia, Laos, Thailand, and Vietnam, and are an important cause of avoidable death in those countries."}, {"list": ["Mekong"], "text": "The WHO said that studies indicate that up to 40% of artesunate-based malaria medications are counterfeit, especially in the Greater Mekong region and have established a rapid alert system to enable information about counterfeit drugs to be rapidly reported to the relevant authorities in participating countries."}, {"text": "There is no reliable way for doctors or lay people to detect counterfeit drugs without help from a laboratory."}, {"text": "Companies are attempting to combat the persistence of counterfeit drugs by using new technology to provide security from source to distribution."}, {"text": "Another clinical and public health concern is the proliferation of substandard antimalarial medicines resulting from inappropriate concentration of ingredients, contamination with other drugs or toxic impurities, poor quality ingredients, poor stability and inadequate packaging."}, {"text": "A 2012 study demonstrated that roughly one-third of antimalarial medications in Southeast Asia and Sub-Saharan Africa failed chemical analysis, packaging analysis, or were falsified."}, {"text": "Throughout history, the contraction of malaria has played a prominent role in the fates of government rulers, nation-states, military personnel, and military actions."}, {"list": ["Nobel Prize in Medicine", "Royal Army Medical College"], "text": "In 1910, Nobel Prize in Medicine-winner Ronald Ross (himself a malaria survivor), published a book titled The Prevention of Malaria that included a chapter titled \\"The Prevention of Malaria in War.\\" The chapter\'s author, Colonel C. H. Melville, Professor of Hygiene at Royal Army Medical College in London, addressed the prominent role that malaria has historically played during wars: \\"The history of malaria in war might almost be taken to be the history of war itself, certainly the history of war in the Christian era. ... It is probably the case that many of the so-called camp fevers, and probably also a considerable proportion of the camp dysentery, of the wars of the sixteenth, seventeenth and eighteenth centuries were malarial in origin.\\""}, {"list": ["World War II"], "text": "Malaria was the most significant health hazard encountered by U.S. troops in the South Pacific during World War II, where about 500,000 men were infected."}, {"text": "According to Joseph Patrick Byrne, \\"Sixty thousand American soldiers died of malaria during the African and South Pacific campaigns.\\""}, {"text": "Significant financial investments have been made to procure existing and create new anti-malarial agents."}, {"list": ["World War I", "cinchona bark", "research and development"], "text": "During World War I and World War II, inconsistent supplies of the natural anti-malaria drugs cinchona bark and quinine prompted substantial funding into research and development of other drugs and vaccines."}, {"list": ["Walter Reed Army Institute of Research", "U.S. Army Medical Research Institute of Infectious Diseases"], "text": "American military organizations conducting such research initiatives include the Navy Medical Research Center, Walter Reed Army Institute of Research, and the U.S. Army Medical Research Institute of Infectious Diseases of the US Armed Forces."}, {"list": ["Centers for Disease Control and Prevention"], "text": "Additionally, initiatives have been founded such as Malaria Control in War Areas (MCWA), established in 1942, and its successor, the Communicable Disease Center (now known as the Centers for Disease Control and Prevention, or CDC) established in 1946."}, {"text": "According to the CDC, MCWA \\"was established to control malaria around military training bases in the southern United States and its territories, where malaria was still problematic\\"."}, {"list": ["eradicate it worldwide"], "text": "Several notable attempts are being made to eliminate the parasite from sections of the world, or to eradicate it worldwide."}, {"list": ["Malaria No More"], "text": "In 2006, the organization Malaria No More set a public goal of eliminating malaria from Africa by 2015, and the organization plans to dissolve if that goal is accomplished."}, {"text": "Several malaria vaccines are in clinical trials, which are intended to provide protection for children in endemic areas and reduce the speed of transmission of the disease."}, {"list": ["The Global Fund to Fight AIDS, Tuberculosis and Malaria"], "text": ", The Global Fund to Fight AIDS, Tuberculosis and Malaria has distributed 230 million insecticide-treated nets intended to stop mosquito-borne transmission of malaria."}, {"list": ["Clinton Foundation"], "text": "The U.S.-based Clinton Foundation has worked to manage demand and stabilize prices in the artemisinin market."}, {"text": "Other efforts, such as the Malaria Atlas Project, focus on analysing climate and weather information required to accurately predict the spread of malaria based on the availability of habitat of malaria-carrying parasites."}, {"list": ["Malaria Policy Advisory Committee", "World Health Organization"], "text": "The Malaria Policy Advisory Committee (MPAC) of the World Health Organization (WHO) was formed in 2012, \\"to provide strategic advice and technical input to WHO on all aspects of malaria control and elimination\\"."}, {"text": "In November 2013, WHO and the malaria vaccine funders group set a goal to develop vaccines designed to interrupt malaria transmission with the long-term goal of malaria eradication."}, {"text": "Malaria has been successfully eliminated or greatly reduced in certain areas."}, {"text": "Malaria was once common in the United States and southern Europe, but vector control programs, in conjunction with the monitoring and treatment of infected humans, eliminated it from those regions."}, {"list": ["water management"], "text": "Several factors contributed, such as the draining of wetland breeding grounds for agriculture and other changes in water management practices, and advances in sanitation, including greater use of glass windows and screens in dwellings."}, {"list": ["pesticide", "National Malaria Eradication Program"], "text": "Malaria was eliminated from most parts of the USA in the early 20th century by such methods, and the use of the pesticide DDT and other means eliminated it from the remaining pockets in the South in the 1950s as part of the National Malaria Eradication Program."}, {"text": "Bill Gates has said that he thinks global eradication is possible by 2040."}, {"text": "The Malaria Eradication Research Agenda (malERA) initiative was a consultative process to identify which areas of research and development (R&D) needed to be addressed for the worldwide eradication of malaria."}, {"list": ["RTS,S"], "text": "A vaccine against malaria called RTS,S, was approved by European regulators in 2015."}, {"text": "It is undergoing pilot trials in select countries in 2016."}, {"list": ["tolerance"], "text": "Immunity (or, more accurately, tolerance) to \'\'P."}, {"text": "falciparum\'\' malaria does occur naturally, but only in response to years of repeated infection."}, {"text": "An individual can be protected from a \'\'P."}, {"list": ["X-ray", "irradiation"], "text": "falciparum\'\' infection if they receive about a thousand bites from mosquitoes that carry a version of the parasite rendered non-infective by a dose of X-ray irradiation."}, {"list": ["polymorphic"], "text": "The highly polymorphic nature of many \'\'P."}, {"text": "falciparum\'\' proteins results in significant challenges to vaccine design."}, {"text": "Vaccine candidates that target antigens on gametes, zygotes, or ookinetes in the mosquito midgut aim to block the transmission of malaria."}, {"text": "These transmission-blocking vaccines induce antibodies in the human blood; when a mosquito takes a blood meal from a protected individual, these antibodies prevent the parasite from completing its development in the mosquito."}, {"text": "Other vaccine candidates, targeting the blood-stage of the parasite\'s life cycle, have been inadequate on their own."}, {"list": ["SPf66"], "text": "For example, SPf66 was tested extensively in areas where the disease is common in the 1990s, but trials showed it to be insufficiently effective."}, {"list": ["apicoplasts", "genomes"], "text": "Malaria parasites contain apicoplasts, organelles usually found in plants, complete with their own genomes."}, {"list": ["endosymbiosis", "fatty acid biosynthesis"], "text": "These apicoplasts are thought to have originated through the endosymbiosis of algae and play a crucial role in various aspects of parasite metabolism, such as fatty acid biosynthesis."}, {"text": "Over 400 proteins have been found to be produced by apicoplasts and these are now being investigated as possible targets for novel anti-malarial drugs."}, {"text": "With the onset of drug-resistant Plasmodium parasites, new strategies are being developed to combat the widespread disease."}, {"list": ["pyridoxal", "adducts", "B vitamins"], "text": "One such approach lies in the introduction of synthetic pyridoxal-amino acid adducts, which are taken up by the parasite and ultimately interfere with its ability to create several essential B vitamins."}, {"list": ["synthetic metal-based", "complexes"], "text": "Antimalarial drugs using synthetic metal-based complexes are attracting research interest."}, {"text": "A non-chemical vector control strategy involves genetic manipulation of malaria mosquitoes."}, {"list": ["genetic engineering"], "text": "Advances in genetic engineering technologies make it possible to introduce foreign DNA into the mosquito genome and either decrease the lifespan of the mosquito, or make it more resistant to the malaria parasite."}, {"list": ["Sterile insect technique"], "text": "Sterile insect technique is a genetic control method whereby large numbers of sterile male mosquitoes are reared and released."}, {"text": "Mating with wild females reduces the wild population in the subsequent generation; repeated releases eventually eliminate the target population."}, {"list": ["Genomics"], "text": "Genomics is central to malaria research."}, {"list": ["sequencing"], "text": "With the sequencing of \'\'P."}, {"list": ["human genome"], "text": "falciparum, one of its vectors Anopheles gambiae\'\', and the human genome, the genetics of all three organisms in the malaria lifecycle can be studied."}, {"list": ["genetically modified", "biological control"], "text": "Another new application of genetic technology is the ability to produce genetically modified mosquitoes that do not transmit malaria, potentially allowing biological control of malaria transmission."}, {"list": ["Anopheles stephensi\'\'"], "text": "In one study, a genetically-modified strain of Anopheles stephensi was created that no longer supported malaria transmission, and this resistance was passed down to mosquito offspring."}, {"list": ["Gene drive", "zika"], "text": "Gene drive is a technique for changing wild populations, for instance to combat insects so they cannot transmit diseases (in particular mosquitoes in the cases of malaria and zika)."}, {"list": ["birds", "reptiles", "other mammals"], "text": "Nearly 200 parasitic Plasmodium species have been identified that infect birds, reptiles, and other mammals, and about 30 species naturally infect non-human primates."}, {"list": ["model organisms", "P. coatneyi\'\'"], "text": "Some malaria parasites that affect non-human primates (NHP) serve as model organisms for human malarial parasites, such as P. coatneyi (a model for \'\'P."}, {"list": ["P. cynomolgi\'\'"], "text": "falciparum) and P. cynomolgi (P."}, {"text": "vivax\'\')."}, {"text": "Diagnostic techniques used to detect parasites in NHP are similar to those employed for humans."}, {"list": ["P. berghei\'\'"], "text": "Malaria parasites that infect rodents are widely used as models in research, such as P. berghei."}, {"list": ["Avian malaria", "Passeriformes", "Hawaii", "Galapagos", "archipelagoes"], "text": "Avian malaria primarily affects species of the order Passeriformes, and poses a substantial threat to birds of Hawaii, the Galapagos, and other archipelagoes."}, {"list": ["P. relictum\'\'", "endemic Hawaiian birds"], "text": "The parasite P. relictum is known to play a role in limiting the distribution and abundance of endemic Hawaiian birds."}, {"list": ["Global warming"], "text": "Global warming is expected to increase the prevalence and global distribution of avian malaria, as elevated temperatures provide optimal conditions for parasite reproduction."}, {"text": ";Cited literature"}]', '{"list": ["Mosquito nets", " insect repellent", " mosquito control", " medications"], "conditions": ["Mosquito nets", "insect repellent", "mosquito control"]}', '{"list": ["Yellow skin", " seizures", " coma"], "conditions": ["Yellow skin", "seizures", "coma"]}', '{"desc": "Antimalarial medication", "list": ["Antimalarial medication"]}', '[{"url": "https://upload.wikimedia.org/wikipedia/commons/f/f1/Malaria.jpg", "file": "File:Malaria.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f1/Malaria.jpg/300px-Malaria.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/3/3d/Malaria.webm", "file": "File:Malaria.webm", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3d/Malaria.webm/300px-Malaria.webm"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/d/db/Symptoms_of_Malaria.png", "file": "File:Symptoms of Malaria.png", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/d/db/Symptoms_of_Malaria.png/300px-Symptoms_of_Malaria.png"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/9/9a/Life_Cycle_of_the_Malaria_Parasite.jpg", "file": "File:Life Cycle of the Malaria Parasite.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/9/9a/Life_Cycle_of_the_Malaria_Parasite.jpg/300px-Life_Cycle_of_the_Malaria_Parasite.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/1/12/Maternal_malaria_placenta_-_cropped_-_very_high_mag.jpg", "file": "File:Maternal malaria placenta - cropped - very high mag.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/1/12/Maternal_malaria_placenta_-_cropped_-_very_high_mag.jpg/300px-Maternal_malaria_placenta_-_cropped_-_very_high_mag.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/5/5b/Red_blood_cells_infected_with_malaria.jpg", "file": "File:Red blood cells infected with malaria.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/5/5b/Red_blood_cells_infected_with_malaria.jpg/300px-Red_blood_cells_infected_with_malaria.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/1/13/5901_lores.jpg", "file": "File:5901 lores.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/1/13/5901_lores.jpg/300px-5901_lores.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/3/3c/Plasmodium.jpg", "file": "File:Plasmodium.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3c/Plasmodium.jpg/300px-Plasmodium.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/7/7e/Anopheles_stephensi.jpeg", "file": "File:Anopheles stephensi.jpeg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/7/7e/Anopheles_stephensi.jpeg/300px-Anopheles_stephensi.jpeg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/4/41/Mansprayingkeroseneoil.jpg", "file": "File:Mansprayingkeroseneoil.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/4/41/Mansprayingkeroseneoil.jpg/300px-Mansprayingkeroseneoil.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/2/24/Mosquitoes-Killedy-By-DDT-Lake-Victoria.JPG", "file": "File:Mosquitoes-Killedy-By-DDT-Lake-Victoria.JPG", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/2/24/Mosquitoes-Killedy-By-DDT-Lake-Victoria.JPG/300px-Mosquitoes-Killedy-By-DDT-Lake-Victoria.JPG"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/5/5d/Mosquitonet149.jpg", "file": "File:Mosquitonet149.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/5/5d/Mosquitonet149.jpg/300px-Mosquitonet149.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/a/a0/%22British_India%22%2C_six_stages_of_malaria._Wellcome_L0022443.jpg", "file": "File:\\"British India\\", six stages of malaria. Wellcome L0022443.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/a/a0/%22British_India%22%2C_six_stages_of_malaria._Wellcome_L0022443.jpg/300px-%22British_India%22%2C_six_stages_of_malaria._Wellcome_L0022443.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/f/f6/Malaria_pathogens.jpg", "file": "File:Malaria pathogens.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f6/Malaria_pathogens.jpg/300px-Malaria_pathogens.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/7/76/Ronald_Ross.jpg", "file": "File:Ronald Ross.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/7/76/Ronald_Ross.jpg/300px-Ronald_Ross.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/a/aa/Tu_Youyou_5012-1-2015.jpg", "file": "File:Tu Youyou 5012-1-2015.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/a/aa/Tu_Youyou_5012-1-2015.jpg/300px-Tu_Youyou_5012-1-2015.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/f/fd/Artemisia_annua_West_Virginia.jpg", "file": "File:Artemisia annua West Virginia.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/f/fd/Artemisia_annua_West_Virginia.jpg/300px-Artemisia_annua_West_Virginia.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/7/76/GuadMarinesMalaria.gif", "file": "File:GuadMarinesMalaria.gif", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/7/76/GuadMarinesMalaria.gif/300px-GuadMarinesMalaria.gif"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/4/46/Saving_Lives_with_SMS_for_Life.jpg", "file": "File:Saving Lives with SMS for Life.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/4/46/Saving_Lives_with_SMS_for_Life.jpg/300px-Saving_Lives_with_SMS_for_Life.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/c/c2/Malaria_Patient%2C_Nyangaton%2C_Ethiopia_(15151075077).jpg", "file": "File:Malaria Patient, Nyangaton, Ethiopia (15151075077).jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/c/c2/Malaria_Patient%2C_Nyangaton%2C_Ethiopia_(15151075077).jpg/300px-Malaria_Patient%2C_Nyangaton%2C_Ethiopia_(15151075077).jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/f/fc/%22Don\'t_go_to_Bed_with_Malaria_Mosquito%22_-_NARA_-_514146.tif", "file": "File:\\"Don\'t go to Bed with Malaria Mosquito\\" - NARA - 514146.tif", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/f/fc/%22Don\'t_go_to_Bed_with_Malaria_Mosquito%22_-_NARA_-_514146.tif/300px-%22Don\'t_go_to_Bed_with_Malaria_Mosquito%22_-_NARA_-_514146.tif"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/9/97/Members_of_the_Malaria_Commission_on_the_Danube_delta%2C_1929_Wellcome_L0011626.jpg", "file": "File:Members of the Malaria Commission on the Danube delta, 1929 Wellcome L0011626.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/9/97/Members_of_the_Malaria_Commission_on_the_Danube_delta%2C_1929_Wellcome_L0011626.jpg/300px-Members_of_the_Malaria_Commission_on_the_Danube_delta%2C_1929_Wellcome_L0011626.jpg"}]');
INSERT INTO `entity_data` (`id`, `entity`, `name`, `caption`, `type`, `symptoms`, `diagnosis`, `causes`, `parse_data`, `prevention`, `complications`, `medication`, `images`) VALUES
(3, 'cholera', 'Cholera', 'A%20person%20with%20severe%20dehydration%20due%20to%20cholera%20causing%20sunken%20eyes%20and%20wrinkled%20hands%20and%20skin.', 'Infectious%20disease', '["Large amounts of watery diarrhea", "vomiting", "muscle cramps"]', '{"desc": "Stool test", "list": ["Stool test"]}', '{"desc": "Vibrio cholerae spread by fecal-oral route", "list": ["Vibrio cholerae\'\'", "fecal-oral route"]}', '[{"list": ["infection", "small intestine", "strains", "bacterium", "Vibrio cholerae\'\'"], "text": "Cholera is an infection of the small intestine by some strains of the bacterium Vibrio cholerae."}, {"text": "Symptoms may range from none, to mild, to severe."}, {"list": ["diarrhea"], "text": "The classic symptom is large amounts of watery diarrhea that lasts a few days."}, {"list": ["Vomiting", "muscle cramps"], "text": "Vomiting and muscle cramps may also occur."}, {"list": ["dehydration", "electrolyte imbalance"], "text": "Diarrhea can be so severe that it leads within hours to severe dehydration and electrolyte imbalance."}, {"list": ["sunken eyes"], "text": "This may result in sunken eyes, cold skin, decreased skin elasticity, and wrinkling of the hands and feet."}, {"list": ["bluish"], "text": "Dehydration can cause the skin to turn bluish."}, {"text": "Symptoms start two hours to five days after exposure."}, {"list": ["types"], "text": "Cholera is caused by a number of types of Vibrio cholerae, with some types producing more severe disease than others."}, {"list": ["unsafe water", "unsafe food", "human feces"], "text": "It is spread mostly by unsafe water and unsafe food that has been contaminated with human feces containing the bacteria."}, {"list": ["seafood"], "text": "Undercooked seafood is a common source."}, {"text": "Humans are the only animal affected."}, {"list": ["Risk factors", "sanitation", "drinking water", "poverty"], "text": "Risk factors for the disease include poor sanitation, not enough clean drinking water, and poverty."}, {"list": ["rising sea levels"], "text": "There are concerns that rising sea levels will increase rates of disease."}, {"list": ["stool test"], "text": "Cholera can be diagnosed by a stool test."}, {"list": ["dipstick test"], "text": "A rapid dipstick test is available but is not as accurate."}, {"list": ["clean water"], "text": "Prevention methods against cholera include improved sanitation and access to clean water."}, {"list": ["Cholera vaccines"], "text": "Cholera vaccines that are given by mouth provide reasonable protection for about six months."}, {"list": ["E. coli\'\'"], "text": "They have the added benefit of protecting against another type of diarrhea caused by E. coli."}, {"list": ["oral rehydration therapy", "slightly sweet and salty solutions"], "text": "The primary treatment is oral rehydration therapy—the replacement of fluids with slightly sweet and salty solutions."}, {"text": "Rice-based solutions are preferred."}, {"list": ["Zinc supplementation"], "text": "Zinc supplementation is useful in children."}, {"list": ["intravenous fluids", "Ringer\'s lactate", "antibiotics"], "text": "In severe cases, intravenous fluids, such as Ringer\'s lactate, may be required, and antibiotics may be beneficial."}, {"list": ["Testing to see which antibiotic"], "text": "Testing to see which antibiotic the cholera is susceptible to can help guide the choice."}, {"text": "Cholera affects an estimated 3–5 million people worldwide and causes 28,800–130,000 deaths a year."}, {"list": ["pandemic", "developed world"], "text": "Although it is classified as a pandemic as of 2010, it is rare in the developed world."}, {"text": "Children are mostly affected."}, {"list": ["outbreaks", "chronically in certain areas"], "text": "Cholera occurs as both outbreaks and chronically in certain areas."}, {"list": ["Africa", "south-east Asia"], "text": "Areas with an ongoing risk of disease include Africa and south-east Asia."}, {"text": "The risk of death among those affected is usually less than 5% but may be as high as 50%."}, {"text": "No access to treatment results in a higher death rate."}, {"list": ["Sanskrit"], "text": "Descriptions of cholera are found as early as the 5th century BC in Sanskrit."}, {"list": ["John Snow", "epidemiology"], "text": "The study of cholera in England by John Snow between 1849 and 1854 led to significant advances in the field of epidemiology."}, {"list": ["diarrhea", "vomiting"], "text": "The primary symptoms of cholera are profuse diarrhea and vomiting of clear fluid."}, {"text": "These symptoms usually start suddenly, half a day to five days after ingestion of the bacteria."}, {"text": "The diarrhea is frequently described as \\"rice water\\" in nature and may have a fishy odor."}, {"text": "An untreated person with cholera may produce of diarrhea a day."}, {"text": "Severe cholera, without treatment, kills about half of affected individuals."}, {"list": ["dehydration", "electrolyte"], "text": "If the severe diarrhea is not treated, it can result in life-threatening dehydration and electrolyte imbalances."}, {"list": ["asymptomatic"], "text": "Estimates of the ratio of asymptomatic to symptomatic infections have ranged from 3 to 100."}, {"list": ["bluish-gray"], "text": "Cholera has been nicknamed the \\"blue death\\" because a person\'s skin may turn bluish-gray from extreme loss of fluids."}, {"text": "Fever is rare and should raise suspicion for secondary infection."}, {"text": "Patients can be lethargic, and might have sunken eyes, dry mouth, cold clammy skin, or wrinkled hands and feet."}, {"list": ["Kussmaul breathing", "acidosis", "stool", "bicarbonate", "lactic acidosis", "perfusion"], "text": "Kussmaul breathing, a deep and labored breathing pattern, can occur because of acidosis from stool bicarbonate losses and lactic acidosis associated with poor perfusion."}, {"list": ["Blood pressure"], "text": "Blood pressure drops due to dehydration, peripheral pulse is rapid and thready, and urine output decreases with time."}, {"list": ["seizures", "coma", "electrolyte imbalances"], "text": "Muscle cramping and weakness, altered consciousness, seizures, or even coma due to electrolyte imbalances are common, especially in children."}, {"list": ["shellfish", "plankton"], "text": "Cholera has been found in two animal populations: shellfish and plankton."}, {"list": ["Transmission", "fecal-oral route", "sanitation"], "text": "Transmission is usually through the fecal-oral route of contaminated food or water caused by poor sanitation."}, {"text": "Most cholera cases in developed countries are a result of transmission by food, while in the developing world it is more often water."}, {"list": ["oysters", "sewage", "planktonic", "crustaceans", "zooplankton"], "text": "Food transmission can occur when people harvest seafood such as oysters in waters infected with sewage, as Vibrio cholerae accumulates in planktonic crustaceans and the oysters eat the zooplankton."}, {"text": "People infected with cholera often have diarrhea, and disease transmission may occur if this highly liquid stool, colloquially referred to as \\"rice-water\\", contaminates water used by others."}, {"text": "A single diarrheal event can cause a one-million fold increase in numbers of \'\'V."}, {"text": "cholerae\'\' in the environment."}, {"list": ["groundwater", "drinking water"], "text": "The source of the contamination is typically other cholera sufferers when their untreated diarrheal discharge is allowed to get into waterways, groundwater or drinking water supplies."}, {"list": ["shellfish", "waterway"], "text": "Drinking any contaminated water and eating any foods washed in the water, as well as shellfish living in the affected waterway, can cause a person to contract an infection."}, {"list": ["spread directly from person to person"], "text": "Cholera is rarely spread directly from person to person."}, {"list": ["phytoplankton", "zooplankton", "biotic", "abiotic"], "text": "V. cholerae also exists outside the human body in natural water sources, either by itself or through interacting with phytoplankton, zooplankton, or biotic and abiotic detritus."}, {"text": "Drinking such water can also result in the disease, even without prior contamination through fecal matter."}, {"text": "Selective pressures exist however in the aquatic environment that may reduce the virulence of \'\'V."}, {"text": "cholerae\'\'."}, {"text": "Specifically, animal models indicate that the transcriptional profile of the pathogen changes as it prepares to enter an aquatic environment."}, {"text": "This transcriptional change results in a loss of ability of \'\'V."}, {"list": ["viable but non-culturable\'", "active but non-culturable\'"], "text": "cholerae\'\' to be cultured on standard media, a phenotype referred to as \'viable but non-culturable\' (VBNC) or more conservatively \'active but non-culturable\' (ABNC)."}, {"text": "One study indicates that the culturability of \'\'V."}, {"text": "cholerae\'\' drops 90% within 24 hours of entering the water, and furthermore that this loss in culturability is associated with a loss in virulence."}, {"text": "Both toxic and non-toxic strains exist."}, {"list": ["toxicity", "temperate", "bacteriophage"], "text": "Non-toxic strains can acquire toxicity through a temperate bacteriophage."}, {"text": "About 100million bacteria must typically be ingested to cause cholera in a normal healthy adult."}, {"list": ["gastric acidity", "proton pump inhibitors"], "text": "This dose, however, is less in those with lowered gastric acidity (for instance those using proton pump inhibitors)."}, {"text": "Children are also more susceptible, with two- to four-year-olds having the highest rates of infection."}, {"list": ["blood type", "type O blood"], "text": "Individuals\' susceptibility to cholera is also affected by their blood type, with those with type O blood being the most susceptible."}, {"list": ["lowered immunity", "AIDS", "malnourished"], "text": "Persons with lowered immunity, such as persons with AIDS or malnourished children, are more likely to experience a severe case if they become infected."}, {"list": ["health care provider"], "text": "Any individual, even a healthy adult in middle age, can experience a severe case, and each person\'s case should be measured by the loss of fluids, preferably in consultation with a professional health care provider."}, {"list": ["cystic fibrosis", "mutation", "delta-F508", "heterozygous advantage", "heterozygous"], "text": "The cystic fibrosis genetic mutation known as delta-F508 in humans has been said to maintain a selective heterozygous advantage: heterozygous carriers of the mutation (who are thus not affected by cystic fibrosis) are more resistant to \'\'V."}, {"text": "cholerae\'\' infections."}, {"list": ["cystic fibrosis transmembrane conductance regulator", "intestinal epithelium"], "text": "In this model, the genetic deficiency in the cystic fibrosis transmembrane conductance regulator channel proteins interferes with bacteria binding to the intestinal epithelium, thus reducing the effects of an infection."}, {"list": ["acidic", "human stomach"], "text": "When consumed, most bacteria do not survive the acidic conditions of the human stomach."}, {"list": ["nutrients", "protein"], "text": "The few surviving bacteria conserve their energy and stored nutrients during the passage through the stomach by shutting down protein production."}, {"list": ["small intestine", "mucus"], "text": "When the surviving bacteria exit the stomach and reach the small intestine, they must propel themselves through the thick mucus that lines the small intestine to reach the intestinal walls where they can attach and thrive."}, {"list": ["flagella"], "text": "Once the cholera bacteria reach the intestinal wall, they no longer need the flagella to move."}, {"list": ["flagellin"], "text": "The bacteria stop producing the protein flagellin to conserve energy and nutrients by changing the mix of proteins which they express in response to the changed chemical surroundings."}, {"text": "On reaching the intestinal wall, \'\'V."}, {"list": ["toxic proteins"], "text": "cholerae\'\' start producing the toxic proteins that give the infected person a watery diarrhea."}, {"text": "This carries the multiplying new generations of \'\'V."}, {"text": "cholerae\'\' bacteria out into the drinking water of the next host if proper sanitation measures are not in place."}, {"list": ["cholera toxin", "oligomeric", "protein subunits", "disulfide bond"], "text": "The cholera toxin (CTX or CT) is an oligomeric complex made up of six protein subunits: a single copy of the A subunit (part A), and five copies of the B subunit (part B), connected by a disulfide bond."}, {"list": ["GM1", "gangliosides"], "text": "The five B subunits form a five-membered ring that binds to GM1 gangliosides on the surface of the intestinal epithelium cells."}, {"list": ["ADP-ribosylates", "G proteins"], "text": "The A1 portion of the A subunit is an enzyme that ADP-ribosylates G proteins, while the A2 chain fits into the central pore of the B subunit ring."}, {"list": ["endocytosis"], "text": "Upon binding, the complex is taken into the cell via receptor-mediated endocytosis."}, {"list": ["ADP-ribosylation factor 6"], "text": "Once inside the cell, the disulfide bond is reduced, and the A1 subunit is freed to bind with a human partner protein called ADP-ribosylation factor 6 (Arf6)."}, {"list": ["Gs alpha subunit", "heterotrimeric G protein"], "text": "Binding exposes its active site, allowing it to permanently ribosylate the Gs alpha subunit of the heterotrimeric G protein."}, {"list": ["cAMP"], "text": "This results in constitutive cAMP production, which in turn leads to the secretion of water, sodium, potassium, and bicarbonate into the lumen of the small intestine and rapid dehydration."}, {"text": "The gene encoding the cholera toxin was introduced into \'\'V."}, {"list": ["horizontal gene transfer"], "text": "cholerae\'\' by horizontal gene transfer."}, {"text": "Virulent strains of \'\'V."}, {"list": ["temperate", "bacteriophage", "CTXφ"], "text": "cholerae\'\' carry a variant of a temperate bacteriophage called CTXφ."}, {"list": ["genetic mechanisms"], "text": "Microbiologists have studied the genetic mechanisms by which the \'\'V."}, {"text": "cholerae\'\' bacteria turn off the production of some proteins and turn on the production of other proteins as they respond to the series of chemical environments they encounter, passing through the stomach, through the mucous layer of the small intestine, and on to the intestinal wall."}, {"list": ["chloride"], "text": "Of particular interest have been the genetic mechanisms by which cholera bacteria turn on the protein production of the toxins that interact with host cell mechanisms to pump chloride ions into the small intestine, creating an ionic pressure which prevents sodium ions from entering the cell."}, {"text": "The chloride and sodium ions create a salt-water environment in the small intestines, which through osmosis can pull up to six liters of water per day through the intestinal cells, creating the massive amounts of diarrhea."}, {"text": "The host can become rapidly dehydrated unless an appropriate mixture of dilute salt water and sugar is taken to replace the blood\'s water and salts lost in the diarrhea."}, {"text": "By inserting separate, successive sections of \'\'V."}, {"list": ["E. coli\'\'"], "text": "cholerae DNA into the DNA of other bacteria, such as E. coli that would not naturally produce the protein toxins, researchers have investigated the mechanisms by which V."}, {"text": "cholerae\'\' responds to the changing chemical environments of the stomach, mucous layers, and intestinal wall."}, {"text": "Researchers have discovered a complex cascade of regulatory proteins controls expression of \'\'V."}, {"list": ["virulence"], "text": "cholerae\'\' virulence determinants."}, {"text": "In responding to the chemical environment at the intestinal wall, the \'\'V."}, {"text": "cholerae\'\' bacteria produce the TcpP/TcpH proteins, which, together with the ToxR/ToxS proteins, activate the expression of the ToxT regulatory protein."}, {"list": ["virulence"], "text": "ToxT then directly activates expression of virulence genes that produce the toxins, causing diarrhea in the infected person and allowing the bacteria to colonize the intestine."}, {"text": "Current research aims at discovering \\"the signal that makes the cholera bacteria stop swimming and start to colonize (that is, adhere to the cells of) the small intestine.\\""}, {"list": ["Amplified fragment length polymorphism", "fingerprinting", "isolates"], "text": "Amplified fragment length polymorphism fingerprinting of the pandemic isolates of \'\'V."}, {"text": "cholerae\'\' has revealed variation in the genetic structure."}, {"list": ["clusters"], "text": "Two clusters have been identified: Cluster I and Cluster II. For the most part, Cluster I consists of strains from the 1960s and 1970s, while Cluster II largely contains strains from the 1980s and 1990s, based on the change in the clone structure."}, {"text": "This grouping of strains is best seen in the strains from the African continent."}, {"list": ["antibiotic resistance"], "text": "In many areas of the world, antibiotic resistance is increasing within cholera bacteria."}, {"list": ["Bangladesh", "tetracycline", "trimethoprim-sulfamethoxazole", "erythromycin"], "text": "In Bangladesh, for example, most cases are resistant to tetracycline, trimethoprim-sulfamethoxazole, and erythromycin."}, {"list": ["assay", "multi-drug resistant"], "text": "Rapid diagnostic assay methods are available for the identification of multi-drug resistant cases."}, {"text": "New generation antimicrobials have been discovered which are effective against cholera bacteria in in vitro studies."}, {"list": ["dipstick"], "text": "A rapid dipstick test is available to determine the presence of \'\'V."}, {"text": "cholerae\'\'."}, {"text": "In those samples that test positive, further testing should be done to determine antibiotic resistance."}, {"list": ["epidemic", "patient history"], "text": "In epidemic situations, a clinical diagnosis may be made by taking a patient history and doing a brief examination."}, {"text": "Treatment is usually started without or before confirmation by laboratory analysis."}, {"text": "Stool and swab samples collected in the acute stage of the disease, before antibiotics have been administered, are the most useful specimens for laboratory diagnosis."}, {"text": "If an epidemic of cholera is suspected, the most common causative agent is \'\'V."}, {"text": "cholerae O1. If V."}, {"list": ["serogroup"], "text": "cholerae serogroup O1 is not isolated, the laboratory should test for V."}, {"text": "cholerae\'\' O139."}, {"text": "However, if neither of these organisms is isolated, it is necessary to send stool specimens to a reference laboratory."}, {"text": "Infection with \'\'V."}, {"text": "cholerae O139 should be reported and handled in the same manner as that caused by V."}, {"text": "cholerae\'\' O1. The associated diarrheal illness should be referred to as cholera and must be reported in the United States."}, {"list": ["World Health Organization"], "text": "The World Health Organization (WHO) recommends focusing on prevention, preparedness, and response to combat the spread of cholera."}, {"text": "They also stress the importance of an effective surveillance system."}, {"text": "Governments can play a role in all of these areas."}, {"list": ["sanitation"], "text": "Although cholera may be life-threatening, prevention of the disease is normally straightforward if proper sanitation practices are followed."}, {"list": ["developed countries", "water treatment"], "text": "In developed countries, due to nearly universal advanced water treatment and sanitation practices present there, cholera is rare."}, {"text": "For example, the last major outbreak of cholera in the United States occurred in 1910–1911."}, {"list": ["developing countries"], "text": "<ref name=\\"moltke2\\"> Cholera is mainly a risk in developing countries."}, {"text": "Effective sanitation practices, if instituted and adhered to in time, are usually sufficient to stop an epidemic."}, {"text": "There are several points along the cholera transmission path at which its spread may be halted:"}, {"text": "Surveillance and prompt reporting allow for containing cholera epidemics rapidly."}, {"list": ["rainy seasons"], "text": "Cholera exists as a seasonal disease in many endemic countries, occurring annually mostly during rainy seasons."}, {"text": "Surveillance systems can provide early alerts to outbreaks, therefore leading to coordinated response and assist in preparation of preparedness plans."}, {"text": "Efficient surveillance systems can also improve the risk assessment for potential cholera outbreaks."}, {"text": "Understanding the seasonality and location of outbreaks provides guidance for improving cholera control activities for the most vulnerable."}, {"text": "For prevention to be effective, it is important that cases be reported to national health authorities."}, {"text": "A number of safe and effective oral vaccines for cholera are available."}, {"text": "The World Health Organization has three prequalified oral cholera vaccines (OCVs): Dukoral, Sanchol, and Euvichol."}, {"list": ["Dukoral"], "text": "Dukoral, an orally administered, inactivated whole cell vaccine, has an overall efficacy of about 52% during the first year after being given and 62% in the second year, with minimal side effects."}, {"text": "It is available in over 60 countries."}, {"list": ["Centers for Disease Control and Prevention"], "text": "However, it is not currently recommended by the Centers for Disease Control and Prevention (CDC) for most people traveling from the United States to endemic countries."}, {"list": ["oral attenuated live vaccine"], "text": "The vaccine that the FDA recommends, Vaxchora, is an oral attenuated live vaccine, that is effective as a single dose."}, {"text": "One injectable vaccine was found to be effective for two to three years."}, {"text": "The protective efficacy was 28% lower in children less than 5 years old."}, {"text": "However, as of 2010, it has limited availability."}, {"text": "Work is under way to investigate the role of mass vaccination."}, {"list": ["HIV", "endemic"], "text": "The World Health Organization (WHO) recommends immunization of high-risk groups, such as children and people with HIV, in countries where this disease is endemic."}, {"list": ["herd immunity"], "text": "If people are immunized broadly, herd immunity results, with a decrease in the amount of contamination in the environment."}, {"list": ["sari\'\'"], "text": "An effective and relatively cheap method to prevent the transmission of cholera is the use of a folded sari (a long cloth garment) to filter drinking water."}, {"text": "In Bangladesh this practice was found to decrease rates of cholera by nearly half."}, {"text": "It involves folding a sari four to eight times."}, {"list": ["nylon"], "text": "Between uses the cloth should be rinsed in clean water and dried in the sun to kill any bacteria on it. A nylon cloth appears to work as well but is not as affordable."}, {"text": "Continued eating speeds the recovery of normal intestinal function."}, {"text": "The World Health Organization recommends this generally for cases of diarrhea no matter what the underlying cause."}, {"text": "A CDC training manual specifically for cholera states: \\"Continue to breastfeed your baby if the baby has watery diarrhea, even when traveling to get treatment. Adults and older children should continue to eat frequently.\\""}, {"text": "The most common error in caring for patients with cholera is to underestimate the speed"}, {"text": "and volume of fluids required."}, {"list": ["oral rehydration therapy"], "text": "In most cases, cholera can be successfully treated with oral rehydration therapy (ORT), which is highly effective, safe, and simple to administer."}, {"text": "Rice-based solutions are preferred to glucose-based ones due to greater efficiency."}, {"list": ["intravenous"], "text": "In severe cases with significant dehydration, intravenous rehydration may be necessary."}, {"list": ["Ringer\'s lactate"], "text": "Ringer\'s lactate is the preferred solution, often with added potassium."}, {"text": "Large volumes and continued replacement until diarrhea has subsided may be needed."}, {"text": "Ten percent of a person\'s body weight in fluid may need to be given in the first two to four hours."}, {"list": ["Bangladesh Liberation War"], "text": "This method was first tried on a mass scale during the Bangladesh Liberation War, and was found to have much success."}, {"text": "Despite widespread beliefs, fruit juices and commercial fizzy drinks like cola, are not ideal for rehydration of people with serious infections of the intestines, and their excessive sugar content may even harm water uptake."}, {"text": "If commercially produced oral rehydration solutions are too expensive or difficult to obtain, solutions can be made."}, {"text": "One such recipe calls for 1 liter of boiled water, 1/2 teaspoon of salt, 6 teaspoons of sugar, and added mashed banana for potassium and to improve taste."}, {"list": ["acidosis", "potassium"], "text": "As there frequently is initially acidosis, the potassium level may be normal, even though large losses have occurred."}, {"text": "As the dehydration is corrected, potassium levels may decrease rapidly, and thus need to be replaced."}, {"text": "This may be done by consuming foods high in potassium, like bananas or coconut water."}, {"list": ["Antibiotic"], "text": "Antibiotic treatments for one to three days shorten the course of the disease and reduce the severity of the symptoms."}, {"text": "Use of antibiotics also reduces fluid requirements."}, {"text": "People will recover without them, however, if sufficient hydration is maintained."}, {"text": "The World Health Organization only recommends antibiotics in those with severe dehydration."}, {"list": ["Doxycycline", "strains"], "text": "Doxycycline is typically used first line, although some strains of \'\'V."}, {"list": ["resistance"], "text": "cholerae\'\' have shown resistance."}, {"text": "Testing for resistance during an outbreak can help determine appropriate future choices."}, {"list": ["cotrimoxazole", "erythromycin", "tetracycline", "chloramphenicol", "furazolidone"], "text": "Other antibiotics proven to be effective include cotrimoxazole, erythromycin, tetracycline, chloramphenicol, and furazolidone."}, {"list": ["Fluoroquinolones", "ciprofloxacin"], "text": "Fluoroquinolones, such as ciprofloxacin, also may be used, but resistance has been reported."}, {"text": "Antibiotics improve outcomes in those who are both severely and not severely dehydrated."}, {"list": ["Azithromycin", "tetracycline", "doxycycline", "ciprofloxacin"], "text": "Azithromycin and tetracycline may work better than doxycycline or ciprofloxacin."}, {"list": ["zinc"], "text": "In Bangladesh zinc supplementation reduced the duration and severity of diarrhea in children with cholera when given with antibiotics and rehydration therapy as needed."}, {"text": "It reduced the length of disease by eight hours and the amount of diarrhea stool by 10%."}, {"text": "Supplementation appears to be also effective in both treating and preventing infectious diarrhea due to other causes among children in the developing world."}, {"text": "If people with cholera are treated quickly and properly, the mortality rate is less than 1%; however, with untreated cholera, the mortality rate rises to 50–60%."}, {"list": ["2010 epidemic in Haiti"], "text": "For certain genetic strains of cholera, such as the one present during the 2010 epidemic in Haiti and the 2004 outbreak in India, death can occur within two hours of becoming ill."}, {"text": "Cholera affects an estimated 3–5 million people worldwide, and causes 58,000–130,000 deaths a year as of 2010."}, {"list": ["developing world"], "text": "This occurs mainly in the developing world."}, {"text": "In the early 1980s, death rates are believed to have been greater than three million a year."}, {"text": "It is difficult to calculate exact numbers of cases, as many go unreported due to concerns that an outbreak may have a negative impact on the tourism of a country."}, {"list": ["epidemic"], "text": "Cholera remains both epidemic and endemic in many areas of the world."}, {"list": ["outbreak of cholera", "Yemen"], "text": "In October 2016, an outbreak of cholera began in war-ravaged Yemen."}, {"text": "WHO called it \\"the worst cholera outbreak in the world\\"."}, {"text": "Although much is known about the mechanisms behind the spread of cholera, this has not led to a full understanding of what makes cholera outbreaks happen in some places and not others."}, {"list": ["feces", "reservoir"], "text": "Lack of treatment of human feces and lack of treatment of drinking water greatly facilitate its spread, but bodies of water can serve as a reservoir, and seafood shipped long distances can spread the disease."}, {"list": ["Americas"], "text": "Cholera was not known in the Americas for most of the 20th century, but it reappeared towards the end of that century."}, {"text": "The word cholera is from kholera from χολή kholē \\"bile\\"."}, {"list": ["Indian subcontinent"], "text": "Cholera likely has its origins in the Indian subcontinent as evidenced by its prevalence in the region for centuries."}, {"list": ["still water"], "text": "Early outbreaks in the Indian subcontinent are believed to have been the result of poor living conditions as well as the presence of pools of still water, both of which provide ideal conditions for cholera to thrive."}, {"list": ["trade routes", "Russia", "Europe", "North America"], "text": "The disease first spread by trade routes (land and sea) to Russia in 1817, later to the rest of Europe, and from Europe to North America and the rest of the world."}, {"list": ["cholera pandemics", "pandemic", "Indonesia"], "text": "Seven cholera pandemics have occurred in the past 200 years, with the seventh pandemic originating in Indonesia in 1961."}, {"list": ["first cholera pandemic"], "text": "The first cholera pandemic occurred in the Bengal region of India, near Calcutta starting in 1817 through 1824."}, {"text": "The disease dispersed from India to Southeast Asia, the Middle East, Europe, and Eastern Africa through trade routes."}, {"list": ["second pandemic"], "text": "The second pandemic lasted from 1827 to 1835 and particularly affected North America and Europe due to the result of advancements in transportation and global trade, and increased human migration, including soldiers."}, {"list": ["third pandemic"], "text": "The third pandemic erupted in 1839, persisted until 1856, extended to North Africa, and reached South America, for the first time specifically affecting Brazil."}, {"list": ["fourth pandemic"], "text": "The fourth pandemic lasted from 1863 to 1875 spread from India to Naples and Spain."}, {"list": ["fifth"], "text": "The fifth pandemic was from 1881–1896 and started in India and spread to Europe, Asia, and South America."}, {"list": ["sixth pandemic"], "text": "The sixth pandemic started 1899–1923."}, {"text": "These epidemics were less fatal due to a greater understanding of the cholera bacteria."}, {"text": "Egypt, the Arabian peninsula, Persia, India, and the Philippines were hit hardest during these epidemics, while other areas, like Germany in 1892 and Naples from 1910–1911, also experienced severe outbreaks."}, {"list": ["pandemic"], "text": "The final pandemic originated in 1961 in Indonesia and is marked by the emergence of a new strain, nicknamed El Tor, which still persists today in developing countries."}, {"text": "Since it became widespread in the 19th century, cholera has killed tens of millions of people."}, {"list": ["Russia"], "text": "In Russia alone, between 1847 and 1851, more than one million people perished of the disease."}, {"text": "It killed 150,000 Americans during the second pandemic."}, {"text": "Between 1900 and 1920, perhaps eight million people died of cholera in India."}, {"list": ["reportable disease"], "text": "Cholera became the first reportable disease in the United States due to the significant effects it had on health."}, {"list": ["John Snow", "England"], "text": "John Snow, in England, was the first to identify the importance of contaminated water as its cause in 1854."}, {"list": ["filtering", "chlorination", "developing countries"], "text": "Cholera is now no longer considered a pressing health threat in Europe and North America due to filtering and chlorination of water supplies, but still heavily affects populations in developing countries."}, {"list": ["quarantine"], "text": "In the past, vessels flew a yellow quarantine flag if any crew members or passengers were suffering from cholera."}, {"text": "No one aboard a vessel flying a yellow flag would be allowed ashore for an extended period, typically 30 to 40 days."}, {"list": ["international maritime signal flags"], "text": "In modern sets of international maritime signal flags, the quarantine flag is yellow and black."}, {"text": "Historically many different claimed remedies have existed in folklore."}, {"list": ["miasma theory"], "text": "Many of the older remedies were based on the miasma theory."}, {"list": ["cholera belts"], "text": "Some believed that abdominal chilling made one more susceptible and flannel and cholera belts were routine in army kits."}, {"list": ["camphor", "Hahnemann"], "text": "In the 1854–1855 outbreak in Naples homeopathic camphor was used according to Hahnemann."}, {"text": "T. J. Ritter\'s \\"Mother\'s Remedies\\" book lists tomato syrup as a home remedy from northern America."}, {"list": ["Elecampane"], "text": "Elecampane was recommended in the United Kingdom according to William Thomas Fernie."}, {"text": "Cholera cases are much less frequent in developed countries where governments have helped to establish water sanitation practices and effective medical treatments."}, {"text": "The United States, for example, used to have a severe cholera problem similar to those in some developing countries."}, {"text": "There were three large cholera outbreaks in the 1800s, which can be attributed to Vibrio cholerae\'s spread through interior waterways like the Erie Canal and routes along the Eastern Seaboard."}, {"text": "The island of Manhattan in New York City touched the Atlantic Ocean, where cholera collected just off the coast."}, {"text": "At this time, New York City did not have as effective a sanitation system as it does today, so cholera was able to spread."}, {"list": ["gastroenteritis"], "text": "Cholera morbus is a historical term that was used to refer to gastroenteritis rather than specifically cholera."}, {"list": ["Filippo Pacini"], "text": "The bacterium was isolated in 1854 by Italian anatomist Filippo Pacini, but its exact nature and his results were not widely known."}, {"list": ["Jaume Ferran i Clua"], "text": "Spanish physician Jaume Ferran i Clua developed a cholera inoculation in 1885, the first to immunize humans against a bacterial disease."}, {"list": ["Waldemar Haffkine"], "text": "Russian-Jewish bacteriologist Waldemar Haffkine developed the first cholera vaccine in July 1892."}, {"list": ["John Snow"], "text": "One of the major contributions to fighting cholera was made by the physician and pioneer medical scientist John Snow (1813–1858), who in 1854 found a link between cholera and contaminated drinking water."}, {"text": "Dr. Snow proposed a microbial origin for epidemic cholera in 1849."}, {"text": "In his major \\"state of the art\\" review of 1855, he proposed a substantially complete and correct model for the cause of the disease."}, {"list": ["sewage"], "text": "In two pioneering epidemiological field studies, he was able to demonstrate human sewage contamination was the most probable disease vector in two major epidemics in London in 1854."}, {"text": "His model was not immediately accepted, but it was seen to be the more plausible, as medical microbiology developed over the next 30 years or so.Cities in developed nations made massive investment in clean water supply and well-separated sewage treatment infrastructures between the mid-1850s and the 1900s."}, {"text": "This eliminated the threat of cholera epidemics from the major developed cities in the world."}, {"list": ["Robert Koch"], "text": "In 1883, Robert Koch identified \'\'V."}, {"text": "cholerae\'\' with a microscope as the bacillus causing the disease."}, {"list": ["Robert Allan Phillips", "Naval Medical Research Unit Two"], "text": "Robert Allan Phillips, working at the US Naval Medical Research Unit Two in Southeast Asia, evaluated the pathophysiology of the disease using modern laboratory chemistry techniques and developed a protocol for rehydration."}, {"list": ["Lasker Foundation"], "text": "His research led the Lasker Foundation to award him its prize in 1967."}, {"list": ["Dhaka, Bangladesh"], "text": "More recently, in 2002, Alam, et al., studied stool samples from patients at the International Centre for Diarrhoeal Disease in Dhaka, Bangladesh."}, {"text": "From the various experiments they conducted, the researchers found a correlation between the passage of \'\'V."}, {"text": "cholerae\'\' through the human digestive system and an increased infectivity state."}, {"list": ["genes", "amino acids", "iron"], "text": "Furthermore, the researchers found the bacterium creates a hyperinfected state where genes that control biosynthesis of amino acids, iron uptake systems, and formation of periplasmic nitrate reductase complexes were induced just before defecation."}, {"text": "These induced characteristics allow the cholera vibrios to survive in the \\"rice water\\" stools, an environment of limited oxygen and iron, of patients with a cholera infection."}, {"text": "In many developing countries, cholera still reaches its victims through contaminated water sources, and countries without proper sanitation techniques have greater incidence of the disease."}, {"text": "Governments can play a role in this."}, {"list": ["Zimbabwean cholera outbreak", "James Baker Institute"], "text": "In 2008, for example, the Zimbabwean cholera outbreak was due partly to the government\'s role, according to a report from the James Baker Institute."}, {"text": "The Haitian government\'s inability to provide safe drinking water after the 2010 earthquake led to an increase in cholera cases as well."}, {"text": "Similarly, South Africa\'s cholera outbreak was exacerbated by the government\'s policy of privatizing water programs."}, {"text": "The wealthy elite of the country were able to afford safe water while others had to use water from cholera-infected rivers."}, {"list": ["Rita R. Colwell", "James Baker Institute"], "text": "According to Rita R. Colwell of the James Baker Institute, if cholera does begin to spread, government preparedness is crucial."}, {"text": "A government\'s ability to contain the disease before it extends to other areas can prevent a high death toll and the development of an epidemic or even pandemic."}, {"text": "Effective disease surveillance can ensure that cholera outbreaks are recognized as soon as possible and dealt with appropriately."}, {"text": "Oftentimes, this will allow public health programs to determine and control the cause of the cases, whether it is unsanitary water or seafood that have accumulated a lot of Vibrio cholerae specimens."}, {"text": "Having an effective surveillance program contributes to a government\'s ability to prevent cholera from spreading."}, {"text": "In the year 2000 in the state of Kerala in India, the Kottayam district was determined to be \\"Cholera-affected\\"; this pronouncement led to task forces that concentrated on educating citizens with 13,670 information sessions about human health."}, {"text": "These task forces promoted the boiling of water to obtain safe water, and provided chlorine and oral rehydration salts."}, {"text": "Ultimately, this helped to control the spread of the disease to other areas and minimize deaths."}, {"text": "On the other hand, researchers have shown that most of the citizens infected during the 1991 cholera outbreak in Bangladesh lived in rural areas, and were not recognized by the government\'s surveillance program."}, {"text": "This inhibited physicians\' abilities to detect cholera cases early."}, {"list": ["Zimbabwean cholera outbreak"], "text": "According to Colwell, the quality and inclusiveness of a country\'s health care system affects the control of cholera, as it did in the Zimbabwean cholera outbreak."}, {"text": "While sanitation practices are important, when governments respond quickly and have readily available vaccines, the country will have a lower cholera death toll."}, {"text": "Affordability of vaccines can be a problem; if the governments do not provide vaccinations, only the wealthy may be able to afford them and there will be a greater toll on the country\'s poor."}, {"text": "The speed with which government leaders respond to cholera outbreaks is important."}, {"text": "Besides contributing to an effective or declining public health care system and water sanitation treatments, government can have indirect effects on cholera control and the effectiveness of a response to cholera."}, {"text": "A country\'s government can impact its ability to prevent disease and control its spread."}, {"text": "A speedy government response backed by a fully functioning health care system and financial resources can prevent cholera\'s spread."}, {"text": "This limits cholera\'s ability to cause death, or at the very least a decline in education, as children are kept out of school to minimize the risk of infection."}]', '{"list": ["Improved sanitation", " clean water", " cholera vaccines"], "conditions": ["clean water", "cholera vaccines"]}', '{"list": ["Dehydration", " electrolyte imbalance"], "conditions": ["Dehydration", "electrolyte imbalance"]}', '{}', '[{"url": "https://upload.wikimedia.org/wikipedia/commons/2/2f/PHIL_1939_lores.jpg", "file": "File:PHIL 1939 lores.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/2/2f/PHIL_1939_lores.jpg/300px-PHIL_1939_lores.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/e/ee/Cholera_feces.jpg", "file": "Image:Cholera feces.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/e/ee/Cholera_feces.jpg/300px-Cholera_feces.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/9/9d/Cholera_bacteria_SEM.jpg", "file": "File:Cholera bacteria SEM.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/9/9d/Cholera_bacteria_SEM.jpg/300px-Cholera_bacteria_SEM.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/f/f6/Vibrio_cholerae.jpg", "file": "Image:Vibrio cholerae.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f6/Vibrio_cholerae.jpg/300px-Vibrio_cholerae.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/9/9a/Cholera_role_of_biofilm_in_intestinal_colonization.jpg", "file": "File:Cholera role of biofilm in intestinal colonization.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/9/9a/Cholera_role_of_biofilm_in_intestinal_colonization.jpg/300px-Cholera_role_of_biofilm_in_intestinal_colonization.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/f/f8/Cholera_hospital_in_Dhaka.jpg", "file": "Image:Cholera hospital in Dhaka.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f8/Cholera_hospital_in_Dhaka.jpg/300px-Cholera_hospital_in_Dhaka.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/6/61/Unsafe_disposal_of_faecal_sludge_or_sewage_in_Haiti_(6458176073).jpg", "file": "File:Unsafe disposal of faecal sludge or sewage in Haiti (6458176073).jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/6/61/Unsafe_disposal_of_faecal_sludge_or_sewage_in_Haiti_(6458176073).jpg/300px-Unsafe_disposal_of_faecal_sludge_or_sewage_in_Haiti_(6458176073).jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/d/dc/A_SOIL_EkoLakay_toilet_customer._(15921409131).jpg", "file": "File:A SOIL EkoLakay toilet customer. (15921409131).jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/d/dc/A_SOIL_EkoLakay_toilet_customer._(15921409131).jpg/300px-A_SOIL_EkoLakay_toilet_customer._(15921409131).jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/6/61/It_doesn\'t_hurt%2C_but_it_tickles._A_U.S._Navy_hospital_corpsman%2C_member_of_a_USAID_military_health_team%2C_inoculates_a_flo_-_NARA_-_541855.jpg", "file": "File:It doesn\'t hurt, but it tickles. A U.S. Navy hospital corpsman, member of a USAID military health team, inoculates a flo - NARA - 541855.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/6/61/It_doesn\'t_hurt%2C_but_it_tickles._A_U.S._Navy_hospital_corpsman%2C_member_of_a_USAID_military_health_team%2C_inoculates_a_flo_-_NARA_-_541855.jpg/300px-It_doesn\'t_hurt%2C_but_it_tickles._A_U.S._Navy_hospital_corpsman%2C_member_of_a_USAID_military_health_team%2C_inoculates_a_flo_-_NARA_-_541855.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/a/ad/Cholera_rehydration_nurses.jpg", "file": "Image:Cholera rehydration nurses.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/a/ad/Cholera_rehydration_nurses.jpg/300px-Cholera_rehydration_nurses.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/c/c4/Africa_cholera2008b.jpg", "file": "File:Africa cholera2008b.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/c/c4/Africa_cholera2008b.jpg/300px-Africa_cholera2008b.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/5/5d/Robert_Koch_(Deutsche_Cholera-Expedition_in_%C3%84gypten_1884).jpg", "file": "File:Robert Koch (Deutsche Cholera-Expedition in Ägypten 1884).jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/5/5d/Robert_Koch_(Deutsche_Cholera-Expedition_in_%C3%84gypten_1884).jpg/300px-Robert_Koch_(Deutsche_Cholera-Expedition_in_%C3%84gypten_1884).jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/b/b1/How_to_avoid_the_Cholera_1848.jpg", "file": "File:How to avoid the Cholera 1848.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/b/b1/How_to_avoid_the_Cholera_1848.jpg/300px-How_to_avoid_the_Cholera_1848.jpg"}]');
INSERT INTO `entity_data` (`id`, `entity`, `name`, `caption`, `type`, `symptoms`, `diagnosis`, `causes`, `parse_data`, `prevention`, `complications`, `medication`, `images`) VALUES
(4, 'flu', 'Influenza', 'Influenza%20virus%2C%20magnified%20approximately%20100%2C000%20times', 'Infectious%20disease', '["Fever", "runny nose", "sore throat", "muscle pains", "headache", "coughing", "feeling tired"]', '{}', '{"desc": "Influenza viruses", "list": ["Influenza viruses"]}', '[{"list": ["infectious disease", "influenza virus"], "text": "Influenza, commonly known as \\"the flu\\", is an infectious disease caused by an influenza virus."}, {"text": "Symptoms can be mild to severe."}, {"list": ["symptoms", "fever", "runny nose", "sore throat", "muscle pains", "headache", "coughing", "feeling tired"], "text": "The most common symptoms include: a high fever, runny nose, sore throat, muscle pains, headache, coughing, and feeling tired."}, {"text": "These symptoms typically begin two days after exposure to the virus and most last less than a week."}, {"text": "The cough, however, may last for more than two weeks."}, {"list": ["nausea", "vomiting"], "text": "In children, there may be nausea and vomiting, but these are not common in adults."}, {"list": ["gastroenteritis"], "text": "Nausea and vomiting occur more commonly in the unrelated infection gastroenteritis, which is sometimes inaccurately referred to as \\"stomach flu\\" or the \\"24-hour flu\\"."}, {"list": ["viral pneumonia", "bacterial pneumonia", "sinus infections", "asthma", "heart failure"], "text": "Complications of influenza may include viral pneumonia, secondary bacterial pneumonia, sinus infections, and worsening of previous health problems such as asthma or heart failure."}, {"list": ["spread through the air"], "text": "Three types of influenza viruses affect people, called Type A, Type B, and Type C. Usually, the virus is spread through the air from coughs or sneezes."}, {"text": "This is believed to occur mostly over relatively short distances."}, {"text": "It can also be spread by touching surfaces contaminated by the virus and then touching the mouth or eyes."}, {"text": "A person may be infectious to others both before and during the time they are showing symptoms."}, {"list": ["sputum"], "text": "The infection may be confirmed by testing the throat, sputum, or nose for the virus."}, {"list": ["rapid tests"], "text": "A number of rapid tests are available; however, people may still have the infection if the results are negative."}, {"list": ["polymerase chain reaction", "RNA"], "text": "A type of polymerase chain reaction that detects the virus\'s RNA is more accurate."}, {"text": "Frequent hand washing reduces the risk of viral spread."}, {"list": ["surgical mask"], "text": "Wearing a surgical mask is also useful."}, {"list": ["vaccinations against influenza", "World Health Organization"], "text": "Yearly vaccinations against influenza are recommended by the World Health Organization for those at high risk."}, {"text": "The vaccine is usually effective against three or four types of influenza."}, {"text": "It is usually well tolerated."}, {"text": "A vaccine made for one year may not be useful in the following year, since the virus evolves rapidly."}, {"list": ["Antiviral drugs", "neuraminidase inhibitor", "oseltamivir"], "text": "Antiviral drugs such as the neuraminidase inhibitor oseltamivir, among others, have been used to treat influenza."}, {"text": "Their benefits in those who are otherwise healthy do not appear to be greater than their risks."}, {"text": "No benefit has been found in those with other health problems."}, {"list": ["yearly outbreak"], "text": "Influenza spreads around the world in a yearly outbreak, resulting in about three to five million cases of severe illness and about 250,000 to 500,000 deaths."}, {"text": "In the Northern and Southern parts of the world, outbreaks occur mainly in winter while in areas around the equator outbreaks may occur at any time of the year."}, {"text": "Death occurs mostly in the young, the old and those with other health problems."}, {"list": ["pandemics"], "text": "Larger outbreaks known as pandemics are less frequent."}, {"list": ["influenza pandemics", "Spanish influenza", "Asian influenza", "Hong Kong influenza"], "text": "In the 20th century, three influenza pandemics occurred: Spanish influenza in 1918 (~50 million deaths), Asian influenza in 1957 (two million deaths), and Hong Kong influenza in 1968 (one million deaths)."}, {"list": ["World Health Organization", "influenza A/H1N1", "pandemic in June 2009"], "text": "The World Health Organization declared an outbreak of a new type of influenza A/H1N1 to be a pandemic in June 2009."}, {"text": "Influenza may also affect other animals, including pigs, horses, and birds."}, {"text": "Approximately 33% of people with influenza are asymptomatic."}, {"text": "Symptoms of influenza can start quite suddenly one to two days after infection."}, {"text": "Usually the first symptoms are chills or a chilly sensation, but fever is also common early in the infection, with body temperatures ranging from 38 to 39 °C (approximately 100 to 103 °F)."}, {"text": "Many people are so ill that they are confined to bed for several days, with aches and pains throughout their bodies, which are worse in their backs and legs."}, {"text": "Symptoms of influenza may include:"}, {"list": ["antiviral drugs"], "text": "Since antiviral drugs are effective in treating influenza if given early (see treatment section, below), it can be important to identify cases early."}, {"text": "Of the symptoms listed above, the combinations of fever with cough, sore throat and/or nasal congestion can improve diagnostic accuracy."}, {"list": ["decision analysis", "prevalence", "neuraminidase inhibitors"], "text": "Two decision analysis studies suggest that during local outbreaks of influenza, the prevalence will be over 70%, and thus patients with any of these combinations of symptoms may be treated with neuraminidase inhibitors without testing."}, {"list": ["influenza season"], "text": "Even in the absence of a local outbreak, treatment may be justified in the elderly during the influenza season as long as the prevalence is over 15%."}, {"text": "The available laboratory tests for influenza continue to improve."}, {"list": ["Centers for Disease Control and Prevention"], "text": "The United States Centers for Disease Control and Prevention (CDC) maintains an up-to-date summary of available laboratory tests."}, {"list": ["viral culture"], "text": "According to the CDC, rapid diagnostic tests have a sensitivity of 50–75% and specificity of 90–95% when compared with viral culture."}, {"text": "These tests may be especially useful during the influenza season (prevalence=25%) but in the absence of a local outbreak, or peri-influenza season (prevalence=10% )."}, {"text": "Occasionally, influenza can cause severe illness including primary viral pneumonia or secondary bacterial pneumonia."}, {"text": "The obvious symptom is trouble breathing."}, {"text": "In addition, if a child (or presumably an adult) seems to be getting better and then relapses with a high fever, that is a danger sign since this relapse can be bacterial pneumonia."}, {"list": ["virus classification", "RNA viruses", "genera", "Orthomyxoviridae"], "text": "In virus classification influenza viruses are RNA viruses that make up three of the five genera of the family Orthomyxoviridae:"}, {"text": "A fourth family of influenza viruses has been proposed – influenza D.      The type species for this family is Bovine Influenza D virus which was first isolated in 2012."}, {"text": "This genus has one species, influenza A virus."}, {"list": ["pandemics"], "text": "Wild aquatic birds are the natural hosts for a large variety of influenza A. Occasionally, viruses are transmitted to other species and may then cause devastating outbreaks in domestic poultry or give rise to human influenza pandemics."}, {"text": "The type A viruses are the most virulent human pathogens among the three influenza types and cause the severest disease."}, {"list": ["serotypes", "antibody"], "text": "The influenza A virus can be subdivided into different serotypes based on the antibody response to these viruses."}, {"text": "The serotypes that have been confirmed in humans, ordered by the number of known human pandemic deaths, are:"}, {"text": "This genus has one species, influenza B virus."}, {"list": ["seal", "ferret"], "text": "Influenza B almost exclusively infects humans and is less common than influenza A. The only other animals known to be susceptible to influenza B infection are the seal and the ferret."}, {"text": "This type of influenza mutates at a rate 2–3 times slower than type A and consequently is less genetically diverse, with only one influenza B serotype."}, {"list": ["antigenic", "immunity"], "text": "As a result of this lack of antigenic diversity, a degree of immunity to influenza B is usually acquired at an early age."}, {"text": "However, influenza B mutates enough that lasting immunity is not possible."}, {"list": ["antigenic shift"], "text": "This reduced rate of antigenic change, combined with its limited host range (inhibiting cross species antigenic shift), ensures that pandemics of influenza B do not occur."}, {"text": "This genus has one species, influenza C virus, which infects humans, dogs and pigs, sometimes causing both severe illness and local epidemics."}, {"text": "However, influenza C is less common than the other types and usually only causes mild disease in children."}, {"text": "Influenzaviruses A, B and C are very similar in overall structure."}, {"list": ["nanometers"], "text": "The virus particle is 80–120 nanometers in diameter and usually roughly spherical, although filamentous forms can occur."}, {"list": ["micrometers"], "text": "These filamentous forms are more common in influenza C, which can form cordlike structures up to 500 micrometers long on the surfaces of infected cells."}, {"text": "However, despite these varied shapes, the viral particles of all influenza viruses are similar in composition."}, {"list": ["viral envelope", "glycoproteins"], "text": "These are made of a viral envelope containing two main types of glycoproteins, wrapped around a central core."}, {"list": ["RNA", "genome"], "text": "The central core contains the viral RNA genome and other viral proteins that package and protect this RNA."}, {"text": "RNA tends to be single stranded but in special cases it is double."}, {"list": ["nucleic acid", "negative-sense", "genes"], "text": "Unusually for a virus, its genome is not a single piece of nucleic acid; instead, it contains seven or eight pieces of segmented negative-sense RNA, each piece of RNA containing either one or two genes, which code for a gene product (protein)."}, {"list": ["proteins", "hemagglutinin", "neuraminidase", "nucleoprotein", "M1", "M2", "NS1"], "text": "For example, the influenza A genome contains 11 genes on eight pieces of RNA, encoding for 11 proteins: hemagglutinin (HA), neuraminidase (NA), nucleoprotein (NP), M1, M2, NS1, NS2 (NEP: nuclear export protein), PA, PB1 (polymerase basic 1), PB1-F2 and PB2."}, {"text": "Hemagglutinin (HA) and neuraminidase (NA) are the two large glycoproteins on the outside of the viral particles."}, {"list": ["lectin"], "text": "HA is a lectin that mediates binding of the virus to target cells and entry of the viral genome into the target cell, while NA is involved in the release of progeny virus from infected cells, by cleaving sugars that bind the mature viral particles."}, {"list": ["antiviral drugs"], "text": "Thus, these proteins are targets for antiviral drugs."}, {"list": ["antigens", "antibodies"], "text": "Furthermore, they are antigens to which antibodies can be raised."}, {"text": "Influenza A viruses are classified into subtypes based on antibody responses to HA and NA. These different types of HA and NA form the basis of the H and N distinctions in, for example, H5N1."}, {"text": "There are 16 H and 9 N subtypes known, but only H 1, 2 and 3, and N 1 and 2 are commonly found in humans."}, {"text": "Viruses can replicate only in living cells."}, {"text": "Influenza infection and replication is a multi-step process: First, the virus has to bind to and enter the cell, then deliver its genome to a site where it can produce new copies of viral proteins and RNA, assemble these components into new viral particles, and, last, exit the host cell."}, {"list": ["hemagglutinin", "sialic acid", "epithelial cells", "lungs", "intestines"], "text": "Influenza viruses bind through hemagglutinin onto sialic acid sugars on the surfaces of epithelial cells, typically in the nose, throat, and lungs of mammals, and intestines of birds (Stage 1 in infection figure)."}, {"list": ["cleaved", "protease", "endocytosis"], "text": "After the hemagglutinin is cleaved by a protease, the cell imports the virus by endocytosis."}, {"text": "The intracellular details are still being elucidated."}, {"list": ["microtubule"], "text": "It is known that virions converge to the microtubule organizing center, interact with acidic endosomes and finally enter the target endosomes for genome release."}, {"list": ["endosome", "viral envelope", "ion channel", "protons"], "text": "Once inside the cell, the acidic conditions in the endosome cause two events to happen: First, part of the hemagglutinin protein fuses the viral envelope with the vacuole\'s membrane, then the M2 ion channel allows protons to move through the viral envelope and acidify the core of the virus, which causes the core to disassemble and release the viral RNA and core proteins."}, {"list": ["RNA-dependent RNA polymerase", "cytoplasm", "amantadine"], "text": "The viral RNA (vRNA) molecules, accessory proteins and RNA-dependent RNA polymerase are then released into the cytoplasm (Stage 2). The M2 ion channel is blocked by amantadine drugs, preventing infection."}, {"list": ["cell nucleus"], "text": "These core proteins and vRNA form a complex that is transported into the cell nucleus, where the RNA-dependent RNA polymerase begins transcribing complementary positive-sense vRNA (Steps 3a and b). The vRNA either is exported into the cytoplasm and translated (step 4) or remains in the nucleus."}, {"list": ["Golgi apparatus"], "text": "Newly synthesized viral proteins are either secreted through the Golgi apparatus onto the cell surface (in the case of neuraminidase and hemagglutinin, step 5b) or transported back into the nucleus to bind vRNA and form new viral genome particles (step 5a)."}, {"list": ["mRNA", "nucleotides", "translation"], "text": "Other viral proteins have multiple actions in the host cell, including degrading cellular mRNA and using the released nucleotides for vRNA synthesis and also inhibiting translation of host-cell mRNAs."}, {"list": ["genomes"], "text": "Negative-sense vRNAs that form the genomes of future viruses, RNA-dependent RNA polymerase, and other viral proteins are assembled into a virion."}, {"text": "Hemagglutinin and neuraminidase molecules cluster into a bulge in the cell membrane."}, {"list": ["viral core", "phospholipid membrane", "neuraminidase"], "text": "The vRNA and viral core proteins leave the nucleus and enter this membrane protrusion (step 6). The mature virus buds off from the cell in a sphere of host phospholipid membrane, acquiring hemagglutinin and neuraminidase with this membrane coat (step 7). As before, the viruses adhere to the cell through hemagglutinin; the mature viruses detach once their neuraminidase has cleaved sialic acid residues from the host cell."}, {"text": "After the release of new influenza viruses, the host cell dies."}, {"list": ["proofreading"], "text": "Because of the absence of RNA proofreading enzymes, the RNA-dependent RNA polymerase that copies the viral genome makes an error roughly every 10 thousand nucleotides, which is the approximate length of the influenza vRNA."}, {"list": ["antigenic drift"], "text": "Hence, the majority of newly manufactured influenza viruses are mutants; this causes antigenic drift, which is a slow change in the antigens on the viral surface over time."}, {"list": ["reassortment"], "text": "The separation of the genome into eight separate segments of vRNA allows mixing or reassortment of vRNAs if more than one type of influenza virus infects a single cell."}, {"list": ["antigenic shifts"], "text": "The resulting rapid change in viral genetics produces antigenic shifts, which are sudden changes from one antigen to another."}, {"text": "These sudden large changes allow the virus to infect new host species and quickly overcome protective immunity."}, {"text": "This is important in the emergence of pandemics, as discussed below in the section on Epidemiology."}, {"text": "When an infected person sneezes or coughs more than half a million virus particles can be spread to those close by. In otherwise healthy adults, influenza virus shedding (the time during which a person might be infectious to another person) increases sharply one-half to one day after infection, peaks on day 2 and persists for an average total duration of 5 days—but can persist as long as 9 days."}, {"text": "In those who develop symptoms from experimental infection (only 67% of healthy experimentally infected individuals), symptoms and viral shedding show a similar pattern, but with viral shedding preceding illness by one day."}, {"text": "Children are much more infectious than adults and shed virus from just before they develop symptoms until two weeks after infection."}, {"text": "In immunocompromised people, viral shedding can continue for longer than two weeks."}, {"list": ["aerosols"], "text": "Influenza can be spread in three main ways: by direct transmission (when an infected person sneezes mucus directly into the eyes, nose or mouth of another person); the airborne route (when someone inhales the aerosols produced by an infected person coughing, sneezing or spitting) and through hand-to-eye, hand-to-nose, or hand-to-mouth transmission, either from contaminated surfaces or from direct personal contact such as a handshake."}, {"text": "The relative importance of these three modes of transmission is unclear, and they may all contribute to the spread of the virus."}, {"list": ["µm"], "text": "In the airborne route, the droplets that are small enough for people to inhale are 0.5 to 5 µm in diameter and inhaling just one droplet might be enough to cause an infection."}, {"text": "Although a single sneeze releases up to 40,000 droplets, most of these droplets are quite large and will quickly settle out of the air."}, {"list": ["humidity", "UV radiation"], "text": "How long influenza survives in airborne droplets seems to be influenced by the levels of humidity and UV radiation, with low humidity and a lack of sunlight in winter aiding its survival."}, {"text": "As the influenza virus can persist outside of the body, it can also be transmitted by contaminated surfaces such as banknotes, doorknobs, light switches and other household items."}, {"text": "The length of time the virus will persist on a surface varies, with the virus surviving for one to two days on hard, non-porous surfaces such as plastic or metal, for about fifteen minutes from dry paper tissues, and only five minutes on skin."}, {"text": "However, if the virus is present in mucus, this can protect it for longer periods (up to 17 days on banknotes)."}, {"text": "Avian influenza viruses can survive indefinitely when frozen."}, {"text": "They are inactivated by heating to 56 °C (133 °F) for a minimum of 60 minutes, as well as by acids (at pH <2)."}, {"text": "The mechanisms by which influenza infection causes symptoms in humans have been studied intensively."}, {"list": ["adrenocorticotropic hormone", "cortisol"], "text": "One of the mechanisms is believed to be the inhibition of adrenocorticotropic hormone (ACTH) resulting in lowered cortisol levels."}, {"list": ["pathophysiology"], "text": "Knowing which genes are carried by a particular strain can help predict how well it will infect humans and how severe this infection will be (that is, predict the strain\'s pathophysiology)."}, {"list": ["cleavage", "hemagglutinin", "proteases"], "text": "For instance, part of the process that allows influenza viruses to invade cells is the cleavage of the viral hemagglutinin protein by any one of several human proteases."}, {"text": "In mild and avirulent viruses, the structure of the hemagglutinin means that it can only be cleaved by proteases found in the throat and lungs, so these viruses cannot infect other tissues."}, {"text": "However, in highly virulent strains, such as H5N1, the hemagglutinin can be cleaved by a wide variety of proteases, allowing the virus to spread throughout the body."}, {"list": ["respiratory tract"], "text": "The viral hemagglutinin protein is responsible for determining both which species a strain can infect and where in the human respiratory tract a strain of influenza will bind."}, {"text": "Strains that are easily transmitted between people have hemagglutinin proteins that bind to receptors in the upper part of the respiratory tract, such as in the nose, throat and mouth."}, {"text": "In contrast, the highly lethal H5N1 strain binds to receptors that are mostly found deep in the lungs."}, {"text": "This difference in the site of infection may be part of the reason why the H5N1 strain causes severe viral pneumonia in the lungs, but is not easily transmitted by people coughing and sneezing."}, {"list": ["cytokines", "chemokines", "interferon", "tumor necrosis factor"], "text": "Common symptoms of the flu such as fever, headaches, and fatigue are the result of the huge amounts of proinflammatory cytokines and chemokines (such as interferon or tumor necrosis factor) produced from influenza-infected cells."}, {"list": ["rhinovirus", "common cold", "inflammatory response"], "text": "In contrast to the rhinovirus that causes the common cold, influenza does cause tissue damage, so symptoms are not entirely due to the inflammatory response."}, {"list": ["cytokine storm"], "text": "This massive immune response might produce a life-threatening cytokine storm."}, {"text": "This effect has been proposed to be the cause of the unusual lethality of both the H5N1 avian influenza, and the 1918 pandemic strain."}, {"text": "However, another possibility is that these large amounts of cytokines are just a result of the massive levels of viral replication produced by these strains, and the immune response does not itself contribute to the disease."}, {"list": ["influenza vaccine", "World Health Organization", "Centers for Disease Control and Prevention", "asthma", "diabetes", "heart disease"], "text": "The influenza vaccine is recommended by the World Health Organization and United States Centers for Disease Control and Prevention for high-risk groups, such as children, the elderly, health care workers, and people who have chronic illnesses such as asthma, diabetes, heart disease, or are immuno-compromised among others."}, {"text": "In healthy adults it is modestly effective in decreasing the amount of influenza-like symptoms in a population."}, {"text": "Evidence is supportive of a decreased rate of influenza in children over the age of two."}, {"list": ["chronic obstructive pulmonary disease"], "text": "In those with chronic obstructive pulmonary disease vaccination reduces exacerbations, it is not clear if it reduces asthma exacerbations."}, {"list": ["HIV/AIDS", "cancer"], "text": "Evidence supports a lower rate of influenza-like illness in many groups who are immunocompromised such as those with: HIV/AIDS, cancer, and post organ transplant."}, {"list": ["heart disease"], "text": "In those at high risk immunization may reduce the risk of heart disease."}, {"text": "Whether immunizing health care workers affects patient outcomes is controversial with some reviews finding insufficient evidence and others finding tentative evidence."}, {"list": ["mutation rate"], "text": "Due to the high mutation rate of the virus, a particular influenza vaccine usually confers protection for no more than a few years."}, {"list": ["Historical annual reformulations of the influenza vaccine", "pharmaceutical companies"], "text": "Every year, the World Health Organization predicts which strains of the virus are most likely to be circulating in the next year (see Historical annual reformulations of the influenza vaccine), allowing pharmaceutical companies to develop vaccines that will provide the best immunity against these strains."}, {"text": "The vaccine is reformulated each season for a few specific flu strains but does not include all the strains active in the world during that season."}, {"text": "It takes about six months for the manufacturers to formulate and produce the millions of doses required to deal with the seasonal epidemics; occasionally, a new or overlooked strain becomes prominent during that time."}, {"text": "It is also possible to get infected just before vaccination and get sick with the strain that the vaccine is supposed to prevent, as the vaccine takes about two weeks to become effective."}, {"list": ["immune system"], "text": "Vaccines can cause the immune system to react as if the body were actually being infected, and general infection symptoms (many cold and flu symptoms are just general infection symptoms) can appear, though these symptoms are usually not as severe or long-lasting as influenza."}, {"list": ["adverse effect", "allergic reaction"], "text": "The most dangerous adverse effect is a severe allergic reaction to either the virus material itself or residues from the hen eggs used to grow the influenza; however, these reactions are extremely rare."}, {"text": "The cost-effectiveness of seasonal influenza vaccination has been widely evaluated for different groups and in different settings."}, {"text": "It has generally been found to be a cost-effective intervention, especially in children and the elderly, however the results of economic evaluations of influenza vaccination have often been found to be dependent on key assumptions."}, {"list": ["hand washing"], "text": "Reasonably effective ways to reduce the transmission of influenza include good personal health and hygiene habits such as: not touching your eyes, nose or mouth; frequent hand washing (with soap and water, or with alcohol-based hand rubs); covering coughs and sneezes; avoiding close contact with sick people; and staying home yourself if you are sick."}, {"text": "Avoiding spitting is also recommended."}, {"list": ["face masks"], "text": "Although face masks might help prevent transmission when caring for the sick, there is mixed evidence on beneficial effects in the community."}, {"text": "Smoking raises the risk of contracting influenza, as well as producing more severe disease symptoms."}, {"list": ["aerosols"], "text": "Since influenza spreads through both aerosols and contact with contaminated surfaces, surface sanitizing may help prevent some infections."}, {"list": ["Alcohol", "quaternary ammonium compounds"], "text": "Alcohol is an effective sanitizer against influenza viruses, while quaternary ammonium compounds can be used with alcohol so that the sanitizing effect lasts for longer."}, {"list": ["bleach"], "text": "In hospitals, quaternary ammonium compounds and bleach are used to sanitize rooms or equipment that have been occupied by patients with influenza symptoms."}, {"text": "At home, this can be done effectively with a diluted chlorine bleach."}, {"list": ["Social distancing"], "text": "Social distancing strategies used during past pandemics, such as closing schools, churches and theaters, slowed the spread of the virus but did not have a large effect on the overall death rate."}, {"text": "It is uncertain if reducing public gatherings, by for example closing schools and workplaces, will reduce transmission since people with influenza may just be moved from one area to another; such measures would also be difficult to enforce and might be unpopular."}, {"text": "When small numbers of people are infected, isolating the sick might reduce the risk of transmission."}, {"list": ["alcohol", "tobacco", "paracetamol"], "text": "People with the flu are advised to get plenty of rest, drink plenty of liquids, avoid using alcohol and tobacco and, if necessary, take medications such as acetaminophen (paracetamol) to relieve the fever and muscle aches associated with the flu."}, {"list": ["aspirin", "influenza type B", "Reye\'s syndrome", "liver"], "text": "Children and teenagers with flu symptoms (particularly fever) should avoid taking aspirin during an influenza infection (especially influenza type B), because doing so can lead to Reye\'s syndrome, a rare but potentially fatal disease of the liver."}, {"list": ["antibiotics", "secondary infections", "bacterial pneumonia"], "text": "Since influenza is caused by a virus, antibiotics have no effect on the infection; unless prescribed for secondary infections such as bacterial pneumonia."}, {"text": "Antiviral medication may be effective, if given early, but some strains of influenza can show resistance to the standard antiviral drugs and there is concern about the quality of the research."}, {"list": ["neuraminidase inhibitors", "oseltamivir", "zanamivir", "M2 protein", "adamantane"], "text": "The two classes of antiviral drugs used against influenza are neuraminidase inhibitors (oseltamivir and zanamivir) and M2 protein inhibitors (adamantane derivatives)."}, {"text": "Overall the benefits of neuraminidase inhibitors in those who are otherwise healthy do not appear to be greater than the risks."}, {"text": "There does not appear to be any benefit in those with other health problems."}, {"list": ["pneumonia"], "text": "In those believed to have the flu, they decreased the length of time symptoms were present by slightly less than a day but did not appear to affect the risk of complications such as needing hospitalization or pneumonia."}, {"list": ["Roche"], "text": "Previous to 2013 the benefits were unclear as the manufacturer (Roche) refused to release trial data for independent analysis."}, {"text": "Increasingly prevalent resistance to neuraminidase inhibitors has led to researchers to seek alternative antiviral drugs with different mechanisms of action."}, {"list": ["antiviral drugs", "amantadine", "rimantadine", "ion channel", "M2 protein"], "text": "The antiviral drugs amantadine and rimantadine inhibit a viral ion channel (M2 protein), thus inhibiting replication of the influenza A virus."}, {"text": "These drugs are sometimes effective against influenza A if given early in the infection but are ineffective against influenza B viruses, which lack the M2 drug target."}, {"list": ["H3N2"], "text": "Measured resistance to amantadine and rimantadine in American isolates of H3N2 has increased to 91% in 2005."}, {"text": "This high level of resistance may be due to the easy availability of amantadines as part of over-the-counter cold remedies in countries such as China and Russia, and their use to prevent outbreaks of influenza in farmed poultry."}, {"list": ["drug resistance"], "text": "The CDC recommended against using M2 inhibitors during the 2005–06 influenza season due to high levels of drug resistance."}, {"list": ["common cold"], "text": "Influenza\'s effects are much more severe and last longer than those of the common cold."}, {"list": ["pneumonia"], "text": "Most people will recover completely in about one to two weeks, but others will develop life-threatening complications (such as pneumonia)."}, {"list": ["weak immune system", "HIV"], "text": "Thus, influenza can be deadly, especially for the weak, young and old, or chronically ill. People with a weak immune system, such as people with advanced HIV infection or transplant patients (whose immune systems are medically suppressed to prevent transplant organ rejection), suffer from particularly severe disease."}, {"text": "Pregnant women and young children are also at a high risk for complications."}, {"text": "The flu can worsen chronic health problems."}, {"list": ["shortness of breath", "coronary heart disease", "congestive heart failure"], "text": "People with emphysema, chronic bronchitis or asthma may experience shortness of breath while they have the flu, and influenza may cause worsening of coronary heart disease or congestive heart failure."}, {"list": ["Smoking", "risk factor"], "text": "Smoking is another risk factor associated with more serious disease and increased mortality from influenza."}, {"text": "According to the World Health Organization: \\"Every winter, tens of millions of people get the flu. Most are only ill and out of work for a week, yet the elderly are at a higher risk of death from the illness. We know the worldwide death toll exceeds a few hundred thousand people a year, but even in developed countries the numbers are uncertain, because medical authorities don\'t usually verify who actually died of influenza and who died of a flu-like illness.\\" Even healthy people can be affected, and serious problems from influenza can happen at any age."}, {"list": ["medical conditions", "bronchitis", "sinus", "ear infections"], "text": "People over 65 years old, pregnant women, very young children and people of any age with chronic medical conditions are more likely to get complications from influenza, such as pneumonia, bronchitis, sinus, and ear infections."}, {"list": ["autoimmune", "Guillain–Barré syndrome"], "text": "In some cases, an autoimmune response to an influenza infection may contribute to the development of Guillain–Barré syndrome."}, {"text": "However, as many other infections can increase the risk of this disease, influenza may only be an important cause during epidemics."}, {"text": "This syndrome has been believed to also be a rare side effect of influenza vaccines."}, {"text": "One review gives an incidence of about one case per million vaccinations."}, {"text": "Getting infected by influenza itself increases both the risk of death (up to 1 in 10,000) and increases the risk of developing GBS to a much higher level than the highest level of suspected vaccine involvement (approx."}, {"text": "10 times higher by recent estimates)."}, {"list": ["Northern", "Southern Hemispheres"], "text": "Influenza reaches peak prevalence in winter, and because the Northern and Southern Hemispheres have winter at different times of the year, there are actually two different flu seasons each year."}, {"list": ["National Influenza Centers"], "text": "This is why the World Health Organization (assisted by the National Influenza Centers) makes recommendations for two different vaccine formulations every year; one for the Northern, and one for the Southern Hemisphere."}, {"text": "A long-standing puzzle has been why outbreaks of the flu occur seasonally rather than uniformly throughout the year."}, {"text": "One possible explanation is that, because people are indoors more often during the winter, they are in close contact more often, and this promotes transmission from person to person."}, {"text": "Increased travel due to the Northern Hemisphere winter holiday season may also play a role."}, {"text": "Another factor is that cold temperatures lead to drier air, which may dehydrate mucus particles."}, {"text": "Dry particles are lighter and can thus remain airborne for a longer period.The virus also survives longer on surfaces at colder temperatures and aerosol transmission of the virus is highest in cold environments (less than 5 °C) with low relative humidity."}, {"text": "The lower air humidity in winter seems to be the main cause of seasonal influenza transmission in temperate regions."}, {"text": "However, seasonal changes in infection rates also occur in tropical regions, and in some countries these peaks of infection are seen mainly during the rainy season."}, {"list": ["childhood diseases", "measles", "pertussis"], "text": "Seasonal changes in contact rates from school terms, which are a major factor in other childhood diseases such as measles and pertussis, may also play a role in the flu."}, {"text": "A combination of these small seasonal effects may be amplified by dynamical resonance with the endogenous disease cycles."}, {"list": ["H5N1"], "text": "H5N1 exhibits seasonality in both humans and birds."}, {"list": ["vitamin D"], "text": "An alternative hypothesis to explain seasonality in influenza infections is an effect of vitamin D levels on immunity to the virus."}, {"list": ["Robert Edgar Hope-Simpson"], "text": "This idea was first proposed by Robert Edgar Hope-Simpson in 1965."}, {"list": ["UV radiation"], "text": "He proposed that the cause of influenza epidemics during winter may be connected to seasonal fluctuations of vitamin D, which is produced in the skin under the influence of solar (or artificial) UV radiation."}, {"text": "This could explain why influenza occurs mostly in winter and during the tropical rainy season, when people stay indoors, away from the sun, and their vitamin D levels fall."}, {"list": ["viruses", "epidemics", "pandemic"], "text": "As influenza is caused by a variety of species and strains of viruses, in any given year some strains can die out while others create epidemics, while yet another strain can cause a pandemic."}, {"list": ["flu seasons"], "text": "Typically, in a year\'s normal two flu seasons (one per hemisphere), there are between three and five million cases of severe illness and around 500,000 deaths worldwide, which by some definitions is a yearly influenza epidemic."}, {"text": "Although the incidence of influenza can vary widely between years, approximately 36,000 deaths and more than 200,000 hospitalizations are directly associated with influenza every year in the United States."}, {"text": "One method of calculating influenza mortality produced an estimate of 41,400 average deaths per year in the United States between 1979 and 2001."}, {"list": ["Centers for Disease Control and Prevention"], "text": "Different methods in 2010 by the Centers for Disease Control and Prevention (CDC) reported a range from a low of about 3,300 deaths to a high of 49,000 per year."}, {"list": ["pandemics section"], "text": "Roughly three times per century, a pandemic occurs, which infects a large proportion of the world\'s population and can kill tens of millions of people (see pandemics section)."}, {"list": ["virulence", "1918 influenza"], "text": "One study estimated that if a strain with similar virulence to the 1918 influenza emerged today, it could kill between 50 and 80 million people."}, {"list": ["evolving", "mutation", "reassortment"], "text": "New influenza viruses are constantly evolving by mutation or by reassortment."}, {"list": ["hemagglutinin", "neuraminidase", "antigens"], "text": "Mutations can cause small changes in the hemagglutinin and neuraminidase antigens on the surface of the virus."}, {"list": ["antigenic drift"], "text": "This is called antigenic drift, which slowly creates an increasing variety of strains until one evolves that can infect people who are immune to the pre-existing strains."}, {"text": "This new variant then replaces the older strains as it rapidly sweeps through the human population, often causing an epidemic."}, {"text": "However, since the strains produced by drift will still be reasonably similar to the older strains, some people will still be immune to them."}, {"list": ["antigenic shift"], "text": "In contrast, when influenza viruses reassort, they acquire completely new antigens—for example by reassortment between avian strains and human strains; this is called antigenic shift."}, {"text": "If a human influenza virus is produced that has entirely new antigens, everybody will be susceptible, and the novel influenza will spread uncontrollably, causing a pandemic."}, {"text": "In contrast to this model of pandemics based on antigenic drift and shift, an alternative approach has been proposed where the periodic pandemics are produced by interactions of a fixed set of viral strains with a human population with a constantly changing set of immunities to different viral strains."}, {"text": "From a public health point of view, flu epidemics spread rapidly and are very difficult to control."}, {"text": "Most influenza virus strains are not very infectious and each infected individual will only go on to infect one or two other individuals (the basic reproduction number for influenza is generally around 1.4)."}, {"text": "However, the generation time for influenza is extremely short: the time from a person becoming infected to when he infects the next person is only two days."}, {"text": "The short generation time means that influenza epidemics generally peak at around 2 months and burn out after 3 months: the decision to intervene in an influenza epidemic therefore has to be taken early, and the decision is therefore often made on the back of incomplete data."}, {"text": "Another problem is that individuals become infectious before they become symptomatic, which means that putting people in quarantine after they become ill is not an effective public health intervention."}, {"text": "For the average person, viral shedding tends to peak on day two whereas symptoms peak on day three."}, {"list": ["Italian language", "astrological"], "text": "The word Influenza comes from the Italian language meaning \\"influence\\" and refers to the cause of the disease; initially, this ascribed illness to unfavorable astrological influences."}, {"text": "Changes in medical thought led to its modification to influenza del freddo, meaning \\"influence of the cold\\"."}, {"text": "The word influenza was first used in English to refer to the disease we know today in 1703 by J. Hugger of the University of Edinburgh in his thesis De Catarrho epidemio, vel Influenza, prout in India occidentali sese ostendit."}, {"list": ["1918 flu pandemic"], "text": "Archaic terms for influenza include epidemic catarrh, la grippe (from the French, first used by Molyneaux in 1694 ), sweating sickness, and Spanish fever (particularly for the 1918 flu pandemic strain)."}, {"list": ["Hippocrates"], "text": "The symptoms of human influenza were clearly described by Hippocrates roughly 2,400 years ago."}, {"text": "Although the virus seems to have caused epidemics throughout human history, historical data on influenza are difficult to interpret, because the symptoms can be similar to those of other respiratory diseases."}, {"list": ["European colonization of the Americas", "Christopher Columbus"], "text": "The disease may have spread from Europe to the Americas as early as the European colonization of the Americas; since almost the entire indigenous population of the Antilles was killed by an epidemic resembling influenza that broke out in 1493, after the arrival of Christopher Columbus."}, {"text": "The first convincing record of an influenza pandemic was of an outbreak in 1580, which began in Russia and spread to Europe via Africa."}, {"list": ["Rome"], "text": "In Rome, over 8,000 people were killed, and several Spanish cities were almost wiped out."}, {"text": "Pandemics continued sporadically throughout the 17th and 18th centuries, with the pandemic of 1830–1833 being particularly widespread; it infected approximately a quarter of the people exposed."}, {"list": ["1918 flu pandemic", "type A influenza", "H1N1"], "text": "The most famous and lethal outbreak was the 1918 flu pandemic (Spanish flu pandemic) (type A influenza, H1N1 subtype), which lasted from 1918 to 1919."}, {"text": "It is not known exactly how many it killed, but estimates range from 50 to 100 million people."}, {"list": ["Black Death"], "text": "This pandemic has been described as \\"the greatest medical holocaust in history\\" and may have killed as many people as the Black Death."}, {"list": ["cytokine storms"], "text": "This huge death toll was caused by an extremely high infection rate of up to 50% and the extreme severity of the symptoms, suspected to be caused by cytokine storms."}, {"list": ["cholera"], "text": "Symptoms in 1918 were so unusual that initially influenza was misdiagnosed as dengue, cholera, or typhoid."}, {"list": ["mucous membranes", "petechial hemorrhages", "bacterial pneumonia", "secondary infection", "hemorrhages", "edema"], "text": "One observer wrote, \\"One of the most striking of the complications was hemorrhage from mucous membranes, especially from the nose, stomach, and intestine. Bleeding from the ears and petechial hemorrhages in the skin also occurred.\\" The majority of deaths were from bacterial pneumonia, a secondary infection caused by influenza, but the virus also killed people directly, causing massive hemorrhages and edema in the lung."}, {"list": ["Arctic", "Pacific islands"], "text": "The 1918 flu pandemic was truly global, spreading even to the Arctic and remote Pacific islands."}, {"list": ["mortality rate"], "text": "The unusually severe disease killed between two and twenty percent of those infected, as opposed to the more usual flu epidemic mortality rate of 0.1%."}, {"text": "Another unusual feature of this pandemic was that it mostly killed young adults, with 99% of pandemic influenza deaths occurring in people under 65, and more than half in young adults 20 to 40 years old."}, {"text": "This is unusual since influenza is normally most deadly to the very young (under age 2) and the very old (over age 70)."}, {"text": "The total mortality of the 1918–1919 pandemic is not known, but it is estimated that 2.5% to 5% of the world\'s population was killed."}, {"list": ["HIV/AIDS"], "text": "As many as 25 million may have been killed in the first 25 weeks; in contrast, HIV/AIDS has killed 25 million in its first 25 years."}, {"text": "Later flu pandemics were not so devastating."}, {"list": ["Asian Flu", "H2N2", "Hong Kong Flu", "H3N2"], "text": "They included the 1957 Asian Flu (type A, H2N2 strain) and the 1968 Hong Kong Flu (type A, H3N2 strain), but even these smaller outbreaks killed millions of people."}, {"list": ["antibiotics"], "text": "In later pandemics antibiotics were available to control secondary infections and this may have helped reduce mortality compared to the Spanish Flu of 1918."}, {"list": ["Chamberland filters", "bacteria"], "text": "The first influenza virus to be isolated was from poultry, when in 1901 the agent causing a disease called \\"fowl plague\\" was passed through Chamberland filters, which have pores that are too small for bacteria to pass through."}, {"list": ["etiological", "pigs", "Richard Shope"], "text": "The etiological cause of influenza, the virus family Orthomyxoviridae, was first discovered in pigs by Richard Shope in 1931."}, {"list": ["Patrick Laidlaw", "Medical Research Council", "the United Kingdom"], "text": "This discovery was shortly followed by the isolation of the virus from humans by a group headed by Patrick Laidlaw at the Medical Research Council of the United Kingdom in 1933."}, {"list": ["Wendell Stanley", "tobacco mosaic virus", "non-cellular"], "text": "However, it was not until Wendell Stanley first crystallized tobacco mosaic virus in 1935 that the non-cellular nature of viruses was appreciated."}, {"list": ["Thomas Francis, Jr.", "Frank Macfarlane Burnet"], "text": "The first significant step towards preventing influenza was the development in 1944 of a killed-virus vaccine for influenza by Thomas Francis, Jr. This built on work by Australian Frank Macfarlane Burnet, who showed that the virus lost virulence when it was cultured in fertilized hen\'s eggs."}, {"list": ["University of Michigan", "U.S. Army"], "text": "Application of this observation by Francis allowed his group of researchers at the University of Michigan to develop the first influenza vaccine, with support from the U.S. Army."}, {"list": ["World War I"], "text": "The Army was deeply involved in this research due to its experience of influenza in World War I, when thousands of troops were killed by the virus in a matter of months."}, {"list": ["amantadine", "neuraminidase inhibitors"], "text": "In comparison to vaccines, the development of anti-influenza drugs has been slower, with amantadine being licensed in 1966 and, almost thirty years later, the next class of drugs (the neuraminidase inhibitors) being developed."}, {"list": ["direct costs", "productivity", "indirect costs"], "text": "Influenza produces direct costs due to lost productivity and associated medical treatment, as well as indirect costs of preventative measures."}, {"text": "In the United States, influenza is responsible for a total cost of over $10 billion per year, while it has been estimated that a future pandemic could cause hundreds of billions of dollars in direct and indirect costs."}, {"list": ["Spanish influenza", "depressive"], "text": "However, the economic impacts of past pandemics have not been intensively studied, and some authors have suggested that the Spanish influenza actually had a positive long-term effect on per-capita income growth, despite a large reduction in the working population and severe short-term depressive effects."}, {"list": ["U.S. economy"], "text": "Other studies have attempted to predict the costs of a pandemic as serious as the 1918 Spanish flu on the U.S. economy, where 30% of all workers became ill, and 2.5% were killed."}, {"list": ["gross domestic product"], "text": "A 30% sickness rate and a three-week length of illness would decrease the gross domestic product by 5%. Additional costs would come from medical treatment of 18 million to 45 million people, and total economic costs would be approximately $700 billion."}, {"text": "Preventative costs are also high."}, {"list": ["U.S. dollars", "disaster drills", "border controls"], "text": "Governments worldwide have spent billions of U.S. dollars preparing and planning for a potential H5N1 avian influenza pandemic, with costs associated with purchasing drugs and vaccines as well as developing disaster drills and strategies for improved border controls."}, {"list": ["United States President", "George W. Bush", "Congress"], "text": "On 1 November 2005, United States President George W. Bush unveiled the National Strategy to Safeguard Against the Danger of Pandemic Influenza backed by a request to Congress for $7.1 billion to begin implementing the plan."}, {"text": "Internationally, on 18 January 2006, donor nations pledged US$2 billion to combat bird flu at the two-day International Pledging Conference on Avian and Human Influenza held in China."}, {"text": "In an assessment of the 2009 H1N1 pandemic on selected countries in the Southern Hemisphere, data suggest that all countries experienced some time-limited and/or geographically isolated socio/economic effects and a temporary decrease in tourism most likely due to fear of 2009 H1N1 disease."}, {"text": "It is still too early to determine whether the H1N1 pandemic has caused any long-term economic impacts."}, {"list": ["molecular virology", "pathogenesis", "immune responses", "viral genomics", "epidemiology"], "text": "Research on influenza includes studies on molecular virology, how the virus produces disease (pathogenesis), host immune responses, viral genomics, and how the virus spreads (epidemiology)."}, {"list": ["vaccine"], "text": "These studies help in developing influenza countermeasures; for example, a better understanding of the body\'s immune system response helps vaccine development, and a detailed picture of how influenza invades cells aids the development of antiviral drugs."}, {"list": ["basic research", "Influenza Genome Sequencing Project", "immunogenicity", "evolves"], "text": "One important basic research program is the Influenza Genome Sequencing Project, which is creating a library of influenza sequences; this library should help clarify which factors make one strain more lethal than another, which genes most affect immunogenicity, and how the virus evolves over time."}, {"text": "Research into new vaccines is particularly important, as current vaccines are very slow and expensive to produce and must be reformulated every year."}, {"list": ["recombinant DNA"], "text": "The sequencing of the influenza genome and recombinant DNA technology may accelerate the generation of new vaccine strains by allowing scientists to substitute new antigens into a previously developed vaccine strain."}, {"list": ["cell culture"], "text": "New technologies are also being developed to grow viruses in cell culture, which promises higher yields, less cost, better quality and surge capacity."}, {"list": ["M2 protein", "University of Ghent", "Walter Fiers", "Xavier Saelens"], "text": "Research on a universal influenza A vaccine, targeted against the external domain of the transmembrane viral M2 protein (M2e), is being done at the University of Ghent by Walter Fiers, Xavier Saelens and their team  and has now successfully concluded Phase I clinical trials."}, {"text": "There has been some research success towards a \\"universal flu vaccine\\" that produces antibodies against proteins on the viral coat which mutate less rapidly, and thus a single shot could potentially provide longer-lasting protection."}, {"list": ["biologics"], "text": "A number of biologics, therapeutic vaccines and immunobiologics are also being investigated for treatment of infection caused by viruses."}, {"text": "Therapeutic biologics are designed to activate the immune response to virus or antigens."}, {"list": ["metabolic pathways", "lymphocytes", "macrophages", "antigen presenting cells", "cytotoxic"], "text": "Typically, biologics do not target metabolic pathways like anti-viral drugs, but stimulate immune cells such as lymphocytes, macrophages, and/or antigen presenting cells, in an effort to drive an immune response towards a cytotoxic effect against the virus."}, {"text": "Influenza models, such as murine influenza, are convenient models to test the effects of prophylactic and therapeutic biologics."}, {"list": ["Lymphocyte T-Cell Immune Modulator"], "text": "For example, Lymphocyte T-Cell Immune Modulator inhibits viral growth in the murine model of influenza."}, {"text": "Influenza infects many animal species, and transfer of viral strains between species can occur."}, {"list": ["Birds", "animal reservoirs"], "text": "Birds are thought to be the main animal reservoirs of influenza viruses."}, {"list": ["hemagglutinin", "neuraminidase"], "text": "Sixteen forms of hemagglutinin and nine forms of neuraminidase have been identified."}, {"list": ["dogs", "horses", "pigs", "camels", "ferrets", "cats", "seals", "mink", "whales"], "text": "All known subtypes (HxNy) are found in birds, but many subtypes are endemic in humans, dogs, horses, and pigs; populations of camels, ferrets, cats, seals, mink, and whales also show evidence of prior infection or exposure to influenza."}, {"list": ["bird flu", "human flu", "swine flu", "horse flu", "dog flu"], "text": "Variants of flu virus are sometimes named according to the species the strain is endemic in or adapted to. The main variants named using this convention are: bird flu, human flu, swine flu, horse flu and dog flu."}, {"list": ["Cat flu", "feline viral rhinotracheitis", "feline calicivirus", "loss of appetite"], "text": "(Cat flu generally refers to feline viral rhinotracheitis or feline calicivirus and not infection from an influenza virus.) In pigs, horses and dogs, influenza symptoms are similar to humans, with cough, fever and loss of appetite."}, {"list": ["New England"], "text": "The frequency of animal diseases are not as well-studied as human infection, but an outbreak of influenza in harbor seals caused approximately 500 seal deaths off the New England coast in 1979–1980."}, {"text": "However, outbreaks in pigs are common and do not cause severe mortality."}, {"list": ["Vaccines", "poultry", "avian influenza"], "text": "Vaccines have also been developed to protect poultry from avian influenza."}, {"list": ["culling"], "text": "These vaccines can be effective against multiple strains and are used either as part of a preventative strategy, or combined with culling in attempts to eradicate outbreaks."}, {"text": "Flu symptoms in birds are variable and can be unspecific."}, {"list": ["weight loss", "respiratory disease"], "text": "The symptoms following infection with low-pathogenicity avian influenza may be as mild as ruffled feathers, a small reduction in egg production, or weight loss combined with minor respiratory disease."}, {"list": ["avian influenza"], "text": "Since these mild symptoms can make diagnosis in the field difficult, tracking the spread of avian influenza requires laboratory testing of samples from infected birds."}, {"list": ["H9N2"], "text": "Some strains such as Asian H9N2 are highly virulent to poultry and may cause more extreme symptoms and significant mortality."}, {"list": ["chickens", "turkeys"], "text": "In its most highly pathogenic form, influenza in chickens and turkeys produces a sudden appearance of severe symptoms and almost 100% mortality within two days."}, {"list": ["intensive farming"], "text": "As the virus spreads rapidly in the crowded conditions seen in the intensive farming of chickens and turkeys, these outbreaks can cause large economic losses to poultry farmers."}, {"list": ["H5N1 flu", "endemic", "Southeast Asia"], "text": "An avian-adapted, highly pathogenic strain of H5N1 (called HPAI A(H5N1), for \\"highly pathogenic avian influenza virus of type A of subtype H5N1\\") causes H5N1 flu, commonly known as \\"avian influenza\\" or simply \\"bird flu\\", and is endemic in many bird populations, especially in Southeast Asia."}, {"list": ["spreading globally"], "text": "This Asian lineage strain of HPAI A(H5N1) is spreading globally."}, {"list": ["epizootic", "culling"], "text": "It is epizootic (an epidemic in non-humans) and panzootic (a disease affecting animals of many species, especially over a wide area), killing tens of millions of birds and spurring the culling of hundreds of millions of other birds in an attempt to control its spread."}, {"text": "Most references in the media to \\"bird flu\\" and most references to H5N1 are about this specific strain."}, {"text": "At present, HPAI A(H5N1) is an avian disease, and there is no evidence suggesting efficient human-to-human transmission of HPAI A(H5N1)."}, {"text": "In almost all cases, those infected have had extensive physical contact with infected birds."}, {"text": "In the future, H5N1 may mutate or reassort into a strain capable of efficient human-to-human transmission."}, {"text": "The exact changes that are required for this to happen are not well understood."}, {"list": ["virulence", "endemic"], "text": "However, due to the high lethality and virulence of H5N1, its endemic presence, and its large and increasing biological host reservoir, the H5N1 virus was the world\'s pandemic threat in the 2006–07 flu season, and billions of dollars are being raised and spent researching H5N1 and preparing for a potential influenza pandemic."}, {"text": "In March 2013, the Chinese government reported three cases of H7N9 influenza infections in humans."}, {"text": "Two of whom had died and the third was critically ill. Although the strain of the virus is not thought to spread efficiently between humans,  by mid-April, at least 82 persons had become ill from H7N9, of which 17 had died."}, {"text": "These cases include three small family clusters in Shanghai and one cluster between a neighboring girl and boy in Beijing, raising at least the possibility of human-to-human transmission."}, {"text": "WHO points out that one cluster did not have two of the cases lab confirmed and further points out, as a matter of baseline information, that some viruses are able to cause limited human-to-human transmission under conditions of close contact but are not transmissible enough to cause large community outbreaks."}, {"list": ["swine influenza"], "text": "In pigs swine influenza produces fever, lethargy, sneezing, coughing, difficulty breathing and decreased appetite."}, {"text": "In some cases the infection can cause abortion."}, {"text": "Although mortality is usually low, the virus can produce weight loss and poor growth, causing economic loss to farmers."}, {"text": "Infected pigs can lose up to 12 pounds of body weight over a 3- to 4-week period."}, {"list": ["zoonotic"], "text": "Direct transmission of an influenza virus from pigs to humans is occasionally possible (this is called zoonotic swine flu)."}, {"text": "In all, 50 human cases are known to have occurred since the virus was identified in the mid-20th century, which have resulted in six deaths."}, {"list": ["H1N1", "2009 flu pandemic"], "text": "In 2009, a swine-origin H1N1 virus strain commonly referred to as \\"swine flu\\" caused the 2009 flu pandemic, but there is no evidence that it is endemic to pigs (i.e. actually a swine flu) or of transmission from pigs to people, instead the virus is spreading from person to person."}, {"list": ["humans", "birds"], "text": "This strain is a reassortment of several strains of H1N1 that are usually found separately, in humans, birds, and pigs."}, {"text": "General"}, {"text": "Microbiology"}, {"text": "Pathogenesis"}, {"text": "Epidemiology"}, {"list": ["Epidemiology of WHO-confirmed human cases of avian influenza A(H5N1) infection"], "text": "* Epidemiology of WHO-confirmed human cases of avian influenza A(H5N1) infection"}, {"text": "Treatment and prevention"}, {"text": "Research"}]', '{"list": ["Handwashing", " surgical mask", " influenza vaccine"], "conditions": ["Handwashing", "surgical mask", "influenza vaccine"]}', '{}', '{"desc": "Antiviral drugs such as oseltamivir", "list": ["Antiviral drugs", "oseltamivir"]}', '[{"url": "https://upload.wikimedia.org/wikipedia/commons/a/a4/EM_of_influenza_virus.jpg", "file": "File:EM of influenza virus.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/a/a4/EM_of_influenza_virus.jpg/300px-EM_of_influenza_virus.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/3/3e/Influenza.webm", "file": "File:Influenza.webm", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3e/Influenza.webm/300px-Influenza.webm"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/9/9e/Symptoms_of_influenza.svg", "file": "File:Symptoms of influenza.svg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/9/9e/Symptoms_of_influenza.svg/300px-Symptoms_of_influenza.svg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/3/3b/3D_Influenza_virus.png", "file": "File:3D Influenza virus.png", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3b/3D_Influenza_virus.png/300px-3D_Influenza_virus.png"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/7/7a/Influenza_nomenclature.svg", "file": "File:Influenza nomenclature.svg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/7/7a/Influenza_nomenclature.svg/300px-Influenza_nomenclature.svg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/a/a0/Virus_Replication_large.svg", "file": "File:Virus Replication large.svg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/a/a0/Virus_Replication_large.svg/300px-Virus_Replication_large.svg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/b/b0/H1N1_versus_H5N1_pathology.png", "file": "Image:H1N1 versus H5N1 pathology.png", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/b/b0/H1N1_versus_H5N1_pathology.png/300px-H1N1_versus_H5N1_pathology.png"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/9/9f/Defense.gov_News_Photo_041028-N-9864S-021.jpg", "file": "File:Defense.gov News Photo 041028-N-9864S-021.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/9/9f/Defense.gov_News_Photo_041028-N-9864S-021.jpg/300px-Defense.gov_News_Photo_041028-N-9864S-021.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/3/30/Influenza_Seasonal_Risk_Areas.svg", "file": "Image:Influenza Seasonal Risk Areas.svg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/3/30/Influenza_Seasonal_Risk_Areas.svg/300px-Influenza_Seasonal_Risk_Areas.svg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/d/d2/Influenza_geneticshift.svg", "file": "File:Influenza geneticshift.svg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/d/d2/Influenza_geneticshift.svg/300px-Influenza_geneticshift.svg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/4/4f/Ferguson_influenza_generation_time_distribution.png", "file": "File:Ferguson influenza generation time distribution.png", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/4/4f/Ferguson_influenza_generation_time_distribution.png/300px-Ferguson_influenza_generation_time_distribution.png"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/7/70/W_curve.png", "file": "File:W curve.png", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/7/70/W_curve.png/300px-W_curve.png"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/1/1c/Airport_Thermographic_Camera.jpg", "file": "Image:Airport Thermographic Camera.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/1/1c/Airport_Thermographic_Camera.jpg/300px-Airport_Thermographic_Camera.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/b/b8/Influenza_subtypes.svg", "file": "Image:Influenza subtypes.svg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/b/b8/Influenza_subtypes.svg/300px-Influenza_subtypes.svg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/8/86/Influenza_virus_research.jpg", "file": "File:Influenza virus research.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/8/86/Influenza_virus_research.jpg/300px-Influenza_virus_research.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/6/6d/Chine