INSERT INTO `entity_data` (`id`, `entity`, `name`, `caption`, `type`, `symptoms`, `diagnosis`, `causes`, `parse_data`, `prevention`, `complications`, `medication`, `images`) VALUES
(5, 'acne', 'Acne', 'Acne%20vulgaris%20in%20an%2018-year-old%20male%20during%20puberty', 'Dermatology', '["Blackheads", "whiteheads", "pimples", "oily skin", "scarring"]', '{}', '{}', '[{"list": ["skin disease", "hair follicles", "dead skin cells", "oil from the skin"], "text": "Acne, also known as acne vulgaris, is a long-term skin disease that occurs when hair follicles are clogged with dead skin cells and oil from the skin."}, {"list": ["blackheads or whiteheads", "pimples", "scarring"], "text": "It is characterized by blackheads or whiteheads, pimples, oily skin, and possible scarring."}, {"list": ["oil glands"], "text": "It primarily affects areas of the skin with a relatively high number of oil glands, including the face, upper part of the chest, and back."}, {"list": ["anxiety", "self-esteem", "depression", "thoughts of suicide"], "text": "The resulting appearance can lead to anxiety, reduced self-esteem and, in extreme cases, depression or thoughts of suicide."}, {"list": ["Genetics"], "text": "Genetics is thought to be the primary cause of acne in 80% of cases."}, {"list": ["cigarette smoking", "cleanliness"], "text": "The role of diet and cigarette smoking is unclear, and neither cleanliness nor exposure to sunlight appear to play a part."}, {"list": ["puberty", "sexes", "hormones", "testosterone"], "text": "During puberty, in both sexes, acne is often brought on by an increase in hormones such as testosterone."}, {"list": ["Propionibacterium acnes\'\'"], "text": "A frequent factor is excessive growth of the bacterium Propionibacterium acnes, which is normally present on the skin."}, {"text": "Many treatment options for acne are available, including lifestyle changes, medications, and medical procedures."}, {"list": ["simple carbohydrates", "sugar"], "text": "Eating fewer simple carbohydrates such as sugar may help."}, {"list": ["Treatments applied directly to the affected skin", "azelaic acid", "benzoyl peroxide", "salicylic acid"], "text": "Treatments applied directly to the affected skin, such as azelaic acid, benzoyl peroxide, and salicylic acid, are commonly used."}, {"list": ["Antibiotics", "retinoids", "formulations", "taken by mouth"], "text": "Antibiotics and retinoids are available in formulations that are applied to the skin and taken by mouth for the treatment of acne."}, {"list": ["resistance to antibiotics"], "text": "However, resistance to antibiotics may develop as a result of antibiotic therapy."}, {"list": ["birth control pills"], "text": "Several types of birth control pills help against acne in women."}, {"list": ["Isotretinoin"], "text": "Isotretinoin pills are usually reserved for severe acne due to greater potential side effects."}, {"text": "Early and aggressive treatment of acne is advocated by some in the medical community to decrease the overall long-term impact to individuals."}, {"text": "In 2015, acne was estimated to affect 633 million people globally, making it the 8th most common disease worldwide."}, {"list": ["adolescence", "Western world"], "text": "Acne commonly occurs in adolescence and affects an estimated 80–90% of teenagers in the Western world."}, {"text": "Lower rates are reported in some rural societies."}, {"text": "Children and adults may also be affected before and after puberty."}, {"text": "Although acne becomes less common in adulthood, it persists in nearly half of affected people into their twenties and thirties and a smaller group continue to have difficulties into their forties."}, {"text": "The severity of acne vulgaris (Gr."}, {"text": "ἀκµή, \\"point\\" + L. vulgaris, \\"common\\") can be classified as mild, moderate, or severe as this helps to determine an appropriate treatment regimen."}, {"list": ["clogged skin follicles"], "text": "Mild acne is classically defined by the presence of clogged skin follicles (known as comedones) limited to the face with occasional inflammatory lesions."}, {"list": ["papules", "pustules"], "text": "Moderate severity acne is said to occur when a higher number of inflammatory papules and pustules occur on the face compared to mild cases of acne and are found on the trunk of the body."}, {"list": ["nodules"], "text": "Severe acne is said to occur when nodules (the painful \'bumps\' lying under the skin) are the characteristic facial lesions and involvement of the trunk is extensive."}, {"list": ["cysts"], "text": "Large nodules were previously referred to as cysts, and the term nodulocystic has been used in the medical literature to describe severe cases of inflammatory acne."}, {"text": "True cysts are in fact rare in those with acne and the term severe nodular acne is now the preferred terminology."}, {"text": "Acne inversa (L."}, {"list": ["hidradenitis suppurativa", "rosacea"], "text": "invertō, \\"upside down\\") and acne rosacea (rosa, \\"rose-colored\\" + -āceus, \\"forming\\") are not true forms of acne and respectively refer to the skin conditions hidradenitis suppurativa (HS) and rosacea."}, {"text": "Although HS shares certain common features with acne vulgaris, such as a tendency to clog skin follicles with skin cell debris, the condition otherwise lacks the defining features of acne and is therefore considered a distinct skin disorder."}, {"list": ["increased secretion", "sebum"], "text": "Typical features of acne include increased secretion of oily sebum by the skin, microcomedones, comedones, papules, nodules (large papules), pustules, and often results in scarring."}, {"text": "The appearance of acne varies with skin color."}, {"text": "It may result in psychological and social problems."}, {"list": ["scars", "inflammation", "dermal layer of skin"], "text": "Acne scars are caused by inflammation within the dermal layer of skin and are estimated to affect 95% of people with acne vulgaris."}, {"text": "The scar is created by abnormal healing following this dermal inflammation."}, {"text": "Scarring is most likely to take place with severe acne, but may occur with any form of acne vulgaris."}, {"list": ["collagen"], "text": "Acne scars are classified based on whether the abnormal healing response following dermal inflammation leads to excess collagen deposition or loss at the site of the acne lesion."}, {"text": "Atrophic acne scars have lost collagen from the healing response and are the most common type of acne scar (account for approximately 75% of all acne scars)."}, {"text": "They may be further classified as ice-pick scars, boxcar scars, and rolling scars."}, {"list": ["mm"], "text": "Ice-pick scars are narrow (less than 2 mm across), deep scars that extend into the dermis."}, {"text": "Boxcar scars are round or ovoid indented scars with sharp borders and vary in size from 1.5–4 mm across."}, {"text": "Rolling scars are wider than icepick and boxcar scars (4–5 mm across) and have a wave-like pattern of depth in the skin."}, {"list": ["Hypertrophic scars"], "text": "Hypertrophic scars are uncommon, and are characterized by increased collagen content after the abnormal healing response."}, {"text": "They are described as firm and raised from the skin."}, {"list": ["keloid scars"], "text": "Hypertrophic scars remain within the original margins of the wound, whereas keloid scars can form scar tissue outside of these borders."}, {"text": "Keloid scars from acne occur more often in men and people with darker skin, and usually occur on the trunk of the body."}, {"list": ["Postinflammatory hyperpigmentation (PIH)"], "text": "Postinflammatory hyperpigmentation (PIH) is usually the result of nodular acne lesions."}, {"text": "These lesions often leave behind an inflamed darkened mark after the original acne lesion has resolved."}, {"list": ["melanocytes", "melanin"], "text": "This inflammation stimulates specialized pigment-producing skin cells (known as melanocytes) to produce more melanin pigment which leads to the skin\'s darkened appearance."}, {"list": ["darker skin color"], "text": "People with darker skin color are more frequently affected by this condition."}, {"text": "Pigmented scar is a common term used for PIH, but is misleading as it suggests the color change is permanent."}, {"text": "Often, PIH can be prevented by avoiding any aggravation of the nodule, and can fade with time."}, {"text": "However, untreated PIH can last for months, years, or even be permanent if deeper layers of skin are affected."}, {"list": ["ultraviolet rays"], "text": "Even minimal skin exposure to the sun\'s ultraviolet rays can sustain hyperpigmentation."}, {"list": ["SPF", "sunscreen"], "text": "Daily use of SPF 15 or higher sunscreen can minimize such a risk."}, {"text": "Risk factors for the development of acne, other than genetics, have not been conclusively identified."}, {"text": "Possible secondary contributors include hormones, infections, diet and stress."}, {"text": "Studies investigating the impact of smoking on the incidence and severity of acne have been inconclusive."}, {"text": "Sunlight and cleanliness are not associated with acne."}, {"list": ["twins", "first-degree relatives"], "text": "The predisposition to acne for specific individuals is likely explained by a genetic component, a theory which is supported by studies examining the rates of acne among twins and first-degree relatives."}, {"list": ["XYY syndrome"], "text": "Severe acne may be associated with XYY syndrome."}, {"list": ["due to the influence of multiple genes", "(Mendelian)"], "text": "Acne susceptibility is likely due to the influence of multiple genes, as the disease does not follow a classic (Mendelian) inheritance pattern."}, {"list": ["certain variations", "tumor necrosis factor-alpha (TNF-alpha)", "IL-1 alpha", "CYP1A1"], "text": "Multiple gene candidates have been proposed including certain variations in tumor necrosis factor-alpha (TNF-alpha), IL-1 alpha, and CYP1A1 genes, among others."}, {"list": ["single nucleotide polymorphism", "TNF"], "text": "Increased risk is associated with the 308 G/A single nucleotide polymorphism variation in the gene for TNF."}, {"list": ["menstrual cycles", "puberty"], "text": "Hormonal activity, such as occurs during menstrual cycles and puberty, may contribute to the formation of acne."}, {"list": ["androgens"], "text": "During puberty, an increase in sex hormones called androgens causes the skin follicle glands to grow larger and make more oily sebum."}, {"list": ["hormones", "dihydrotestosterone", "dehydroepiandrosterone", "growth hormone", "insulin-like growth factor 1"], "text": "Several hormones have been linked to acne, including the androgens testosterone, dihydrotestosterone (DHT), and dehydroepiandrosterone (DHEA); high levels of growth hormone (GH) and insulin-like growth factor 1 (IGF-1) have also been associated with worsened acne."}, {"list": ["complete androgen insensitivity syndrome", "Laron syndrome"], "text": "Both androgens and IGF-1 seem to be essential for acne to occur, as acne does not develop in individuals with complete androgen insensitivity syndrome (CAIS) or Laron syndrome (insensitivity to GH, resulting in very low IGF-1 levels)."}, {"list": ["polycystic ovary syndrome", "congenital adrenal hyperplasia", "androgen-secreting tumors"], "text": "Medical conditions that commonly cause a high-androgen state, such as polycystic ovary syndrome, congenital adrenal hyperplasia, and androgen-secreting tumors, can cause acne in affected individuals."}, {"list": ["lack androgenic hormones", "insensitive to the effects of androgens"], "text": "Conversely, people who lack androgenic hormones or are insensitive to the effects of androgens rarely have acne."}, {"text": "An increase in androgen and oily sebum synthesis can be seen during pregnancy."}, {"list": ["testosterone replacement therapy", "anabolic steroid"], "text": "Acne can be a side effect of testosterone replacement therapy or of anabolic steroid use."}, {"list": ["bodybuilding", "dietary supplements"], "text": "Over-the-counter bodybuilding and dietary supplements are commonly found to contain illegally added anabolic steroids."}, {"list": ["anaerobic bacterial", "Propionibacterium acnes\'\'"], "text": "It is widely suspected that the anaerobic bacterial species Propionibacterium acnes (\'\'P."}, {"text": "acnes\'\') contributes to the development of acne, but its exact role is not well understood."}, {"text": "There are specific sub-strains of \'\'P."}, {"text": "acnes\'\' associated with normal skin, and moderate or severe inflammatory acne."}, {"text": "It is unclear whether these undesirable strains evolve on-site or are acquired, or possibly both depending on the person."}, {"text": "These strains have the capability of changing, perpetuating, or adapting to the abnormal cycle of inflammation, oil production, and inadequate sloughing of dead skin cells from acne pores."}, {"list": ["Demodex\'\'"], "text": "Infection with the parasitic mite Demodex is associated with the development of acne."}, {"text": "It is unclear whether eradication of the mite improves acne."}, {"list": ["high-quality evidence"], "text": "The relationship between diet and acne is unclear, as there is no high-quality evidence that establishes any definitive link between them."}, {"list": ["glycemic-load"], "text": "High-glycemic-load diets have been found to have different degrees of effect on acne severity."}, {"list": ["randomized controlled trials"], "text": "Multiple randomized controlled trials and nonrandomized studies have found a lower-glycemic-load diet to be effective in reducing acne."}, {"text": "There is weak observational evidence suggesting that dairy milk consumption is positively associated with a higher frequency and severity of acne."}, {"list": ["whey protein"], "text": "Milk contains whey protein and hormones such as bovine IGF-1 and precursors of dihydrotestosterone."}, {"list": ["insulin"], "text": "These components are hypothesized to promote the effects of insulin and IGF-1 and thereby increase the production of androgen hormones, sebum, and promote the formation of comedones."}, {"list": ["chocolate", "salt"], "text": "Effects from other potentially contributing dietary factors, such as consumption of chocolate or salt, are not supported by the evidence."}, {"text": "Chocolate does contain varying amounts of sugar, which can lead to a high glycemic load, and it can be made with or without milk."}, {"list": ["obesity"], "text": "Few studies have examined the relationship between obesity and acne."}, {"list": ["Vitamin B 12 ", "recommended daily intake"], "text": "Vitamin B 12 may trigger skin outbreaks similar to acne (acneiform eruptions), or worsen existing acne, when taken in doses exceeding the recommended daily intake."}, {"text": "Eating greasy foods does not increase acne nor make it worse."}, {"text": "Few high-quality studies have been performed which demonstrate that stress causes or worsens acne."}, {"list": ["premenstrual syndrome"], "text": "While the connection between acne and stress has been debated, some research indicates that increased severity is associated with high stress levels in certain contexts such as hormonal changes seen in premenstrual syndrome."}, {"text": "Mechanical obstruction of skin follicles with helmets or chinstraps can worsen pre-existing acne."}, {"list": ["lithium", "hydantoin", "isoniazid", "glucocorticoids", "iodides", "bromides", "testosterone"], "text": "Several medications can worsen pre-existing acne, with examples being lithium, hydantoin, isoniazid, glucocorticoids, iodides, bromides, and testosterone."}, {"list": ["pilosebaceous unit", "hair follicles"], "text": "Acne vulgaris is a chronic skin disease of the pilosebaceous unit and develops due to blockages in the skin\'s hair follicles."}, {"list": ["sebum", "androgens", "excessive deposition of the protein keratin"], "text": "These blockages are thought to occur as a result of the following four abnormal processes: a higher than normal amount of oily sebum production (influenced by androgens), excessive deposition of the protein keratin leading to comedo formation, colonization of the follicle by Propionibacterium acnes (\'\'P."}, {"text": "acnes\'\') bacteria, and the local release of pro-inflammatory chemicals in the skin."}, {"list": ["microcomedone", "skin cells"], "text": "The earliest pathologic change is the formation of a plug (a microcomedone), which is driven primarily by excessive growth, reproduction, and accumulation of skin cells in the hair follicle."}, {"text": "In normal skin, the skin cells that have died come up to the surface and exit the pore of the hair follicle."}, {"text": "However, increased production of oily sebum in those with acne causes the dead skin cells to stick together."}, {"text": "The accumulation of dead skin cell debris and oily sebum blocks the pore of the hair follicle, thus forming the microcomedone."}, {"list": ["biofilm"], "text": "This is further exacerbated by the biofilm created by \'\'P."}, {"text": "acnes\'\' within the hair follicle."}, {"list": ["melanin", "oxidation"], "text": "If the microcomedone is superficial within the hair follicle, the skin pigment melanin is exposed to air, resulting in its oxidation and dark appearance (known as a blackhead or open comedo)."}, {"text": "In contrast, if the microcomedone occurs deep within the hair follicle, this causes the formation of a whitehead (known as a closed comedo)."}, {"list": ["dihydrotestosterone"], "text": "The main hormonal driver of oily sebum production in the skin is dihydrotestosterone."}, {"list": ["DHEA-S"], "text": "Another androgenic hormone responsible for increased sebaceous gland activity is DHEA-S."}, {"list": ["adrenarche", "puberty"], "text": "Higher amounts of DHEA-S are secreted during adrenarche (a stage of puberty), and this leads to an increase in sebum production."}, {"list": ["commensal"], "text": "In a sebum-rich skin environment, the naturally occurring and largely commensal skin bacterium \'\'P."}, {"list": ["inflammation", "innate immune system"], "text": "acnes\'\' readily grows and can cause inflammation within and around the follicle due to activation of the innate immune system."}, {"list": ["chemical signals", "IL-1α", "IL-8", "TNF-α"], "text": "P. acnes triggers skin inflammation in acne by increasing the production of several pro-inflammatory chemical signals (such as IL-1α, IL-8, TNF-α, and LTB4); IL-1α is known to be essential to comedo formation."}, {"text": "A major mechanism of acne-related skin inflammation is mediated by \'\'P."}, {"list": ["immune system", "toll-like receptors", "TLR2", "TLR4"], "text": "acnes\'\' ability to bind and activate a class of immune system receptors known as toll-like receptors (TLRs), especially TLR2 and TLR4."}, {"text": "Activation of TLR2 and TLR4 by \'\'P."}, {"text": "acnes\'\' leads to increased secretion of IL-1α, IL-8, and TNF-α."}, {"list": ["neutrophils", "macrophages", "Th1 cells"], "text": "Release of these inflammatory signals attracts various immune cells to the hair follicle including neutrophils, macrophages, and Th1 cells."}, {"text": "IL-1α stimulates increased skin cell activity and reproduction, which in turn fuels comedo development."}, {"list": ["antimicrobial peptides", "HBD1", "HBD2"], "text": "Furthermore, sebaceous gland cells produce more antimicrobial peptides, such as HBD1 and HBD2, in response to binding of TLR2 and TLR4."}, {"text": "P. acnes also provokes skin inflammation by altering the fatty composition of oily sebum."}, {"list": ["lipid", "squalene"], "text": "Oxidation of the lipid squalene by \'\'P."}, {"text": "acnes\'\' is of particular importance."}, {"list": ["NF-κB"], "text": "Squalene oxidation activates NF-κB (a protein complex) and consequently increases IL-1α levels."}, {"list": ["5-lipoxygenase", "arachidonic acid", "leukotriene B4"], "text": "Additionally, squalene oxidation leads to increased activity of the 5-lipoxygenase enzyme responsible for conversion of arachidonic acid to leukotriene B4 (LTB4)."}, {"list": ["peroxisome proliferator-activated receptor alpha"], "text": "LTB4 promotes skin inflammation by acting on the peroxisome proliferator-activated receptor alpha (PPARα) protein."}, {"list": ["activator protein 1", "T cells"], "text": "PPARα increases activity of activator protein 1 (AP-1) and NF-κB, thereby leading to the recruitment of inflammatory T cells."}, {"text": "The inflammatory properties of \'\'P."}, {"list": ["triglycerides", "free fatty acids", "lipase"], "text": "acnes\'\' can be further explained by the bacterium\'s ability to convert sebum triglycerides to pro-inflammatory free fatty acids via secretion of the enzyme lipase."}, {"list": ["cathelicidin"], "text": "These free fatty acids spur production of cathelicidin, HBD1, and HBD2, thus leading to further inflammation."}, {"list": ["papules", "pustules", "nodules"], "text": "This inflammatory cascade typically leads to the formation of inflammatory acne lesions, including papules, infected pustules, or nodules."}, {"list": ["subcutaneous tissue"], "text": "If the inflammatory reaction is severe, the follicle can break into the deeper layers of the dermis and subcutaneous tissue and cause the formation of deep nodules."}, {"list": ["matrix metalloproteinases"], "text": "Involvement of AP-1 in the aforementioned inflammatory cascade leads to activation of matrix metalloproteinases, which contribute to local tissue destruction and scar formation."}, {"text": "Comedones (blackheads and whiteheads) must be present to diagnose acne."}, {"text": "In their absence, an appearance similar to that of acne would suggest a different skin disorder."}, {"list": ["seen with a microscope"], "text": "Microcomedones (the precursor to blackheads and whiteheads) are not visible to the naked eye when inspecting the skin and can only be seen with a microscope."}, {"text": "There are many features that may indicate a person\'s acne vulgaris is sensitive to hormonal influences."}, {"text": "Historical and physical clues that may suggest hormone-sensitive acne include onset between ages 20 and 30; worsening the week before a woman\'s menstrual cycle; acne lesions predominantly over the jawline and chin; and inflammatory/nodular acne lesions."}, {"text": "Several scales exist to grade the severity of acne vulgaris, but no single technique has been universally accepted as the diagnostic standard."}, {"text": "Cook\'s acne grading scale uses photographs to grade severity from 0 to 8 (0 being the least severe and 8 being the most severe)."}, {"text": "This scale was the first to use a standardized photographic protocol to assess acne severity; since its creation in 1979, the scale has undergone several revisions."}, {"text": "The Leeds acne grading technique counts acne lesions on the face, back, and chest and categorizes them as inflammatory or non-inflammatory."}, {"text": "Leeds scores range from 0 (least severe) to 10 (most severe) though modified scales have a maximum score of 12. The Pillsbury acne grading scale simply classifies the severity of the acne from 1 (least severe) to 4 (most severe)."}, {"list": ["acneiform eruptions"], "text": "Many skin conditions can mimic acne vulgaris and are collectively known as acneiform eruptions."}, {"list": ["angiofibromas", "epidermal cysts", "flat warts", "folliculitis", "keratosis pilaris", "milia", "perioral dermatitis", "rosacea"], "text": "Such conditions include angiofibromas, epidermal cysts, flat warts, folliculitis, keratosis pilaris, milia, perioral dermatitis, and rosacea, among others."}, {"text": "Age is one factor which may help distinguish between these disorders."}, {"text": "Skin disorders such as perioral dermatitis and keratosis pilaris can appear similar to acne but tend to occur more frequently in childhood, whereas rosacea tends to occur more frequently in older adults."}, {"text": "Facial redness triggered by heat or the consumption of alcohol or spicy food is suggestive of rosacea."}, {"text": "The presence of comedones helps health professionals differentiate acne from skin disorders that are similar in appearance."}, {"list": ["Chloracne"], "text": "Chloracne, due to exposure to certain chemicals, may look very similar to acne vulgaris."}, {"text": "Many different treatments exist for acne."}, {"list": ["alpha hydroxy acid", "azelaic acid", "benzoyl peroxide", "hormonal", "keratolytic", "nicotinamide", "retinoids", "salicylic acid"], "text": "These include alpha hydroxy acid, anti-androgen medications, antibiotics, antiseborrheic medications, azelaic acid, benzoyl peroxide, hormonal treatments, keratolytic soaps, nicotinamide, retinoids, and salicylic acid."}, {"text": "They are believed to work in at least four different ways, including the following: reducing inflammation, hormonal manipulation, killing \'\'P."}, {"text": "acnes\'\', and normalizing skin cell shedding and sebum production in the pore to prevent blockage."}, {"text": "Common treatments include topical therapies such as antibiotics, benzoyl peroxide, and retinoids, and systemic therapies including antibiotics, hormonal agents, and oral retinoids."}, {"text": "Recommended therapies for first-line use in acne vulgaris treatment include topical retinoids, benzoyl peroxide, and topical or oral antibiotics."}, {"list": ["adjunctive"], "text": "Procedures such as light therapy and laser therapy are not considered to be first-line treatments and typically have an adjunctive role due to their high cost and limited evidence of efficacy."}, {"text": "Medications for acne work by targeting the early stages of comedo formation and are generally ineffective for visible skin lesions; improvement in the appearance of acne is typically expected between six and eight weeks after starting therapy."}, {"list": ["diet low in simple sugars"], "text": "A diet low in simple sugars is recommended as a method of improving acne."}, {"text": "As of 2014, evidence is insufficient to recommend milk restriction for this purpose."}, {"list": ["skin irritation"], "text": "Benzoyl peroxide (BPO) is a first-line treatment for mild and moderate acne due to its effectiveness and mild side-effects (mainly skin irritation)."}, {"text": "In the skin follicle, benzoyl peroxide kills \'\'P."}, {"list": ["free radicals", "benzoic acid"], "text": "acnes\'\' by oxidizing its proteins through the formation of oxygen free radicals and benzoic acid."}, {"text": "These free radicals are thought to interfere with the bacterium\'s metabolism and ability to make proteins."}, {"text": "Additionally, benzoyl peroxide is mildly effective at breaking down comedones and inhibiting inflammation."}, {"list": ["benzoyl peroxide/clindamycin", "benzoyl peroxide/adapalene"], "text": "Benzoyl peroxide may be paired with a topical antibiotic or retinoid such as benzoyl peroxide/clindamycin and benzoyl peroxide/adapalene, respectively."}, {"list": ["skin photosensitivity"], "text": "Side effects include increased skin photosensitivity, dryness, redness and occasional peeling."}, {"list": ["sunburn"], "text": "Sunscreen use is often advised during treatment, to prevent sunburn."}, {"text": "Lower concentrations of benzoyl peroxide are just as effective as higher concentrations in treating acne but are associated with fewer side effects."}, {"list": ["bacterial antibiotic resistance"], "text": "Unlike antibiotics, benzoyl peroxide does not appear to generate bacterial antibiotic resistance."}, {"list": ["Retinoids", "cell life cycle"], "text": "Retinoids are medications which reduce inflammation, normalize the follicle cell life cycle, and reduce sebum production."}, {"list": ["vitamin A"], "text": "They are structurally related to vitamin A."}, {"text": "The retinoids appear to influence the cell life cycle in the follicle lining."}, {"list": ["accumulation of skin cells"], "text": "This helps prevent the accumulation of skin cells within the hair follicle that can create a blockage."}, {"text": "They are a first-line acne treatment, especially for people with dark-colored skin, and are known to lead to faster improvement of postinflammatory hyperpigmentation."}, {"list": ["adapalene", "isotretinoin", "retinol", "tazarotene", "tretinoin"], "text": "Frequently used topical retinoids include adapalene, isotretinoin, retinol, tazarotene, and tretinoin."}, {"list": ["flushing"], "text": "They often cause an initial flare-up of acne and facial flushing, and can cause significant skin irritation."}, {"text": "Generally speaking, retinoids increase the skin\'s sensitivity to sunlight and are therefore recommended for use at night."}, {"text": "Tretinoin is the least expensive of the topical retinoids and is the most irritating to the skin, whereas adapalene is the least irritating to the skin but costs significantly more."}, {"text": "Tazarotene is the most effective and expensive topical retinoid, but is not as well-tolerated."}, {"text": "Retinol is a form of vitamin A that has similar but milder effects, and is used in many over-the-counter moisturizers and other topical products."}, {"text": "Isotretinoin is an oral retinoid that is very effective for severe nodular acne, and moderate acne that is stubborn to other treatments."}, {"text": "One to two months use is typically adequate to see improvement."}, {"text": "Acne often resolves completely or is much milder after a 4–6 month course of oral isotretinoin."}, {"text": "After a single course, about 80% of people report an improvement, with more than 50% reporting complete remission."}, {"text": "About 20% of patients require a second course."}, {"list": ["depression", "suicidality", "anemia"], "text": "Concerns have emerged that isotretinoin use is linked with an increased risk of adverse effects, like depression, suicidality, anemia, although there is no clear evidence to support some of these claims."}, {"text": "Isotretinoin is superior to antibiotics or placebo in reducing acne lesions."}, {"text": "The frequency of adverse events was about twice as high with isotretinoin, although these were mostly dryness-related events."}, {"text": "No increased risk of suicide or depression was conclusively found."}, {"list": ["harmful effects in pregnancy"], "text": "Isotretinoin use in women of childbearing age is regulated due to its known harmful effects in pregnancy."}, {"list": ["pregnancy test", "birth control"], "text": "For such a woman to be considered a candidate for isotretinoin, she must have a confirmed negative pregnancy test and use an effective form of birth control."}, {"list": ["iPLEDGE"], "text": "In 2008, the United States started the iPLEDGE program to prevent isotretinoin use during pregnancy."}, {"text": "iPledge requires the woman under consideration for isotretinoin therapy to have two negative pregnancy tests and mandates the use of two types of birth control for at least one month before therapy begins and one month after therapy is complete."}, {"text": "The effectiveness of the iPledge program has been questioned due to continued instances of contraception nonadherence."}, {"text": "Antibiotics are frequently applied to the skin or taken orally to treat acne and are thought to work due to their antimicrobial activity against \'\'P."}, {"text": "acnes\'\' and their ability to reduce inflammation."}, {"text": "With the widespread use of antibiotics for acne and an increased frequency of antibiotic-resistant \'\'P."}, {"list": ["macrolide"], "text": "acnes\'\' worldwide, antibiotics are becoming less effective, especially macrolide antibiotics such as topical erythromycin."}, {"list": ["clindamycin", "erythromycin", "metronidazole", "sulfacetamide", "tetracyclines", "doxycycline", "minocycline"], "text": "Commonly used antibiotics, either applied to the skin or taken orally, include clindamycin, erythromycin, metronidazole, sulfacetamide, and tetracyclines such as doxycycline and minocycline."}, {"text": "When antibiotics are applied to the skin, they are typically used for mild to moderately severe acne."}, {"text": "Antibiotics taken orally are generally considered to be more effective than topical antibiotics, and produce faster resolution of inflammatory acne lesions than topical applications."}, {"text": "Topical and oral antibiotics are not recommended for use together."}, {"text": "Oral antibiotics are recommended for no longer than three months as antibiotic courses exceeding this duration are associated with the development of antibiotic resistance and show no clear benefit over shorter courses."}, {"text": "Furthermore, if long-term oral antibiotics beyond three months are thought to be necessary, it is recommended that benzoyl peroxide and/or a retinoid be used at the same time to limit the risk of \'\'P."}, {"text": "acnes\'\' developing antibiotic resistance."}, {"list": ["Dapsone"], "text": "Dapsone is not a first-line topical antibiotic due to higher cost and lack of clear superiority over other antibiotics."}, {"text": "Topical dapsone is not recommended for use with benzoyl peroxide due to yellow-orange skin discoloration with this combination."}, {"list": ["combined birth control pill"], "text": "In women, acne can be improved with the use of any combined birth control pill."}, {"text": "These decrease the production of androgen hormones by the ovaries, resulting in lower skin production of sebum, and consequently reduce acne severity."}, {"list": ["progestins", "desogestrel", "drospirenone", "norgestimate"], "text": "Combinations containing third- or fourth-generation progestins such as desogestrel, drospirenone, or norgestimate are preferred for women with acne due to their stronger antiandrogenic effects."}, {"list": ["review"], "text": "A 2014 review found that oral antibiotics appear to be somewhat more effective than birth control pills at decreasing the number of inflammatory acne lesions at three months."}, {"text": "However, the two therapies are approximately equal in efficacy at six months for decreasing the number of inflammatory, non-inflammatory, and total acne lesions."}, {"text": "The authors of the analysis suggested that birth control pills may be a preferred first-line acne treatment, over oral antibiotics, in certain women due to similar efficacy at six months and a lack of associated antibiotic resistance."}, {"list": ["Antiandrogens", "cyproterone acetate", "spironolactone", "hairiness", "baldness"], "text": "Antiandrogens such as cyproterone acetate and spironolactone have been used successfully to treat acne, especially in women with signs of excessive androgen production such as increased hairiness or skin production of sebum, or baldness."}, {"list": ["Food and Drug Administration"], "text": "Spironolactone is an effective treatment for acne in adult women, but unlike combination oral contraceptives, is not approved by the United States Food and Drug Administration for this purpose."}, {"list": ["aldosterone antagonist", "androgen receptor"], "text": "The drug is primarily used as an aldosterone antagonist and is thought to be a useful acne treatment due to its ability to block the androgen receptor at higher doses."}, {"text": "It may be used with or without an oral contraceptive."}, {"list": ["birth disorders", "hypospadias", "feminization", "fetus"], "text": "Hormonal therapies should not be used to treat acne during pregnancy or lactation as they have been associated with birth disorders such as hypospadias, and feminization of the male fetus or infant."}, {"list": ["Finasteride"], "text": "Finasteride is likely an effective treatment for acne."}, {"list": ["Azelaic acid"], "text": "Azelaic acid has been shown to be effective for mild to moderate acne when applied topically at a 20% concentration."}, {"list": ["antibacterial", "anti-inflammatory"], "text": "Treatment twice daily for six months is necessary, and is as effective as topical benzoyl peroxide 5%, isotretinoin 0.05%, and erythromycin 2%. Azelaic acid is thought to be an effective acne treatment due to its ability to reduce skin cell accumulation in the follicle, and its antibacterial and anti-inflammatory properties."}, {"text": "It has a slight skin-lightening effect due to its ability to inhibit melanin synthesis, and is therefore useful in treating of individuals with acne who are also affected by postinflammatory hyperpigmentation."}, {"text": "Azelaic acid may cause skin irritation but is otherwise very safe."}, {"text": "It is less effective and more expensive than retinoids."}, {"list": ["Salicylic acid", "beta-hydroxy acid", "stops bacteria from reproducing"], "text": "Salicylic acid is a topically applied beta-hydroxy acid that stops bacteria from reproducing and has keratolytic properties."}, {"text": "It opens obstructed skin pores and promotes shedding of epithelial skin cells."}, {"text": "Salicylic acid is known to be less effective than retinoid therapy."}, {"list": ["Dry skin", "darkening"], "text": "Dry skin is the most commonly seen side effect with topical application, though darkening of the skin has been observed in individuals with darker skin types."}, {"list": ["nicotinamide", "amide", "vitamin B 3 "], "text": "Topical and oral preparations of nicotinamide (the amide form of vitamin B 3 ) have been suggested as alternative medical treatments."}, {"text": "It is thought to improve acne due to its anti-inflammatory properties, its ability to suppress sebum production, and by promoting wound healing."}, {"text": "Topical and oral preparations of zinc have similarly been proposed as effective treatments for acne; evidence to support their use for this purpose is limited."}, {"text": "The purported efficacy of zinc is attributed to its capacity to reduce inflammation and sebum production, and inhibit \'\'P."}, {"text": "acnes\'\'."}, {"list": ["Antihistamines"], "text": "Antihistamines may improve symptoms among those already taking isotretinoin due to their anti-inflammatory properties and their ability to suppress sebum production."}, {"list": ["Hydroquinone", "tyrosinase", "tyrosine", "melanin"], "text": "Hydroquinone lightens the skin when applied topically by inhibiting tyrosinase, the enzyme responsible for converting the amino acid tyrosine to the skin pigment melanin, and is used to treat acne-associated postinflammatory hyperpigmentation."}, {"list": ["epidermis"], "text": "By interfering with new production of melanin in the epidermis, hydroquinone leads to less hyperpigmentation as darkened skin cells are naturally shed over time."}, {"text": "Improvement in skin hyperpigmentation is typically seen within six months when used twice daily."}, {"list": ["dermis"], "text": "Hydroquinone is ineffective for hyperpigmentation affecting deeper layers of skin such as the dermis."}, {"list": ["sunscreen", "SPF"], "text": "The use of a sunscreen with SPF 15 or higher in the morning with reapplication every two hours is recommended when using hydroquinone."}, {"text": "Its application only to affected areas lowers the risk of lightening the color of normal skin but can lead to a temporary ring of lightened skin around the hyperpigmented area."}, {"text": "Hydroquinone is generally well-tolerated; side effects are typically mild (e.g., skin irritation) and occur with use of a higher than the recommended 4% concentration."}, {"list": ["sodium metabisulfite", "allergic reactions", "anaphylaxis", "asthma"], "text": "Most preparations contain the preservative sodium metabisulfite, which has been linked to rare cases of allergic reactions including anaphylaxis and severe asthma exacerbations in susceptible people."}, {"list": ["exogenous ochronosis"], "text": "In extremely rare cases, repeated improper topical application of high-dose hydroquinone has been associated with an accumulation of homogentisic acid in connective tissues, a condition known as exogenous ochronosis."}, {"text": "Combination therapy—using medications of different classes together, each with a different mechanism of action—has been demonstrated to be a more efficacious approach to acne treatment than monotherapy."}, {"text": "The use of topical benzoyl peroxide and antibiotics together has been shown to be more effective than antibiotics alone."}, {"text": "Similarly, using a topical retinoid with an antibiotic clears acne lesions faster than the use of antibiotics alone."}, {"text": "Frequently used combinations include the following: antibiotic and benzoyl peroxide, antibiotic and topical retinoid, or topical retinoid and benzoyl peroxide."}, {"text": "The pairing of benzoyl peroxide with a retinoid is preferred over the combination of a topical antibiotic with a retinoid since both regimens are effective but benzoyl peroxide does not lead to antibiotic resistance."}, {"text": "Although the late stages of pregnancy are associated with an increase in sebaceous gland activity in the skin, pregnancy has not been reliably associated with worsened acne severity."}, {"list": ["fetus"], "text": "In general, topically applied medications are considered the first-line approach to acne treatment during pregnancy, as they have little systemic absorption and are therefore unlikely to harm a developing fetus."}, {"list": ["too little amniotic fluid in the uterus", "ductus arteriosus"], "text": "Highly recommended therapies include topically applied benzoyl peroxide (category C) and azelaic acid (category B). Salicylic acid carries a category C safety rating due to higher systemic absorption (9–25%), and an association between the use of anti-inflammatory medications in the third trimester and adverse effects to the developing fetus including too little amniotic fluid in the uterus and early closure of the babies\' ductus arteriosus blood vessel."}, {"list": ["occlusive dressings"], "text": "Prolonged use of salicylic acid over significant areas of the skin or under occlusive dressings is not recommended as these methods increase systemic absorption and the potential for fetal harm."}, {"list": ["teratogenic"], "text": "Tretinoin (category C) and adapalene (category C) are very poorly absorbed, but certain studies have suggested teratogenic effects in the first trimester."}, {"text": "Due to persistent safety concerns, topical retinoids are not recommended for use during pregnancy."}, {"text": "In studies examining the effects of topical retinoids during pregnancy, fetal harm has not been seen in the second and third trimesters."}, {"list": ["acitretin"], "text": "Retinoids contraindicated for use during pregnancy include the topical retinoid tazarotene, and oral retinoids isotretinoin and acitretin (all category X). Spironolactone is relatively contraindicated for use during pregnancy due to its antiandrogen effects."}, {"text": "Finasteride is not recommended as it is highly teratogenic."}, {"text": "Topical antibiotics deemed safe during pregnancy include clindamycin, erythromycin, and metronidazole (all category B), due to negligible systemic absorption."}, {"list": ["Nadifloxacin"], "text": "Nadifloxacin and dapsone (category C) are other topical antibiotics that may be used to treat acne in pregnant women, but have received less study."}, {"text": "No adverse fetal events have been reported from the topical use of dapsone."}, {"list": ["birth control"], "text": "If retinoids are used there is a high risk of abnormalities occurring in the developing fetus; women of childbearing age are therefore required to use effective birth control if retinoids are used to treat acne."}, {"list": ["azithromycin", "cephalosporins", "penicillins"], "text": "Oral antibiotics deemed safe for pregnancy (all category B) include azithromycin, cephalosporins, and penicillins."}, {"list": ["thinned tooth enamel"], "text": "Tetracyclines (category D) are contraindicated during pregnancy as they are known to deposit in developing fetal teeth, resulting in yellow discoloration and thinned tooth enamel."}, {"list": ["acute fatty liver of pregnancy"], "text": "Their use during pregnancy has been associated with development of acute fatty liver of pregnancy and is further avoided for this reason."}, {"list": ["Comedo extraction"], "text": "Comedo extraction is supported by limited evidence but is recommended for comedones that do not improve with standard treatment."}, {"text": "Another procedure for immediate relief is injection of a corticosteroid into an inflamed acne comedo."}, {"list": ["Electrocautery", "electrofulguration"], "text": "Electrocautery and electrofulguration have also been reported as effective treatments for comedones."}, {"list": ["Light therapy"], "text": "Light therapy is a treatment method that involves delivering certain specific wavelengths of light to an area of skin affected by acne."}, {"list": ["laser light"], "text": "Both regular and laser light have been used."}, {"list": ["sensitizing substance", "aminolevulinic acid", "methyl aminolevulinate", "photodynamic therapy"], "text": "When regular light is used immediately following the application of a sensitizing substance to the skin such as aminolevulinic acid or methyl aminolevulinate, the treatment is referred to as photodynamic therapy (PDT)."}, {"text": "PDT has the most supporting evidence of all light therapies."}, {"text": "Many different types of nonablative lasers (i.e., lasers that do not vaporize the top layer of the skin but rather induce a physiologic response in the skin from the light) have been used to treat acne, including those that use infrared wavelengths of light."}, {"list": ["CO 2 "], "text": "Ablative lasers (such as CO 2 and fractional types) have also been used to treat active acne and its scars."}, {"list": ["laser resurfacing"], "text": "When ablative lasers are used, the treatment is often referred to as laser resurfacing because, as mentioned previously, the entire upper layers of the skin are vaporized."}, {"text": "Ablative lasers are associated with higher rates of adverse effects compared with nonablative lasers, with examples being postinflammatory hyperpigmentation, persistent facial redness, and persistent pain."}, {"text": "Physiologically, certain wavelengths of light, used with or without accompanying topical chemicals, are thought to kill bacteria and decrease the size and activity of the glands that produce sebum."}, {"text": "As of 2012, evidence for various light therapies was insufficient to recommend them for routine use."}, {"text": "Disadvantages of light therapy can include its cost, the need for multiple visits, time required to complete the procedure(s), and pain associated with some of the treatment modalities."}, {"text": "Various light therapies appear to provide a short-term benefit, but data for long-term outcomes, and for outcomes in those with severe acne, are sparse; it may have a role for individuals whose acne has been resistant to topical medications."}, {"list": ["skin peeling"], "text": "Typical side effects include skin peeling, temporary reddening of the skin, swelling, and postinflammatory hyperpigmentation."}, {"list": ["Dermabrasion"], "text": "Dermabrasion is an effective therapeutic procedure for reducing the appearance of superficial atrophic scars of the boxcar and rolling varieties."}, {"text": "Ice-pick scars do not respond well to treatment with dermabrasion due to their depth."}, {"list": ["redness", "decreased pigmentation of the skin"], "text": "The procedure is painful and has many potential side effects such as skin sensitivity to sunlight, redness, and decreased pigmentation of the skin."}, {"text": "Dermabrasion has fallen out of favor with the introduction of laser resurfacing."}, {"list": ["microdermabrasion"], "text": "Unlike dermabrasion, there is no evidence that microdermabrasion is an effective treatment for acne."}, {"list": ["Microneedling"], "text": "Microneedling is a procedure in which an instrument with multiple rows of tiny needles is rolled over the skin to elicit a wound healing response and stimulate collagen production to reduce the appearance of atrophic acne scars in people with darker skin color."}, {"text": "Notable adverse effects of microneedling include postinflammatory hyperpigmentation and tram track scarring (described as discrete slightly raised scars in a linear distribution similar to a tram track)."}, {"text": "The latter is thought to be primarily attributable to improper technique by the practitioner, including the use of excessive pressure or inappropriately large needles."}, {"list": ["Subcision"], "text": "Subcision is useful for treatment of superficial atrophic acne scars and involves the use of a small needle to loosen the fibrotic adhesions that result in the depressed appearance of the scar."}, {"list": ["Chemical peels"], "text": "Chemical peels can be used to reduce the appearance of acne scars."}, {"list": ["glycolic acid", "lactic acid", "salicylic acid", "Jessner\'s solution", "trichloroacetic acid"], "text": "Mild peels include those using glycolic acid, lactic acid, salicylic acid, Jessner\'s solution, or a lower concentrations (20%) of trichloroacetic acid."}, {"list": ["epidermal layer of the skin"], "text": "These peels only affect the epidermal layer of the skin and can be useful in the treatment of superficial acne scars as well as skin pigmentation changes from inflammatory acne."}, {"list": ["papillary dermis"], "text": "Higher concentrations of trichloroacetic acid (30–40%) are considered to be medium-strength peels and affect skin as deep as the papillary dermis."}, {"text": "Formulations of trichloroacetic acid concentrated to 50% or more are considered to be deep chemical peels."}, {"list": ["small white superficial cysts known as milia"], "text": "Medium-strength and deep-strength chemical peels are more effective for deeper atrophic scars, but are more likely to cause side effects such as skin pigmentation changes, infection, and small white superficial cysts known as milia."}, {"text": "Complementary therapies have been investigated for treating people with acne."}, {"list": ["Low-quality evidence", "tea tree oil", "bee venom"], "text": "Low-quality evidence suggests topical application of tea tree oil or bee venom may reduce the total number of skin lesions in those with acne."}, {"list": ["allergic contact dermatitis"], "text": "Tea tree oil is thought to be approximately as effective as benzoyl peroxide or salicylic acid, but has been associated with allergic contact dermatitis."}, {"text": "Proposed mechanisms for tea tree oil\'s anti-acne effects include antibacterial action against \'\'P."}, {"text": "acnes,\'\' and anti-inflammatory properties."}, {"list": ["basil oil", "oligosaccharides", "seaweed"], "text": "Numerous other plant-derived therapies have been observed to have positive effects against acne (e.g., basil oil and oligosaccharides from seaweed); however, few studies have been performed, and most have been of lower methodological quality."}, {"list": ["acupuncture", "herbal medicine", "cupping therapy"], "text": "There is a lack of high-quality evidence for the use of acupuncture, herbal medicine, or cupping therapy for acne."}, {"list": ["cosmeceuticals"], "text": "Many over-the-counter treatments in many forms are available, which are often referred to as cosmeceuticals."}, {"list": ["makeup"], "text": "Certain types of makeup may be useful to mask acne."}, {"text": "In those with oily skin, a water-based product is often preferred."}, {"text": "Acne usually improves around the age of 20, but may persist into adulthood."}, {"text": "Permanent physical scarring may occur."}, {"list": ["anxiety disorders", "depression", "suicidal thoughts"], "text": "There is good evidence to support the idea that acne and associated scarring negatively impact a person\'s psychological state, worsen mood, lower self-esteem, and are associated with a higher risk of anxiety disorders, depression, and suicidal thoughts."}, {"list": ["acne excoriée"], "text": "Another psychological complication of acne vulgaris is acne excoriée, which occurs when a person persistently picks and scratches pimples, irrespective of the severity of their acne."}, {"text": "This can lead to significant scarring, changes in the affected person\'s skin pigmentation, and a cyclic worsening of the affected person\'s anxiety about their appearance."}, {"list": ["pyogenic granulomas", "osteoma cutis", "solid facial edema"], "text": "Rare complications from acne or its treatment include the formation of pyogenic granulomas, osteoma cutis, and solid facial edema."}, {"text": "Early and aggressive treatment of acne is advocated by some in the medical community to reduce the chances of these poor outcomes."}, {"text": "Globally, acne affects approximately 650 million people, or about 9.4% of the population, as of 2010."}, {"text": "It affects nearly 90% of people in Western societies during their teenage years, but can occur before adolescence and may persist into adulthood."}, {"text": "While acne that first develops between the ages of 21 and 25 is uncommon, it affects 54% of women and 40% of men older than 25 years of age, and has a lifetime prevalence of 85%."}, {"text": "About 20% of those affected have moderate or severe cases."}, {"text": "It is slightly more common in females than males (9.8% versus 9.0%)."}, {"text": "In those over 40 years old, 1% of males and 5% of females still have problems."}, {"text": "Rates appear to be lower in rural societies."}, {"list": ["Papua New Guinea", "Paraguay"], "text": "While some research has found it affects people of all ethnic groups, acne may not occur in the non-Westernized peoples of Papua New Guinea and Paraguay."}, {"list": ["United States"], "text": "Acne affects 40–50 million people in the United States (16%) and approximately 3–5 million in Australia (23%)."}, {"text": "Severe acne tends to be more common in people of Caucasian or Hispanic descent than in people of African descent."}, {"text": "Pharaohs are recorded as having had acne, which may be the earliest known reference to the disease."}, {"list": ["Cleopatra", "sulfur"], "text": "Since at least the reign of Cleopatra (69–30 BC), the application of sulfur to the skin has been recognized as a useful treatment for acne."}, {"list": ["Greek", "Aëtius of Amida", "puberty"], "text": "The sixth-century Greek physician Aëtius of Amida is credited with coining the term \\"ionthos\\" (ίονθωξ,) or \\"acnae\\", which is believed to have been a reference to facial skin lesions that occur during \\"the \'\' of life\\" (puberty)."}, {"list": ["François Boissier de Sauvages de Lacroix"], "text": "In the 16th century, the French physician and botanist François Boissier de Sauvages de Lacroix provided one of the earlier descriptions of acne."}, {"list": ["tubercles"], "text": "He used the term \\"psydracia achne\\" to describe small, red and hard tubercles that altered a person\'s facial appearance during adolescence, and were neither itchy nor painful."}, {"list": ["Josef Plenck", "Austrian"], "text": "The recognition and characterization of acne progressed in 1776 when Josef Plenck (an Austrian physician) published a book that proposed the novel concept of classifying skin diseases by their elementary (initial) lesions."}, {"list": ["English", "dermatologist", "Robert Willan"], "text": "In 1808 the English dermatologist Robert Willan refined Plenck\'s work by providing the first detailed descriptions of several skin disorders using a morphologic terminology that remains in use today."}, {"list": ["Thomas Bateman"], "text": "Thomas Bateman continued and expanded on Robert Willan\'s work as his student and provided the first descriptions and illustrations of acne accepted as accurate by modern dermatologists."}, {"list": ["Erasmus Wilson"], "text": "Erasmus Wilson, in 1842, was the first to make the distinction between acne vulgaris and rosacea."}, {"list": ["monograph"], "text": "The first professional medical monograph dedicated entirely to acne was published in New York in 1885."}, {"text": "Scientists initially hypothesized that acne represented a disease of the skin\'s hair follicle, and occurred due to blockage of the pore by sebum."}, {"text": "During the 1880s, bacteria were observed by microscopy in skin samples affected by acne and were regarded as the causal agents of comedones, sebum production, and ultimately acne."}, {"text": "During the mid-twentieth century, dermatologists realized that no single hypothesized factor (sebum, bacteria, or excess keratin) could completely explain the disease."}, {"text": "This led to the current understanding that acne could be explained by a sequence of related events, beginning with blockage of the skin follicle by excessive dead skin cells, followed by bacterial invasion of the hair follicle pore, changes in sebum production, and inflammation."}, {"text": "The approach to acne treatment underwent significant changes during the twentieth century."}, {"text": "Retinoids were introduced as a medical treatment for acne in 1943."}, {"text": "Benzoyl peroxide was first proposed as a treatment in 1958 and has been routinely used for this purpose since the 1960s."}, {"text": "Acne treatment was modified in the 1950s with the introduction of oral tetracycline antibiotics (such as minocycline)."}, {"text": "These reinforced the idea amongst dermatologists that bacterial growth on the skin plays an important role in causing acne."}, {"list": ["tretinoin"], "text": "Subsequently, in the 1970s tretinoin (original trade name Retin A) was found to be an effective treatment."}, {"list": ["isotretinoin"], "text": "The development of oral isotretinoin (sold as Accutane and Roaccutane) followed in 1980."}, {"text": "After its introduction in the United States it was recognized as a medication highly likely to cause birth defects if taken during pregnancy."}, {"list": ["abortion", "miscarriage"], "text": "In the United States, more than 2,000 women became pregnant while taking isotretinoin between 1982 and 2003, with most pregnancies ending in abortion or miscarriage."}, {"list": ["birth defects"], "text": "About 160 babies were born with birth defects."}, {"text": "Treatment of acne with topical crushed dry ice (termed \\"cryoslush\\") was first described in 1907, but is no longer performed commonly."}, {"text": "Prior to 1960, the use of X-rays was also a common treatment."}, {"text": "The costs and social impact of acne are substantial."}, {"list": ["doctor", "direct costs"], "text": "In the United States, acne vulgaris is responsible for more than 5 million doctor visits and costs over billion each year in direct costs."}, {"list": ["United Kingdom"], "text": "Similarly, acne vulgaris is responsible for 3.5 million doctor visits each year in the United Kingdom."}, {"text": "Sales for the top ten leading acne treatment brands in the US in 2015, have been reported as amounting to $352 million."}, {"text": "Misperceptions about acne\'s causative and aggravating factors are common, and those affected by it are often blamed for their condition."}, {"text": "Such blame can worsen the affected person\'s sense of self-esteem."}, {"list": ["masturbation"], "text": "Until the 20th century, even among dermatologists, the list of causes was believed to include excessive sexual thoughts and masturbation."}, {"list": ["sexually transmitted infections", "syphilis"], "text": "Dermatology\'s association with sexually transmitted infections, especially syphilis, contributed to the stigma."}, {"text": "Acne vulgaris and its resultant scars have been associated with significant social and academic difficulties that can last into adulthood, including difficulties obtaining employment."}, {"list": ["smallpox", "tuberculosis", "Great Depression", "World War II", "tropical acne", "unfit for duty"], "text": "Until the 1930s, it was largely seen as a trivial problem among middle-class girls – a trivial problem, because, unlike smallpox and tuberculosis, no one died from it, and a feminine problem, because boys were much less likely to seek medical assistance for it. During the Great Depression, dermatologists discovered that young men with acne had difficulty obtaining jobs, and during World War II, some soldiers in tropical climates developed such severe and widespread tropical acne on their bodies that they were declared medically unfit for duty."}, {"text": "Efforts to better understand the mechanisms of sebum production are underway."}, {"list": ["IGF-1", "alpha-melanocyte-stimulating hormone"], "text": "The aim of this research is to develop medications that target and interfere with the hormones that are known to increase sebum production (e.g., IGF-1 and alpha-melanocyte-stimulating hormone)."}, {"list": ["peroxisome proliferator-activated receptor"], "text": "Additional sebum-lowering medications being researched include topical antiandrogens and peroxisome proliferator-activated receptor modulators."}, {"text": "Another avenue of early-stage research has focused on how to best use laser and light therapy to selectively destroy sebum-producing glands in the skin\'s hair follicles in order to reduce sebum production and improve acne appearance."}, {"list": ["antimicrobial peptides"], "text": "The use of antimicrobial peptides against \'\'P."}, {"text": "acnes\'\' is under investigation as a treatment for acne to overcoming antibiotic resistance."}, {"list": ["genome", "sequencing"], "text": "In 2007, the first genome sequencing of a \'\'P."}, {"list": ["bacteriophage"], "text": "acnes\'\' bacteriophage (PA6) was reported."}, {"list": ["bacteriophage therapy"], "text": "The authors proposed applying this research toward development of bacteriophage therapy as an acne treatment in order to overcome the problems associated with long-term antibiotic therapy such as bacterial resistance."}, {"list": ["probiotics"], "text": "Oral and topical probiotics are also being evaluated as treatments for acne."}, {"list": ["ceramide"], "text": "Probiotics have been hypothesized to have therapeutic effects for those affected by acne due to their ability to decrease skin inflammation and improve skin moisture by increasing the skin\'s ceramide content."}, {"text": "As of 2014, studies examining the effects of probiotics on acne in humans were limited."}, {"list": ["retinoic acid"], "text": "Decreased levels of retinoic acid in the skin may contribute to comedo formation."}, {"text": "To address this deficiency, methods to increase the skin\'s production of retinoid acid are being explored."}, {"list": ["vaccine"], "text": "A vaccine against inflammatory acne has shown promising results in mice and humans."}, {"text": "Some have voiced concerns about creating a vaccine designed to neutralize a stable community of normal skin bacteria that is known to protect the skin from colonization by more harmful microorganisms."}, {"text": "Acne can occur on cats, dogs, and horses."}, {"list": ["Questions and Answers about Acne"], "text": "* Questions and Answers about Acne - US National Institute of Arthritis and Musculoskeletal and Skin Diseases"}]', '{}', '{"list": ["Anxiety", " reduced self-esteem", " depression", " thoughts of suicide"], "conditions": ["Anxiety", "self-esteem", "depression", "thoughts of suicide"]}', '{"desc": "Azelaic acid, benzoyl peroxide, salicylic acid, antibiotics, birth control pills, isotretinoin", "list": ["Azelaic acid", "benzoyl peroxide", "salicylic acid", "antibiotics", "birth control pills", "isotretinoin"]}', '[{"url": "https://upload.wikimedia.org/wikipedia/commons/f/f0/Acne_vulgaris_on_a_very_oily_skin.jpg", "file": "File:Acne vulgaris on a very oily skin.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/f/f0/Acne_vulgaris_on_a_very_oily_skin.jpg/300px-Acne_vulgaris_on_a_very_oily_skin.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/3/39/Comedo.png", "file": "File:Comedo.png", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/3/39/Comedo.png/300px-Comedo.png"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/2/25/Blausen_0811_SkinPores.png", "file": "File:Blausen 0811 SkinPores.png", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/2/25/Blausen_0811_SkinPores.png/300px-Blausen_0811_SkinPores.png"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/e/eb/Acne_Pathogenesis_Medscape.png", "file": "File:Acne Pathogenesis Medscape.png", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/e/eb/Acne_Pathogenesis_Medscape.png/300px-Acne_Pathogenesis_Medscape.png"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/4/4a/Benzoyl_peroxide_gel.jpg", "file": "File:Benzoyl peroxide gel.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/4/4a/Benzoyl_peroxide_gel.jpg/300px-Benzoyl_peroxide_gel.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/e/e5/Jar_of_\'Domolene\'_ointment%2C_London%2C_England%2C_1945-1965_Wellcome_L0058221.jpg", "file": "File:Jar of \'Domolene\' ointment, London, England, 1945-1965 Wellcome L0058221.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/e/e5/Jar_of_\'Domolene\'_ointment%2C_London%2C_England%2C_1945-1965_Wellcome_L0058221.jpg/300px-Jar_of_\'Domolene\'_ointment%2C_London%2C_England%2C_1945-1965_Wellcome_L0058221.jpg"}]');
INSERT INTO `entity_data` (`id`, `entity`, `name`, `caption`, `type`, `symptoms`, `diagnosis`, `causes`, `parse_data`, `prevention`, `complications`, `medication`, `images`) VALUES
(6, 'chicken_pox', 'Chickenpox', 'Male%20with%20varicella%20disease', 'Infectious%20disease', '["Small", "itchy blisters", "headache", "loss of appetite", "tiredness"]', '{}', '{"desc": "Varicella zoster virus", "list": ["Varicella zoster virus"]}', '[{"list": ["contagious", "infection", "varicella zoster virus"], "text": "Chickenpox, also known as varicella, is a highly contagious disease caused by the initial infection with varicella zoster virus (VZV)."}, {"list": ["small, itchy blisters"], "text": "The disease results in a characteristic skin rash that forms small, itchy blisters, which eventually scab over."}, {"text": "It usually starts on the chest, back, and face then spreads to the rest of the body."}, {"list": ["tiredness", "headaches"], "text": "Other symptoms may include fever, tiredness, and headaches."}, {"text": "Symptoms usually last five to seven days."}, {"list": ["pneumonia", "inflammation of the brain"], "text": "Complications may occasionally include pneumonia, inflammation of the brain, and bacterial skin infections."}, {"text": "The disease is often more severe in adults than in children."}, {"text": "Symptoms begin 10 to 21 days after exposure to the virus."}, {"list": ["airborne disease"], "text": "Chickenpox is an airborne disease which spreads easily through the coughs and sneezes of an infected person."}, {"text": "It may be spread from one to two days before the rash appears until all lesions have crusted over."}, {"text": "It may also spread through contact with the blisters."}, {"list": ["shingles"], "text": "Those with shingles may spread chickenpox to those who are not immune through contact with the blisters."}, {"list": ["polymerase chain reaction"], "text": "The disease can usually be diagnosed based on the presenting symptom; however, in unusual cases it may be confirmed by polymerase chain reaction (PCR) testing of the blister fluid or scabs."}, {"list": ["antibodies"], "text": "Testing for antibodies may be done to determine if a person is or is not immune."}, {"text": "People usually only get chickenpox once."}, {"text": "Although reinfections by the virus occur, these reinfections usually do not cause any symptoms."}, {"list": ["varicella vaccine"], "text": "The varicella vaccine has resulted in a decrease in the number of cases and complications from the disease."}, {"text": "It protects about 70 to 90 percent of people from disease with a greater benefit for severe disease."}, {"text": "Routine immunization of children is recommended in many countries."}, {"text": "Immunization within three days of exposure may improve outcomes in children."}, {"list": ["calamine lotion", "paracetamol"], "text": "Treatment of those infected may include calamine lotion to help with itching, keeping the fingernails short to decrease injury from scratching, and the use of paracetamol (acetaminophen) to help with fevers."}, {"list": ["antiviral medication", "aciclovir"], "text": "For those at increased risk of complications antiviral medication such as aciclovir are recommended."}, {"text": "Chickenpox occurs in all parts of the world."}, {"list": ["herpes zoster"], "text": "In 2013 there were 140 million cases of chickenpox and herpes zoster worldwide."}, {"text": "Before routine immunization the number of cases occurring each year was similar to the number of people born."}, {"text": "Since immunization the number of infections in the United States has decreased nearly 90%."}, {"text": "In 2015 chickenpox resulted in 6,400 deaths globally – down from 8,900 in 1990."}, {"text": "Death occurs in about 1 per 60,000 cases."}, {"list": ["smallpox"], "text": "Chickenpox was not separated from smallpox until the late 19th century."}, {"text": "In 1888 its connection to shingles was determined."}, {"text": "The first documented use of the term chicken pox was in 1658."}, {"text": "Various explanations have been suggested for the use of \\"chicken\\" in the name, one being the relative mildness of the disease."}, {"list": ["prodromal"], "text": "The early (prodromal) symptoms in adolescents and adults are nausea, loss of appetite, aching muscles, and headache."}, {"list": ["malaise"], "text": "This is followed by the characteristic rash or oral sores, malaise, and a low-grade fever that signal the presence of the disease."}, {"text": "Oral manifestations of the disease (enanthem) not uncommonly may precede the external rash (exanthem)."}, {"text": "In children the illness is not usually preceded by prodromal symptoms, and the first sign is the rash or the spots in the oral cavity."}, {"list": ["pustules", "umbilication"], "text": "The rash begins as small red dots on the face, scalp, torso, upper arms and legs; progressing over 10–12 hours to small bumps, blisters and pustules; followed by umbilication and the formation of scabs."}, {"text": "At the blister stage, intense itching is usually present."}, {"text": "Blisters may also occur on the palms, soles, and genital area."}, {"text": "Commonly, visible evidence of the disease develops in the oral cavity and tonsil areas in the form of small ulcers which can be painful or itchy or both; this enanthem (internal rash) can precede the exanthem (external rash) by 1 to 3 days or can be concurrent."}, {"text": "These symptoms of chickenpox appear 10 to 21 days after exposure to a contagious person."}, {"list": ["pneumonia"], "text": "Adults may have a more widespread rash and longer fever, and they are more likely to experience complications, such as varicella pneumonia."}, {"text": "Because watery nasal discharge containing live virus usually precedes both exanthem (external rash) and enanthem (oral ulcers) by 1 to 2 days, the infected person actually becomes contagious one to two days before recognition of the disease."}, {"text": "Contagiousness persists until all vesicular lesions have become dry crusts (scabs), which usually entails four or five days, by which time nasal shedding of live virus ceases."}, {"text": "The condition usually resolves by itself within a couple of weeks."}, {"text": "The rash may, however, last for up to one month."}, {"text": "Chickenpox is contagious starting from one to two days before the appearance of the rash and lasts until the lesions have crusted."}, {"text": "Chickenpox is rarely fatal, although it is generally more severe in adult men than in women or children."}, {"text": "Non-immune pregnant women and those with a suppressed immune system are at highest risk of serious complications."}, {"list": ["Arterial ischemic stroke"], "text": "Arterial ischemic stroke (AIS) associated with chickenpox in the previous year accounts for nearly one third of childhood AIS."}, {"list": ["shingles"], "text": "The most common late complication of chickenpox is shingles (herpes zoster), caused by reactivation of the varicella zoster virus decades after the initial, often childhood, chickenpox infection."}, {"text": "During pregnancy the dangers to the fetus associated with a primary VZV infection are greater in the first six months."}, {"text": "In the third trimester, the mother is more likely to have severe symptoms."}, {"list": ["antibodies", "placenta", "fetus"], "text": "For pregnant women, antibodies produced as a result of immunization or previous infection are transferred via the placenta to the fetus."}, {"list": ["spread"], "text": "Varicella infection in pregnant women could lead to spread via the placenta and infection of the fetus."}, {"list": ["gestation"], "text": "If infection occurs during the first 28 weeks of gestation, this can lead to fetal varicella syndrome (also known as congenital varicella syndrome)."}, {"text": "Effects on the fetus can range in severity from underdeveloped toes and fingers to severe anal and bladder malformation."}, {"text": "Possible problems include:"}, {"list": ["immunoglobulin G", "immunoglobulin M", "immunoglobulin A", "antibodies"], "text": "Exposure to VZV in a healthy child initiates the production of host immunoglobulin G (IgG), immunoglobulin M (IgM), and immunoglobulin A (IgA) antibodies; IgG antibodies persist for life and confer immunity."}, {"list": ["Cell-mediated immune responses"], "text": "Cell-mediated immune responses are also important in limiting the scope and the duration of primary varicella infection."}, {"list": ["mucosal", "epidermal", "sensory nerves"], "text": "After primary infection, VZV is hypothesized to spread from mucosal and epidermal lesions to local sensory nerves."}, {"list": ["dorsal ganglion"], "text": "VZV then remains latent in the dorsal ganglion cells of the sensory nerves."}, {"list": ["herpes zoster", "postherpetic neuralgia", "Ramsay Hunt syndrome type II"], "text": "Reactivation of VZV results in the clinically distinct syndrome of herpes zoster (i.e., shingles), postherpetic neuralgia, and sometimes Ramsay Hunt syndrome type II."}, {"text": "Varicella zoster can affect the arteries in the neck and head, producing stroke, either during childhood, or after a latency period of many years."}, {"text": "After a chickenpox infection, the virus remains dormant in the body\'s nerve tissues."}, {"list": ["immune system", "shingles"], "text": "The immune system keeps the virus at bay, but later in life, usually in an adult, it can be reactivated and cause a different form of the viral infection called shingles (also known as herpes zoster)."}, {"text": "The United States Advisory Committee on Immunization Practices (ACIP) suggests that every adult over the age of 50 years get the herpes zoster vaccine."}, {"text": "Shingles affects one in five adults infected with chickenpox as children, especially those who are immune-suppressed, particularly from cancer, HIV, or other conditions."}, {"text": "Stress can bring on shingles as well, although scientists are still researching the connection."}, {"text": "Shingles are most commonly found in adults over the age of 60 who were diagnosed with chickenpox when they were under the age of 1."}, {"list": ["rash"], "text": "The diagnosis of chickenpox is primarily based on the signs and symptoms, with typical early symptoms followed by a characteristic rash."}, {"list": ["rash"], "text": "Confirmation of the diagnosis is by examination of the fluid within the vesicles of the rash, or by testing blood for evidence of an acute immunologic response."}, {"list": ["Tzanck smear", "direct fluorescent antibody"], "text": "Vesicular fluid can be examined with a Tzanck smear, or by testing for direct fluorescent antibody."}, {"text": "The fluid can also be \\"cultured\\", whereby attempts are made to grow the virus from a fluid sample."}, {"text": "Blood tests can be used to identify a response to acute infection (IgM) or previous infection and subsequent immunity (IgG)."}, {"list": ["ultrasound"], "text": "Prenatal diagnosis of fetal varicella infection can be performed using ultrasound, though a delay of 5 weeks following primary maternal infection is advised."}, {"list": ["PCR", "amniotic fluid", "spontaneous abortion", "amniocentesis"], "text": "A PCR (DNA) test of the mother\'s amniotic fluid can also be performed, though the risk of spontaneous abortion due to the amniocentesis procedure is higher than the risk of the baby\'s developing fetal varicella syndrome."}, {"text": "The spread of chickenpox can be prevented by isolating affected individuals."}, {"text": "Contagion is by exposure to respiratory droplets, or direct contact with lesions, within a period lasting from three days before the onset of the rash, to four days after the onset of the rash."}, {"list": ["chlorine bleach", "sodium hypochlorite"], "text": "The chickenpox virus is susceptible to disinfectants, notably chlorine bleach (i.e., sodium hypochlorite)."}, {"list": ["enveloped viruses"], "text": "Like all enveloped viruses, it is sensitive to desiccation, heat and detergents."}, {"list": ["varicella vaccine"], "text": "The varicella vaccine is recommended in many countries."}, {"text": "Some countries require the varicella vaccination or an exemption before entering elementary school."}, {"text": "A second dose is recommended five years after the initial immunization."}, {"text": "A vaccinated person is likely to have a milder case of chickenpox if they become infected."}, {"text": "Immunization within three days following household contact reduces infection rates and severity in children."}, {"text": "It is part of the routine immunization schedule in the US. Some European countries include it as part of universal vaccinations in children, but not all countries provide the vaccine due to its cost."}, {"text": "In the UK as of 2014, the vaccine is only recommended in people who are particularly vulnerable to chickenpox."}, {"list": ["immunoassay"], "text": "In populations that have not been immunized or if immunity is questionable, a clinician may order an enzyme immunoassay."}, {"text": "An immunoassay measures the levels of antibodies against the virus that give immunity to a person."}, {"text": "If the levels of antibodies are low (low titer) or questionable, reimmunization may be done."}, {"text": "Treatment mainly consists of easing the symptoms."}, {"text": "As a protective measure, people are usually required to stay at home while they are infectious to avoid spreading the disease to others."}, {"list": ["nails", "gloves", "infections"], "text": "Cutting the nails short or wearing gloves may prevent scratching and minimize the risk of secondary infections."}, {"list": ["calamine lotion", "zinc oxide"], "text": "Although there have been no formal clinical studies evaluating the effectiveness of topical application of calamine lotion (a topical barrier preparation containing zinc oxide, and one of the most commonly used interventions), it has an excellent safety profile."}, {"list": ["skin", "bacterial infection"], "text": "It is important to maintain good hygiene and daily cleaning of skin with warm water to avoid secondary bacterial infection."}, {"text": "Scratching may also increase the risk of secondary infection."}, {"list": ["Paracetamol", "aspirin"], "text": "Paracetamol (acetaminophen) but not aspirin may be used to reduce fever."}, {"list": ["Reye syndrome"], "text": "Aspirin use by someone with chickenpox may cause the serious, sometimes fatal disease of the liver and brain, Reye syndrome."}, {"text": "People at risk of developing severe complications who have had significant exposure to the virus may be given intra-muscular varicella zoster immune globulin (VZIG), a preparation containing high titres of antibodies to varicella zoster virus, to ward off the disease."}, {"text": "Antivirals are sometimes used."}, {"list": ["aciclovir", "rash"], "text": "If aciclovir by mouth is started within 24 hours of rash onset, it decreases symptoms by one day but has no effect on complication rates."}, {"text": "Use of acyclovir therefore is not currently recommended for individuals with normal immune function."}, {"list": ["antiviral drugs"], "text": "Children younger than 12 years old and older than one month are not meant to receive antiviral drugs unless they have another medical condition which puts them at risk of developing complications."}, {"text": "Treatment of chickenpox in children is aimed at symptoms while the immune system deals with the virus."}, {"list": ["nails"], "text": "With children younger than 12 years, cutting nails and keeping them clean is an important part of treatment as they are more likely to scratch their blisters more deeply than adults."}, {"list": ["Aspirin", "Reye syndrome"], "text": "Aspirin is highly contraindicated in children younger than 16 years, as it has been related to Reye syndrome."}, {"list": ["Infection"], "text": "Infection in otherwise healthy adults tends to be more severe."}, {"list": ["Treatment", "drugs", "acyclovir", "valacyclovir"], "text": "Treatment with antiviral drugs (e.g. acyclovir or valacyclovir) is generally advised, as long as it is started within 24–48 hours from rash onset."}, {"text": "Remedies to ease the symptoms of chickenpox in adults are basically the same as those used for children."}, {"text": "Adults are more often prescribed antiviral medication, as it is effective in reducing the severity of the condition and the likelihood of developing complications."}, {"text": "Antiviral medicines do not kill the virus but stop it from multiplying."}, {"text": "Adults are advised to increase water intake to reduce dehydration and to relieve headaches."}, {"list": ["paracetamol"], "text": "Painkillers such as paracetamol (acetaminophen) are recommended, as they are effective in relieving itching and other symptoms such as fever or pains."}, {"list": ["sedative"], "text": "Antihistamines relieve itching and may be used in cases where the itching prevents sleep, because they also act as a sedative."}, {"text": "As with children, antiviral medication is considered more useful for those adults who are more prone to develop complications."}, {"list": ["pregnant women"], "text": "These include pregnant women or people who have a weakened immune system."}, {"list": ["Sorivudine"], "text": "Sorivudine, a nucleoside analogue, has been reported to be effective in the treatment of primary varicella in healthy adults (case reports only), but large-scale clinical trials are still needed to demonstrate its efficacy."}, {"list": ["VZV immune globulin", "varicella vaccine", "herpes zoster vaccine"], "text": "After recovering from chickenpox, it is recommended by doctors that adults take one injection of VZV immune globulin and one injection of varicella vaccine or herpes zoster vaccine."}, {"text": "The duration of the visible blistering caused by varicella zoster virus varies in children usually from 4 to 7 days, and the appearance of new blisters begins to subside after the fifth day."}, {"list": ["sodium bicarbonate", "antihistamine"], "text": "Chickenpox infection is milder in young children, and symptomatic treatment, with sodium bicarbonate baths or antihistamine medication may ease itching."}, {"list": ["Paracetamol"], "text": "It is recommended to keep new infants from birth up to age 6 months away from an infected person for 10 to 21 days because their immune systems are not developed enough to handle the stress it can bring on. Paracetamol (acetaminophen) is widely used to reduce fever."}, {"list": ["Aspirin", "Reye\'s Syndrome"], "text": "Aspirin, or products containing aspirin, should not be given to children with chickenpox, as it can cause Reye\'s Syndrome."}, {"text": "In adults, the disease is more severe, though the incidence is much less common."}, {"list": ["pneumonia", "viral pneumonia", "bacterial pneumonia", "bronchitis", "hepatitis", "encephalitis"], "text": "Infection in adults is associated with greater morbidity and mortality due to pneumonia (either direct viral pneumonia or secondary bacterial pneumonia), bronchitis (either viral bronchitis or secondary bacterial bronchitis), hepatitis, and encephalitis."}, {"text": "In particular, up to 10% of pregnant women with chickenpox develop pneumonia, the severity of which increases with onset later in gestation."}, {"text": "In England and Wales, 75% of deaths due to chickenpox are in adults."}, {"list": ["encephalitis", "herpes zoster"], "text": "Inflammation of the brain, or encephalitis, can occur in immunocompromised individuals, although the risk is higher with herpes zoster."}, {"list": ["Necrotizing fasciitis"], "text": "Necrotizing fasciitis is also a rare complication."}, {"text": "Varicella can be lethal to adults with impaired immunity."}, {"text": "The number of people in this high-risk group has increased, due to the HIV epidemic and the increased use of immunosuppressive therapies."}, {"list": ["HIV"], "text": "Varicella is a particular problem in hospitals, when there are patients with immune systems weakened by drugs (e.g., high-dose steroids) or HIV."}, {"list": ["impetigo", "cellulitis", "erysipelas"], "text": "Secondary bacterial infection of skin lesions, manifesting as impetigo, cellulitis, and erysipelas, is the most common complication in healthy children."}, {"text": "Disseminated primary varicella infection usually seen in the immunocompromised may have high morbidity."}, {"text": "Ninety percent of cases of varicella pneumonia occur in the adult population."}, {"list": ["myocarditis", "hepatitis", "glomerulonephritis"], "text": "Rarer complications of disseminated chickenpox include myocarditis, hepatitis, and glomerulonephritis."}, {"text": "Hemorrhagic complications are more common in the immunocompromised or immunosuppressed populations, although healthy children and adults have been affected."}, {"list": ["purpura", "purpura fulminans", "anaphylactoid purpura"], "text": "Five major clinical syndromes have been described: febrile purpura, malignant chickenpox with purpura, postinfectious purpura, purpura fulminans, and anaphylactoid purpura."}, {"text": "These syndromes have variable courses, with febrile purpura being the most benign of the syndromes and having an uncomplicated outcome."}, {"text": "In contrast, malignant chickenpox with purpura is a grave clinical condition that has a mortality rate of greater than 70%."}, {"text": "The cause of these hemorrhagic chickenpox syndromes is not known."}, {"text": "Primary varicella occurs in all countries worldwide."}, {"text": "In 2013 the disease resulted in 7,000 deaths – down from 8,900 in 1990."}, {"list": ["temperate"], "text": "In temperate countries, chickenpox is primarily a disease of children, with most cases occurring during the winter and spring, most likely due to school contact."}, {"text": "It is one of the classic diseases of childhood, with the highest prevalence in the 4–10-year-old age group."}, {"list": ["rubella"], "text": "Like rubella, it is uncommon in preschool children."}, {"text": "Varicella is highly communicable, with an infection rate of 90% in close contacts."}, {"text": "In temperate countries, most people become infected before adulthood, and 10% of young adults remain susceptible."}, {"text": "In the tropics, chickenpox often occurs in older people and may cause more serious disease."}, {"text": "In adults, the pock marks are darker and the scars more prominent than in children."}, {"text": "In the United States, the Centers for Disease Control and Prevention (CDC) does not require state health departments to report infections of chickenpox, and only 31 states currently volunteer this information."}, {"list": ["disease surveillance tool"], "text": "However, in a 2013 study conducted by the social media disease surveillance tool called Sickweather, anecdotal reports of chickenpox infections on Facebook and Twitter were used to measure and rank states with the most infections per capita, with Maryland, Tennessee and Illinois in the top three."}, {"text": "Why the term was used is not clear but it may be due to it being a relatively mild disease."}, {"list": ["chickpeas\'\'"], "text": "It has been said to be derived from chickpeas, based on resemblance of the vesicles to chickpeas, or to come from the rash resembling chicken pecks."}, {"text": "Other suggestions include the designation chicken for a child (i.e., literally \'child pox\'), a corruption of itching-pox, or the idea that the disease may have originated in chickens."}, {"list": ["Samuel Johnson"], "text": "Samuel Johnson explained the designation as \\"from its being of no very great danger\\"."}, {"list": ["chickenpox parties"], "text": "Because chickenpox is usually more severe in adults than it is in children, some parents deliberately expose their children to the virus, sometimes by taking them to \\"chickenpox parties\\"."}, {"text": "Doctors counter that children are safer getting the vaccine, which is a weakened form of the virus, rather than getting the disease, which can be fatal."}, {"text": "Humans are the only known species that the disease affects naturally."}, {"list": ["primates", "chimpanzees", "gorillas"], "text": "However, chickenpox has been caused in other animals, such as primates, including chimpanzees and gorillas."}]', '{"list": ["Varicella vaccine"], "conditions": ["Varicella vaccine"]}', '{}', '{"desc": "Calamine lotion, paracetamol (acetaminophen), aciclovir", "list": ["Calamine lotion", "paracetamol", "aciclovir"]}', '[{"url": "https://upload.wikimedia.org/wikipedia/commons/e/eb/Varicela_Aranzales.jpg", "file": "File:Varicela Aranzales.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/e/eb/Varicela_Aranzales.jpg/300px-Varicela_Aranzales.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/c/c3/Chickenpox_blister.jpg", "file": "File:Chickenpox blister.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/c/c3/Chickenpox_blister.jpg/300px-Chickenpox_blister.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/0/09/Chickenpox.png", "file": "File:Chickenpox.png", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/0/09/Chickenpox.png/300px-Chickenpox.png"}]'),
(7, 'chlamydia', 'Chlamydia%20infection', 'Pap%20smear%20showing%20C.%20trachomatis%20%28H%26E%20stain%29', 'Infectious%20disease%2C%20gynecology%2C%20urology', '["None", "vaginal discharge", "discharge from the penis", "burning with urination"]', '{"desc": "Urine or swab of the cervix, vagina, or urethra", "list": ["urethra"]}', '{"desc": "Chlamydia trachomatis spread by sex or childbirth", "list": ["Chlamydia trachomatis\'\'"]}', '[{"list": ["sexually transmitted infection", "Chlamydia trachomatis\'\'"], "text": "Chlamydia infection, often simply known as chlamydia, is a sexually transmitted infection caused by the bacterium Chlamydia trachomatis."}, {"text": "Most people who are infected have no symptoms."}, {"text": "When symptoms do develop this can take a few weeks following infection to occur."}, {"list": ["vaginal discharge", "burning with urination"], "text": "Symptoms in women may include vaginal discharge or burning with urination."}, {"list": ["pain and swelling of one or both testicles"], "text": "Symptoms in men may include discharge from the penis, burning with urination, or pain and swelling of one or both testicles."}, {"list": ["pelvic inflammatory disease", "infertility", "ectopic pregnancy"], "text": "The infection can spread to the upper genital tract in women causing pelvic inflammatory disease which may result in future infertility or ectopic pregnancy."}, {"list": ["trachoma", "blindness", "developing world"], "text": "Repeated infections of the eyes that go without treatment can result in trachoma, a common cause of blindness in the developing world."}, {"list": ["vaginal", "anal", "oral sex", "childbirth"], "text": "Chlamydia can be spread during vaginal, anal, or oral sex, and can be passed from an infected mother to her baby during childbirth."}, {"list": ["sanitation"], "text": "The eye infections may also be spread by personal contact, flies, and contaminated towels in areas with poor sanitation."}, {"text": "Chlamydia trachomatis only occurs in humans."}, {"list": ["screening", "prenatal visit"], "text": "Diagnosis is often by screening which is recommended yearly in sexually active women under the age of twenty five, others at higher risk, and at the first prenatal visit."}, {"list": ["urethra"], "text": "Testing can be done on the urine or a swab of the cervix, vagina, or urethra."}, {"text": "Rectal or mouth swabs are required to diagnose infections in those areas."}, {"list": ["not having sex", "condoms"], "text": "Prevention is by not having sex, the use of condoms, or having sex with only one other person, who is not infected."}, {"list": ["antibiotics", "azithromycin", "doxycycline"], "text": "Chlamydia can be cured by antibiotics with typically either azithromycin or doxycycline being used."}, {"list": ["Erythromycin", "pregnancy"], "text": "Erythromycin or azithromycin is recommended in babies and during pregnancy."}, {"text": "Sexual partners should also be treated and the infected people advised not to have sex for seven days and until symptom free."}, {"list": ["Gonorrhea", "syphilis", "HIV"], "text": "Gonorrhea, syphilis, and HIV should be tested for in those who have been infected."}, {"text": "Following treatment people should be tested again after three months."}, {"text": "Chlamydia is one of the most common sexually transmitted infections worldwide affecting about 4.2% of women and 2.7% of men."}, {"text": "In 2015 about 61 million new cases occurred globally."}, {"list": ["United States"], "text": "In the United States about 1.4 million cases were reported in 2014."}, {"text": "Infections are most common among those between the ages of 15 and 25 and are more common in women than men."}, {"text": "In 2015 infections resulted in about 200 deaths."}, {"list": ["Greek"], "text": "The word \\"chlamydia\\" is from the Greek, χλαμύδα meaning \\"cloak\\"."}, {"list": ["cervix", "no symptoms"], "text": "Chlamydial infection of the cervix (neck of the womb) is a sexually transmitted infection which has no symptoms for 50&ndash;70% of women infected."}, {"text": "The infection can be passed through vaginal, anal, or oral sex."}, {"list": ["pelvic inflammatory disease", "uterus", "fallopian tubes", "ovaries"], "text": "Of those who have an asymptomatic infection that is not detected by their doctor, approximately half will develop pelvic inflammatory disease (PID), a generic term for infection of the uterus, fallopian tubes, and/or ovaries."}, {"list": ["reproductive organs", "pelvic", "pregnant", "ectopic (tubal) pregnancy"], "text": "PID can cause scarring inside the reproductive organs, which can later cause serious complications, including chronic pelvic pain, difficulty becoming pregnant, ectopic (tubal) pregnancy, and other dangerous complications of pregnancy."}, {"list": ["symptoms"], "text": "Chlamydia is known as the \\"silent epidemic\\" as in women, it may not cause any symptoms in 70–80% of cases, and can linger for months or years before being discovered."}, {"list": ["vaginal bleeding", "painful sexual intercourse", "fever", "painful urination", "urinary urgency"], "text": "Signs and symptoms may include abnormal vaginal bleeding or discharge, abdominal pain, painful sexual intercourse, fever, painful urination or the urge to urinate more often than usual (urinary urgency)."}, {"text": "For sexually active women who are not pregnant, screening is recommended in those under 25 and others at risk of infection."}, {"list": ["condom"], "text": "Risk factors include a history of chlamydial or other sexually transmitted infection, new or multiple sexual partners, and inconsistent condom use."}, {"list": ["emergency contraceptive"], "text": "Guidelines recommend all women attending for emergency contraceptive are offered Chlamydia testing, with studies showing up to 9% of women aged <25 years had Chlamydia."}, {"list": ["inflammation of the urethra"], "text": "In men, those with a chlamydial infection show symptoms of infectious inflammation of the urethra in about 50% of cases."}, {"list": ["penis", "testicular"], "text": "Symptoms that may occur include: a painful or burning sensation when urinating, an unusual discharge from the penis, testicular pain or swelling, or fever."}, {"list": ["epididymitis", "sterility"], "text": "If left untreated, chlamydia in men can spread to the testicles causing epididymitis, which in rare cases can lead to sterility if not treated."}, {"list": ["prostatic inflammation"], "text": "Chlamydia is also a potential cause of prostatic inflammation in men, although the exact relevance in prostatitis is difficult to ascertain due to possible contamination from urethritis."}, {"text": "Chlamydia conjunctivitis or trachoma was once the most important cause of blindness worldwide, but its role diminished from 15% of blindness cases by trachoma in 1995 to 3.6% in 2002."}, {"text": "The infection can be spread from eye to eye by fingers, shared towels or cloths, coughing and sneezing and eye-seeking flies."}, {"list": ["childbirth"], "text": "Newborns can also develop chlamydia eye infection through childbirth (see below)."}, {"list": ["acronym", "in-growing", "in-turned", "antibiotics", "World Health Organization"], "text": "Using the SAFE strategy (acronym for surgery for in-growing or in-turned lashes, antibiotics, facial cleanliness, and environmental improvements), the World Health Organization aims for the global elimination of trachoma by 2020 (GET 2020 initiative)."}, {"list": ["reactive arthritis"], "text": "Chlamydia may also cause reactive arthritis—the triad of arthritis, conjunctivitis and urethral inflammation—especially in young men."}, {"text": "About 15,000 men develop reactive arthritis due to chlamydia infection each year in the U.S., and about 5,000 are permanently affected by it. It can occur in both sexes, though is more common in men."}, {"list": ["infants"], "text": "As many as half of all infants born to mothers with chlamydia will be born with the disease."}, {"list": ["premature birth", "conjunctivitis", "blindness", "pneumonia"], "text": "Chlamydia can affect infants by causing spontaneous abortion; premature birth; conjunctivitis, which may lead to blindness; and pneumonia."}, {"text": "Conjunctivitis due to chlamydia typically occurs one week after birth (compared with chemical causes (within hours) or gonorrhea (2–5 days))."}, {"list": ["serovar", "lymphogranuloma venereum", "lymph nodes", "lymphatics"], "text": "A different serovar of Chlamydia trachomatis is also the cause of lymphogranuloma venereum, an infection of the lymph nodes and lymphatics."}, {"list": ["genital ulceration", "swollen lymph nodes", "groin", "rectal inflammation", "fever"], "text": "It usually presents with genital ulceration and swollen lymph nodes in the groin, but it may also manifest as rectal inflammation, fever or swollen lymph nodes in other regions of the body."}, {"text": "Chlamydia can be transmitted during vaginal, anal, or oral sex or direct contact with infected tissue such as conjunctiva."}, {"text": "Chlamydia can also be passed from an infected mother to her baby during vaginal childbirth."}, {"text": "Chlamydiae have the ability to establish long-term associations with host cells."}, {"list": ["amino acids", "tryptophan", "iron", "vitamins"], "text": "When an infected host cell is starved for various nutrients such as amino acids (for example, tryptophan), iron, or vitamins, this has a negative consequence for Chlamydiae since the organism is dependent on the host cell for these nutrients."}, {"text": "Long-term cohort studies indicate that approximately 50% of those infected clear within a year, 80% within two years, and 90% within three years."}, {"list": ["cell division"], "text": "The starved chlamydiae enter a persistent growth state wherein they stop cell division and become morphologically aberrant by increasing in size."}, {"text": "Persistent organisms remain viable as they are capable of returning to a normal growth state once conditions in the host cell improve."}, {"list": ["in vivo\'\'"], "text": "There is much debate as to whether persistence has in vivo relevance."}, {"text": "Many believe that persistent chlamydiae are the cause of chronic chlamydial diseases."}, {"list": ["β-lactams"], "text": "Some antibiotics such as β-lactams can also induce a persistent-like growth state, which can contribute to the chronicity of chlamydial diseases."}, {"text": "The diagnosis of genital chlamydial infections evolved rapidly from the 1990s through 2006."}, {"list": ["Nucleic acid amplification tests", "polymerase chain reaction", "strand displacement amplification"], "text": "Nucleic acid amplification tests (NAAT), such as polymerase chain reaction (PCR), transcription mediated amplification (TMA), and the DNA strand displacement amplification (SDA) now are the mainstays."}, {"list": ["sampled"], "text": "NAAT for chlamydia may be performed on swab specimens sampled from the cervix (women) or urethra (men), on self-collected vaginal swabs, or on voided urine."}, {"list": ["sensitivity", "specificity"], "text": "NAAT has been estimated to have a sensitivity of approximately 90% and a specificity of approximately 99%, regardless of sampling from a cervical swab or by urine specimen."}, {"list": ["negative"], "text": "In women seeking an STI clinic and a urine test is negative, a subsequent cervical swab has been estimated to be positive in approximately 2% of the time."}, {"text": "At present, the NAATs have regulatory approval only for testing urogenital specimens, although rapidly evolving research indicates that they may give reliable results on rectal specimens."}, {"list": ["gold standard"], "text": "Because of improved test accuracy, ease of specimen management, convenience in specimen management, and ease of screening sexually active men and women, the NAATs have largely replaced culture, the historic gold standard for chlamydia diagnosis, and the non-amplified probe tests."}, {"text": "The latter test is relatively insensitive, successfully detecting only 60–80% of infections in asymptomatic women, and often giving falsely positive results."}, {"text": "Culture remains useful in selected circumstances and is currently the only assay approved for testing non-genital specimens."}, {"text": "Other method also exist including: ligase chain reaction (LCR), direct fluorescent antibody resting, enzyme immunoassay, and cell culture."}, {"text": "For sexually active women who are not pregnant, screening is recommended in those under 25 and others at risk of infection."}, {"list": ["condom"], "text": "Risk factors include a history of chlamydial or other sexually transmitted infection, new or multiple sexual partners, and inconsistent condom use."}, {"list": ["U.S. Preventive Services Task Force", "American Academy of Family Physicians"], "text": "For pregnant women, guidelines vary: screening women with age or other risk factors is recommended by the U.S. Preventive Services Task Force (USPSTF) (which recommends screening women under 25) and the American Academy of Family Physicians (which recommends screening women aged 25 or younger)."}, {"list": ["American College of Obstetricians and Gynecologists", "Centers for Disease Control and Prevention"], "text": "The American College of Obstetricians and Gynecologists recommends screening all at risk, while the Centers for Disease Control and Prevention recommend universal screening of pregnant women."}, {"text": "The USPSTF acknowledges that in some communities there may be other risk factors for infection, such as ethnicity."}, {"text": "Evidence-based recommendations for screening initiation, intervals and termination are currently not possible."}, {"text": "For men, the USPSTF concludes evidence is currently insufficient to determine if regular screening of men for chlamydia is beneficial."}, {"text": "They recommend regular screening of men who are at increased risk for HIV or syphilis infection."}, {"list": ["National Health Service (NHS)"], "text": "In the United Kingdom the National Health Service (NHS) aims to:"}, {"list": ["antibiotics"], "text": "C. trachomatis infection can be effectively cured with antibiotics."}, {"list": ["azithromycin", "doxycycline", "erythromycin", "ofloxacin"], "text": "Guidelines recommend azithromycin, doxycycline, erythromycin, levofloxacin or ofloxacin."}, {"list": ["amoxicillin"], "text": "Agents recommended during pregnancy include erythromycin or amoxicillin."}, {"list": ["gonorrhea", "patient-delivered partner therapy"], "text": "An option for treating sexual partners of those with chlamydia or gonorrhea include patient-delivered partner therapy (PDT or PDPT), which is the practice of treating the sex partners of index cases by providing prescriptions or medications to the patient to take to his/her partner without the health care provider first examining the partner."}, {"text": "Following treatment people should be tested again after three months to check for reinfection."}, {"list": ["Disability-adjusted life year"], "text": "[[File:Chlamydia world map - DALY - WHO2004.svg|thumb|upright=1.3|Disability-adjusted life year (DALY) for chlamydia per 100,000 inhabitants in 2004. Globally, as of 2015, sexually transmitted chlamydia affects approximately 61 million people. It is more common in women (3.8%) than men (2.5%). In 2015 it resulted in about 200 deaths. In the United States about 1.6 million cases were reported in 2016. The CDC estimates that if one includes unreported cases there are about 2.9 million each year. It affects around 2% of young people. Chlamydial infection is the most common bacterial sexually transmitted infection in the UK. Chlamydia causes more than 250,000 cases of epididymitis in the U.S. each year. Chlamydia causes 250,000 to 500,000 cases of PID every year in the United States. Women infected with chlamydia are up to five times more likely to become infected with HIV, if exposed."}]', '{"list": ["Not having sex", " condoms", " sex with only one non–infected person"], "conditions": ["condoms"]}', '{"list": ["Pain in the testicles", " pelvic inflammatory disease", " infertility", " ectopic pregnancy"], "conditions": ["Pain in the testicles", "pelvic inflammatory disease", "infertility", "ectopic pregnancy"]}', '{}', '[{"url": "https://upload.wikimedia.org/wikipedia/commons/9/96/Pap_smear_showing_clamydia_in_the_vacuoles_500x_H%26E.jpg", "file": "File:Pap smear showing clamydia in the vacuoles 500x H&E.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/9/96/Pap_smear_showing_clamydia_in_the_vacuoles_500x_H%26E.jpg/300px-Pap_smear_showing_clamydia_in_the_vacuoles_500x_H%26E.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/3/3a/SOA-Chlamydia-trachomatis-female.jpg", "file": "File:SOA-Chlamydia-trachomatis-female.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3a/SOA-Chlamydia-trachomatis-female.jpg/300px-SOA-Chlamydia-trachomatis-female.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/0/0e/SOA-Chlamydia-trachomatis-male.jpg", "file": "File:SOA-Chlamydia-trachomatis-male.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/0/0e/SOA-Chlamydia-trachomatis-male.jpg/300px-SOA-Chlamydia-trachomatis-male.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/3/3a/SOA-conjunctivitis.jpg", "file": "File:SOA-conjunctivitis.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/3/3a/SOA-conjunctivitis.jpg/300px-SOA-conjunctivitis.jpg"}, {"url": "https://upload.wikimedia.org/wikipedia/commons/a/aa/ChlamydiaTrachomatisEinschlussk%C3%B6rperchen.jpg", "file": "File:ChlamydiaTrachomatisEinschlusskörperchen.jpg", "thumb": "https://upload.wikimedia.org/wikipedia/commons/thumb/a/aa/ChlamydiaTrachomatisEinschlussk%C3%B6rperchen.jpg/300px-ChlamydiaTrachomatisEinschlussk%C3%B6rperchen.jpg"}]');
